import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import PerfumeCard from "../components/PerfumeCard";
import { getMyFavoritePerfumesDetails } from "../services/savedPerfumeService";
import { AuthContext } from "../App"; // Import AuthContext

const Favorites = () => {
  const { isLoggedIn } = useContext(AuthContext); // Use context
  const [favoritePerfumes, setFavoritePerfumes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // For redirection if not logged in

  useEffect(() => {
    // Redirect if not logged in
    if (!isLoggedIn) {
      // Itt dönthetsz úgy is, hogy egy üzenetet jelenítesz meg,
      // ahelyett, hogy azonnal átirányítanál.
      // setError("A kedvencek megtekintéséhez be kell jelentkezni.");
      // setLoading(false);
      navigate("/bejelentkezes"); // Vagy átirányítás
      return; // Stop further execution
    }

    const fetchFavoriteDetails = async () => {
      setLoading(true);
      setError(null);
      try {
        const perfumesData = await getMyFavoritePerfumesDetails();
        setFavoritePerfumes(perfumesData);
      } catch (err) {
        console.error("Hiba a kedvenc parfümök adatainak lekérésekor:", err);
        setError(err.message || "Nem sikerült betölteni a kedvenceket.");
        setFavoritePerfumes([]); // Clear any previous data on error
      } finally {
        setLoading(false);
      }
    };

    fetchFavoriteDetails();
  }, [isLoggedIn, navigate]); // Depend on isLoggedIn and navigate

  if (loading) {
    return <div className="text-center my-5">Kedvencek betöltése...</div>;
  }

  if (error) {
    return <div className="alert alert-danger text-center">{error}</div>;
  }

  return (
    <div className="favorites-page container my-5">
      <h1 className="text-center mb-4 section-title">Kedvenc Parfümjeim</h1>
      {/* ... (loading/error handling) ... */}
      {favoritePerfumes.length === 0 && !loading && !error ? ( // Check loading/error state too
        <div id="noResults" className="text-center">
          {/* ... no results content ... */}
        </div>
      ) : (
        <div className="row g-4" id="perfumeList">
          {" "}
          {/* Használjuk ugyanazt az ID-t, mint a keresőnél */}
          {favoritePerfumes.map((perfume) => (
            // Adjunk hozzá d-flex és align-items-stretch classokat a col divhez
            <div
              key={perfume.id}
              className="col-lg-4 col-md-6 col-12 d-flex align-items-stretch"
            >
              <PerfumeCard perfume={perfume} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Favorites;
