import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import PerfumeCard from "../components/PerfumeCard";
import { getRandomPerfumes } from "../services/perfumeService";
import axios from "axios";
import "../style.css";

const Home = () => {
  const [randomPerfumes, setRandomPerfumes] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchRandomPerfumes = async () => {
      setLoading(true);
      setError(null);
      try {
        // Call the new service function
        const perfumesData = await getRandomPerfumes(5); // Fetch 5 random perfumes
        setRandomPerfumes(perfumesData);
      } catch (err) {
        console.error("Error fetching random perfumes:", err);
        setError(
          err.message || "Nem sikerült betölteni az ajánlott parfümöket."
        );
        setRandomPerfumes([]); // Clear data on error
      } finally {
        setLoading(false);
      }
    };
    fetchRandomPerfumes();
  }, []); // Run only once on mount

  return (
    <div className="home-wrapper">
      {}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Üdvözöljük a Parfümvilágban</h1>
          <p>
            Találd meg álmaid parfümjét ajánlásainkkal és
            árösszehasonlításainkkal – egyszerűen, gyorsan, stílusosan!
          </p>
          <div className="hero-actions">
            <Link to="/kereses" className="btn btn-peach hero-btn">
              Keresés indítása
            </Link>
            <Link
              to="/katalogus"
              className="btn btn-outline-peach ms-3 hero-btn"
            >
              Hírek felfedezése
            </Link>
          </div>
        </div>
      </section>

      {}
      <section className="featured-section container my-5">
        <h2 className="section-title text-center mb-4">Ajánlott Parfümjeink</h2>
        {loading && <div className="text-center">Betöltés...</div>}
        {error && <div className="alert alert-danger text-center">{error}</div>}
        {!loading &&
          !error && ( // Render only when not loading and no error
            <div className="row g-4" id="perfumeList">
              {randomPerfumes.length > 0 ? (
                randomPerfumes.map((perfume) => (
                  // Use d-flex align-items-stretch for consistent height
                  <div
                    key={perfume.id}
                    className="col-lg-3 col-md-6 col-12 d-flex align-items-stretch"
                  >
                    {/* Adjust column classes for potentially 4-5 items */}
                    <PerfumeCard perfume={perfume} />
                  </div>
                ))
              ) : (
                <div className="col-12 text-center">
                  <div id="noResults">
                    <i className="fas fa-search fa-2x mb-3"></i>
                    <h4>Nincs ajánlott parfüm</h4>
                    <p>Jelenleg nem tudunk parfümöt ajánlani.</p>
                  </div>
                </div>
              )}
            </div>
          )}
      </section>
    </div>
  );
};

export default Home;
