// src/components/Sidebar.js
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { getPriceRange } from "../services/perfumeService";

const Sidebar = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  // State inicializálása - Figyeljünk a stringekre
  const [brandFilter, setBrandFilter] = useState(
    searchParams.get("brand") || ""
  );
  const [scentFilter, setScentFilter] = useState(
    searchParams.get("note") || ""
  );
  const [genderFilter, setGenderFilter] = useState(
    searchParams.get("gender") || ""
  );
  const [sortOption, setSortOption] = useState(
    searchParams.get("sort") || "name-asc"
  );
  // Kezdőérték lehet üres string, hogy könnyebb legyen ellenőrizni, betöltődött-e már
  const [minPriceFilter, setMinPriceFilter] = useState(
    searchParams.get("min_price") || ""
  );
  const [maxPriceFilter, setMaxPriceFilter] = useState(
    searchParams.get("max_price") || ""
  );

  const [brands, setBrands] = useState([]);
  const [notes, setNotes] = useState([]);
  const [error, setError] = useState(null);
  const [priceRange, setPriceRange] = useState({ min: 0, max: 100000 });
  const [actualMin, setActualMin] = useState(0);
  const [actualMax, setActualMax] = useState(100000);

  const [isOpen, setIsOpen] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [brandSearchTerm, setBrandSearchTerm] = useState("");
  const [noteSearchTerm, setNoteSearchTerm] = useState("");

  // Fetch data effect (brands, notes, price range)
  useEffect(() => {
    setError(null);
    let isMounted = true;

    // Fetch Brands
    fetch("http://localhost:5000/api/brands")
      .then((res) =>
        res.ok ? res.json() : Promise.reject("Brands fetch failed")
      )
      .then((data) => isMounted && setBrands(Array.isArray(data) ? data : []))
      .catch((err) => {
        console.error("Hiba a márkák betöltésekor:", err);
        if (isMounted)
          setError(
            (prev) => (prev ? prev + "\n" : "") + "Márkák betöltése sikertelen."
          );
      });

    // Fetch Notes
    fetch("http://localhost:5000/api/notes")
      .then((res) =>
        res.ok ? res.json() : Promise.reject("Notes fetch failed")
      )
      .then((data) => isMounted && setNotes(Array.isArray(data) ? data : []))
      .catch((err) => {
        console.error("Hiba az illatjegyek betöltésekor:", err);
        if (isMounted)
          setError(
            (prev) =>
              (prev ? prev + "\n" : "") + "Illatjegyek betöltése sikertelen."
          );
      });

    // Fetch Price Range
    getPriceRange()
      .then((rangeData) => {
        if (isMounted) {
          const min = rangeData.minPrice || 0;
          const max = rangeData.maxPrice || 100000;
          setPriceRange({ min, max });
          setActualMin(min);
          setActualMax(max);
          // Csak akkor állítjuk be a filter state-et, ha az még üres VAGY URL paraméter van
          const urlMin = searchParams.get("min_price");
          const urlMax = searchParams.get("max_price");
          setMinPriceFilter(urlMin !== null ? urlMin : String(min));
          setMaxPriceFilter(urlMax !== null ? urlMax : String(max));
        }
      })
      .catch((err) => {
        console.error("Failed to load price range, using defaults.", err);
        if (isMounted) {
          setError(
            (prev) =>
              (prev ? prev + "\n" : "") + "Árintervallum betöltése sikertelen."
          );
          // Ha hiba van, és nincs URL paraméter, akkor is állítsuk be a defaultot
          if (minPriceFilter === "") setMinPriceFilter(String(priceRange.min));
          if (maxPriceFilter === "") setMaxPriceFilter(String(priceRange.max));
        }
      });

    return () => {
      isMounted = false;
    };
  }, []); // Függőségi tömb maradjon üres, csak mountkor fusson

  // Handle window resize effect
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    if (window.innerWidth > 991) setIsOpen(true);
    else setIsOpen(false);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Filtered lists for dropdowns
  const filteredBrands = useMemo(() => {
    /* ... marad ... */
    if (!brandSearchTerm) return brands;
    return brands.filter((brand) =>
      brand.name.toLowerCase().includes(brandSearchTerm.toLowerCase())
    );
  }, [brands, brandSearchTerm]);
  const filteredNotes = useMemo(() => {
    /* ... marad ... */
    if (!noteSearchTerm) return notes;
    return notes.filter((note) =>
      note.name.toLowerCase().includes(noteSearchTerm.toLowerCase())
    );
  }, [notes, noteSearchTerm]);

  // --- JAVÍTOTT handleSearch ---
  const handleSearch = () => {
    const queryParams = new URLSearchParams(); // Tiszta lappal indulunk, vagy a searchParams-ból? Induljunk tisztával.

    // Mindig hozzáadjuk a NEM szűrő paramétereket (ha vannak)
    if (sortOption && sortOption !== "name-asc")
      queryParams.set("sort", sortOption); // Csak ha nem default
    queryParams.set("page", "1"); // Mindig 1. oldalra

    // Szűrők hozzáadása, ha vannak értékeik
    if (brandFilter) queryParams.set("brand", brandFilter);
    if (scentFilter) queryParams.set("note", scentFilter);
    if (genderFilter) queryParams.set("gender", genderFilter);

    // Árszűrők hozzáadása - MOST MÁR NEM HASONLÍTUNK a defaulttal
    // Csak azt ellenőrizzük, van-e érték és szám-e
    if (minPriceFilter && !isNaN(parseFloat(minPriceFilter))) {
      queryParams.set("min_price", minPriceFilter);
    }
    if (maxPriceFilter && !isNaN(parseFloat(maxPriceFilter))) {
      queryParams.set("max_price", maxPriceFilter);
    }

    const finalUrl = `/kereses?${queryParams.toString()}`;
    console.log("Navigating to URL:", finalUrl); // Logolás marad
    navigate(finalUrl);

    if (windowWidth <= 991) setIsOpen(false);
  };

  // --- JAVÍTOTT handleClearFilters ---
  const handleClearFilters = () => {
    // State visszaállítása
    setBrandFilter("");
    setScentFilter("");
    setGenderFilter("");
    setSortOption("name-asc"); // Vissza az alap rendezésre
    // Árak visszaállítása az aktuális min/max-ra a slider vizuális megjelenítéséhez
    setMinPriceFilter(String(actualMin));
    setMaxPriceFilter(String(actualMax));
    setBrandSearchTerm("");
    setNoteSearchTerm("");

    // URL paraméterek törlése (kivéve page=1)
    const queryParams = new URLSearchParams(); // Tiszta lap
    queryParams.set("page", "1"); // Oldal visszaállítása kellhet

    const finalUrl = `/kereses?${queryParams.toString()}`;
    console.log("Clearing filters, navigating to URL:", finalUrl);
    navigate(finalUrl);

    if (windowWidth <= 991) setIsOpen(false);
  };

  // Toggle sidebar
  const toggleSidebar = () => setIsOpen(!isOpen);

  // Sidebar class definition
  const sidebarClass = `sidebar d-flex flex-column ${
    windowWidth <= 991 && !isOpen ? "hidden" : ""
  }`;

  // Slider input handlers (maradnak)
  const handleMinPriceChange = (e) => {
    /* ... marad ... */
    const newVal = parseInt(e.target.value, 10);
    const currentMax = parseInt(maxPriceFilter || actualMax, 10);
    if (newVal <= currentMax) {
      setMinPriceFilter(String(newVal));
    } else {
      setMinPriceFilter(String(currentMax));
    }
  };
  const handleMaxPriceChange = (e) => {
    /* ... marad ... */
    const newVal = parseInt(e.target.value, 10);
    const currentMin = parseInt(minPriceFilter || actualMin, 10);
    if (newVal >= currentMin) {
      setMaxPriceFilter(String(newVal));
    } else {
      setMaxPriceFilter(String(currentMin));
    }
  };

  // JSX Render
  return (
    <>
      {windowWidth <= 991 && (
        <button
          className="filter-toggle btn btn-sm btn-primary"
          onClick={toggleSidebar}
        >
          <i className={`fas ${isOpen ? "fa-times" : "fa-filter"} me-1`}></i>
          {isOpen ? "Bezár" : "Szűrők"}
        </button>
      )}

      <div className={sidebarClass}>
        <h5 className="mb-3">Szűrők és Rendezés</h5>
        {error && <div className="alert alert-danger small p-2">{error}</div>}

        {/* Rendezés */}
        <div className="mb-3">
          <label htmlFor="sort" className="form-label form-label-sm">
            Rendezés
          </label>
          <select
            id="sort"
            className="form-select form-select-sm"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
          >
            <option value="name-asc">Név (A-Z)</option>
            <option value="name-desc">Név (Z-A)</option>
            <option value="price-asc">Ár (növekvő)</option>
            <option value="price-desc">Ár (csökkenő)</option>
          </select>
        </div>

        {/* Ár Csúszkák */}
        <div className="mb-3">
          <label htmlFor="minPrice" className="form-label form-label-sm">
            {/* Biztosítjuk, hogy a minPriceFilter szám legyen a formázáshoz */}
            Min Ár:{" "}
            {Number(minPriceFilter || actualMin).toLocaleString("hu-HU")} Ft
          </label>
          <input
            type="range"
            className="form-range"
            id="minPrice"
            min={actualMin}
            max={actualMax}
            step="1000"
            // Itt is biztosítjuk, hogy a value szám legyen az inputnak, ha üres, akkor actualMin
            value={minPriceFilter || actualMin}
            onChange={handleMinPriceChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="maxPrice" className="form-label form-label-sm">
            Max Ár:{" "}
            {Number(maxPriceFilter || actualMax).toLocaleString("hu-HU")} Ft
          </label>
          <input
            type="range"
            className="form-range"
            id="maxPrice"
            min={actualMin}
            max={actualMax}
            step="1000"
            value={maxPriceFilter || actualMax}
            onChange={handleMaxPriceChange}
          />
        </div>

        {/* Márka Szűrő */}
        <div className="mb-3 filter-dropdown">
          <label htmlFor="brand" className="form-label form-label-sm">
            Márka
          </label>
          <input
            type="text"
            className="form-control form-control-sm mb-1"
            placeholder="Márka keresése..."
            value={brandSearchTerm}
            onChange={(e) => setBrandSearchTerm(e.target.value)}
          />
          <select
            id="brand"
            className="form-select form-select-sm"
            value={brandFilter}
            onChange={(e) => setBrandFilter(e.target.value)}
          >
            <option value="">Összes márka</option>
            {filteredBrands.map((brand) => (
              <option key={brand.id} value={brand.name}>
                {brand.name}
              </option>
            ))}
            {brands.length > 0 &&
              filteredBrands.length === 0 &&
              brandSearchTerm && (
                <option value="" disabled>
                  Nincs találat
                </option>
              )}
          </select>
        </div>

        {/* Illatjegy Szűrő */}
        <div className="mb-3 filter-dropdown">
          <label htmlFor="scent" className="form-label form-label-sm">
            Illatjegy
          </label>
          <input
            type="text"
            className="form-control form-control-sm mb-1"
            placeholder="Illatjegy keresése..."
            value={noteSearchTerm}
            onChange={(e) => setNoteSearchTerm(e.target.value)}
          />
          <select
            id="scent"
            className="form-select form-select-sm"
            value={scentFilter}
            onChange={(e) => setScentFilter(e.target.value)}
          >
            <option value="">Összes illatjegy</option>
            {filteredNotes.map((note) => (
              <option key={note.id} value={note.name}>
                {note.name}
              </option>
            ))}
            {notes.length > 0 &&
              filteredNotes.length === 0 &&
              noteSearchTerm && (
                <option value="" disabled>
                  Nincs találat
                </option>
              )}
          </select>
        </div>

        {/* Nem Szűrő */}
        <div className="mb-3">
          <label htmlFor="gender" className="form-label form-label-sm">
            Nem
          </label>
          <select
            id="gender"
            className="form-select form-select-sm"
            value={genderFilter}
            onChange={(e) => setGenderFilter(e.target.value)}
          >
            <option value="">Összes</option>
            <option value="male">Férfi</option>
            <option value="female">Női</option>
            <option value="unisex">Unisex</option>
          </select>
        </div>

        {/* Gombok */}
        <div className="mt-auto pt-3">
          <button
            className="btn btn-primary btn-sm w-100 mb-2"
            onClick={handleSearch}
          >
            Szűrés és Rendezés
          </button>
          <button
            className="btn btn-outline-secondary btn-sm w-100"
            onClick={handleClearFilters}
          >
            Szűrők törlése
          </button>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
