import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllPerfumes } from '../services/perfumeService';

const Sidebar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [allPerfumes, setAllPerfumes] = useState([]);
  const [brandFilter, setBrandFilter] = useState('');
  const [scentFilter, setScentFilter] = useState('');
  const [genderFilter, setGenderFilter] = useState('');
  const [sortOption, setSortOption] = useState('name-asc');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPerfumes = async () => {
      try {
        const perfumesData = await getAllPerfumes();
        setAllPerfumes(perfumesData);
      } catch (error) {
        console.error('Hiba a parfümök betöltésekor:', error);
      }
    };
    fetchPerfumes();
  }, []);

  useEffect(() => {
    if (searchTerm.trim()) {
      const filtered = allPerfumes
        .filter((p) => p.name.toLowerCase().includes(searchTerm.toLowerCase()))
        .slice(0, 5);
      setSuggestions(filtered);
    } else {
      setSuggestions([]);
    }
  }, [searchTerm, allPerfumes]);

  const handleSearch = () => {
    const queryParams = new URLSearchParams();
    if (searchTerm.trim()) queryParams.set('query', searchTerm);
    if (brandFilter) queryParams.set('brand', brandFilter);
    if (scentFilter) queryParams.set('scent', scentFilter);
    if (genderFilter) queryParams.set('gender', genderFilter);
    if (sortOption) queryParams.set('sort', sortOption);

    navigate(`/kereses?${queryParams.toString()}`);
    setSuggestions([]);
  };

  const uniqueBrands = [...new Set(allPerfumes.map((p) => p.brand))];
  const uniqueScents = [...new Set(allPerfumes.map((p) => p.scentType))]; // Feltételezem, hogy van scentType mező

  return (
    <div className="sidebar bg-light p-3" style={{ width: '250px', height: '100vh', position: 'fixed' }}>
      {/* Rendezés */}
      <div className="mb-3">
        <label htmlFor="sort" className="form-label">Rendezés</label>
        <select
          id="sort"
          className="form-select"
          value={sortOption}
          onChange={(e) => setSortOption(e.target.value)}
        >
          <option value="name-asc">Név (A-Z)</option>
          <option value="name-desc">Név (Z-A)</option>
          <option value="price-asc">Ár (növekvő)</option>
          <option value="price-desc">Ár (csökkenő)</option>
        </select>
      </div>

      {/* Keresőmező */}
      <h2 className="sidebar-title">Keresés</h2>
      <div className="position-relative mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Keresés név szerint..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {suggestions.length > 0 && (
          <ul className="list-group position-absolute w-100" style={{ zIndex: 1000, maxHeight: '200px', overflowY: 'auto' }}>
            {suggestions.map((perfume) => (
              <li
                key={perfume.id}
                className="list-group-item list-group-item-action"
                onClick={() => navigate(`/parfume/${perfume.id}`)}
                style={{ cursor: 'pointer' }}
              >
                {perfume.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      <button className="btn btn-peach w-100 mb-3" onClick={handleSearch}>
        Keresés
      </button>

      {/* Márka szűrő */}
      <div className="mb-3">
        <label htmlFor="brand" className="form-label">Márka</label>
        <select
          id="brand"
          className="form-select"
          value={brandFilter}
          onChange={(e) => setBrandFilter(e.target.value)}
        >
          <option value="">Összes</option>
          {uniqueBrands.map((brand) => (
            <option key={brand} value={brand}>{brand}</option>
          ))}
        </select>
      </div>

      {/* Illatfajta szűrő */}
      <div className="mb-3">
        <label htmlFor="scent" className="form-label">Illatfajta</label>
        <select
          id="scent"
          className="form-select"
          value={scentFilter}
          onChange={(e) => setScentFilter(e.target.value)}
        >
          <option value="">Összes</option>
          {uniqueScents.map((scent) => (
            <option key={scent} value={scent}>{scent}</option>
          ))}
        </select>
      </div>

      {/* Nem szűrő */}
      <div className="mb-3">
        <label htmlFor="gender" className="form-label">Nem</label>
        <select
          id="gender"
          className="form-select"
          value={genderFilter}
          onChange={(e) => setGenderFilter(e.target.value)}
        >
          <option value="">Összes</option>
          <option value="férfi">Férfi</option>
          <option value="női">Női</option>
          <option value="unisex">Unisex</option>
        </select>
      </div>
    </div>
  );
};

export default Sidebar;
