import React, { useContext } from 'react';
import { AuthContext } from '../App';
import { Navigate } from 'react-router-dom';
import PerfumeCard from '../components/PerfumeCard'; // Feltételezem, hogy a PerfumeCard itt található

const Favorites = () => {
  const { isLoggedIn } = useContext(AuthContext);

  // Ha a felhasználó nincs bejelentkezve, átirányítjuk a bejelentkezés oldalra
  if (!isLoggedIn) {
    return <Navigate to="/bejelentkezes" />;
  }

  // Ideiglenes kedvencek lista (később adatbázisból jön)
  const favoritePerfumes = [
    {
      id: 1,
      name: 'Ocean Breeze',
      brand: 'Aqua',
      price: 15000,
      image_url: 'https://via.placeholder.com/220',
    },
    {
      id: 2,
      name: 'Midnight Rose',
      brand: 'Flora',
      price: 20000,
      image_url: 'https://via.placeholder.com/220',
    },
  ];

  return (
    <div className="favorites-page">
      <h1 className="text-center mb-4">Kedvenc Parfümjeim</h1>
      {favoritePerfumes.length === 0 ? (
        <p className="text-center">Még nincsenek kedvenc parfümjeid.</p>
      ) : (
        <div className="row">
          {favoritePerfumes.map((perfume) => (
            <div key={perfume.id} className="col-md-4 mb-4">
              <PerfumeCard perfume={perfume} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Favorites;
