// frontend/src/pages/Search.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import PerfumeCard from '../components/PerfumeCard';
import SearchBar from '../components/SearchBar';
import '../style.css';

const Search = () => {
  const [perfumes, setPerfumes] = useState([]);
  const [filteredPerfumes, setFilteredPerfumes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [maxPrice, setMaxPrice] = useState(100000);
  const [selectedBrands, setSelectedBrands] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedScents, setSelectedScents] = useState([]);
  const [error, setError] = useState('');
  const [suggestions, setSuggestions] = useState([]); // Gyors keresési javaslatok
  const [activeFilters, setActiveFilters] = useState(''); // Aktív szűrők
  const [noResults, setNoResults] = useState(false); // Nincs találat

  useEffect(() => {
    const fetchPerfumes = async () => {
      try {
        const response = await axios.get('/api/perfumes/all');
        setPerfumes(response.data);
        setFilteredPerfumes(response.data);
      } catch (err) {
        setError('Nem sikerült betölteni a parfüméket.');
        console.error(err);
      }
    };
    fetchPerfumes();
  }, []);

  const applyFilters = () => {
    const filtered = perfumes.filter((perfume) => {
      const matchesSearch =
        searchTerm === '' ||
        perfume.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        perfume.brand.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesPrice = perfume.price <= maxPrice;
      const matchesBrand = selectedBrands.length === 0 || selectedBrands.includes(perfume.brand);
      const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(perfume.gender);
      const matchesScents = selectedScents.length === 0 || perfume.scents.split(',').some(scent => selectedScents.includes(scent.trim()));

      return matchesSearch && matchesPrice && matchesBrand && matchesCategory && matchesScents;
    });

    setActiveFilters(getActiveFilters());
    setFilteredPerfumes(filtered);
    setNoResults(filtered.length === 0);
  };

  const resetFilters = () => {
    setSearchTerm('');
    setMaxPrice(100000);
    setSelectedBrands([]);
    setSelectedCategories([]);
    setSelectedScents([]);
    setFilteredPerfumes(perfumes);
    setSuggestions([]); // Gyors javaslatok törlése törlés után
    setActiveFilters(''); // Aktív szűrők törlése
  };

  const handleSuggestionClick = (perfume) => {
    setSearchTerm(perfume.name);
    setFilteredPerfumes([perfume]);
    setSuggestions([]);
  };

  const updateSuggestions = (newSuggestions) => {
    setSuggestions(newSuggestions);
  };

  const getActiveFilters = () => {
    const filters = [];
    if (searchTerm) filters.push(`Keresés: ${searchTerm}`);
    if (selectedBrands.length > 0) filters.push(`Márkák: ${selectedBrands.join(', ')}`);
    if (selectedCategories.length > 0) filters.push(`Kategóriák: ${selectedCategories.map(cat => cat === 'female' ? 'Női' : cat === 'male' ? 'Férfi' : 'Unisex').join(', ')}`);
    if (selectedScents.length > 0) filters.push(`Illatok: ${selectedScents.join(', ')}`);
    if (maxPrice < 100000) filters.push(`Max ár: ${new Intl.NumberFormat('hu-HU').format(maxPrice)} Ft`);
    return filters.join(', ');
  };

  return (
    <div className="container">
      <h2>Parfüm Kereső</h2>
      <p>Találd meg az ideális parfümöt ajánlásainkkal és árösszehasonlításainkkal!</p>
      
      {/* Keresési Mező és Gyors Javaslatok */}
      <SearchBar
        onSearch={applyFilters}
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        suggestions={suggestions}
        handleSuggestionClick={handleSuggestionClick}
      />

      {/* Aktív Szűrők */}
      <div className="active-filters">
        {activeFilters}
      </div>

      {/* Szűrők */}
      <div className="brand-filter">
        <h5>Márkák</h5>
        {['Chanel', 'Dior', 'Gucci', 'Armani', 'Versace'].map((brand) => (
          <div key={brand} className="form-check">
            <input 
              className="form-check-input brand-checkbox" 
              type="checkbox" 
              value={brand} 
              id={`brand-${brand}`} 
              onChange={(e) => setSelectedBrands(prev => e.target.checked ? [...prev, brand] : prev.filter(b => b !== e.target.value))}
            />
            <label className="form-check-label" htmlFor={`brand-${brand}`}>
              {brand}
            </label>
          </div>
        ))}
      </div>
      <div className="category-filter">
        <h5>Kategóriák</h5>
        {['female', 'male', 'unisex'].map((category) => (
          <div key={category} className="form-check">
            <input 
              className="form-check-input category-checkbox" 
              type="checkbox" 
              value={category} 
              id={`category-${category}`} 
              onChange={(e) => setSelectedCategories(prev => e.target.checked ? [...prev, category] : prev.filter(c => c !== e.target.value))}
            />
            <label className="form-check-label" htmlFor={`category-${category}`}>
              {category === 'female' ? 'Női' : category === 'male' ? 'Férfi' : 'Unisex'}
            </label>
          </div>
        ))}
      </div>
      <div className="scent-filter">
        <h5>Illat típus</h5>
        {['Virágos', 'Fás', 'Orientális', 'Friss', 'Citrusos'].map((scent) => (
          <div key={scent} className="form-check">
            <input 
              className="form-check-input scent-checkbox" 
              type="checkbox" 
              value={scent} 
              id={`scent-${scent}`} 
              onChange={(e) => setSelectedScents(prev => e.target.checked ? [...prev, scent] : prev.filter(s => s !== e.target.value))}
            />
            <label className="form-check-label" htmlFor={`scent-${scent}`}>
              {scent}
            </label>
          </div>
        ))}
      </div>
      <div className="price-filter">
        <h5>Ár (Ft)</h5>
        <input 
          type="range" 
          className="form-range" 
          id="priceRange" 
          min="0" 
          max="100000" 
          value={maxPrice} 
          onChange={(e) => setMaxPrice(parseInt(e.target.value))} 
        />
        <span>Maximum: <span id="priceValue">{new Intl.NumberFormat('hu-HU').format(maxPrice)} Ft</span></span>
      </div>
      <div className="btn-group">
        <button type="button" className="btn btn-primary" onClick={applyFilters}>Szűrés alkalmazása</button>
        <button type="button" className="btn btn-outline-secondary ms-2" onClick={resetFilters}>Szűrők törlése</button>
      </div>

      {/* Találatok */}
      <h3 className="mt-4">Találatok ({filteredPerfumes.length})</h3>
      {error && <div className="alert alert-danger">{error}</div>}
      {noResults && (
        <div id="noResults">
          <i className="fas fa-search"></i>
          <h4>Nincs találat a keresési feltételeknek megfelelően</h4>
          <p>Próbálj meg kevesebb vagy más szűrőfeltételt beállítani</p>
        </div>
      )}
      <div className="row" id="perfumeList">
        {!noResults && filteredPerfumes.map(p => (
          <PerfumeCard key={p.id} perfume={p} />
        ))}
      </div>
    </div>
  );
};

export default Search;
