import React, { useState } from "react";

const ReviewForm = ({ perfumeId }) => {
  const [ratings, setRatings] = useState({
    sillage: 1, // Alapértelmezett érték 1
    longevity: 1,
    value: 1,
    overall: 1,
  });
  const [comment, setComment] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleRatingChange = (category, value) => {
    setRatings({ ...ratings, [category]: parseInt(value) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    try {
      const response = await fetch("/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          perfumeId,
          ratings,
          comment,
          author: "Felhasználó", // Ezt lecserélheted valódi felhasználónévre
          date: new Date().toISOString(),
        }),
      });

      if (!response.ok) {
        throw new Error("Hiba történt az értékelés beküldésekor!");
      }

      setSuccess("Értékelés sikeresen beküldve!");
      setRatings({ sillage: 1, longevity: 1, value: 1, overall: 1 });
      setComment("");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <form className="review-form-custom" onSubmit={handleSubmit}>
      {error && <div className="alert alert-danger">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}

      <div className="review-form-section">
        <label className="form-label">Illatélmény (Sillage): <span className="rating-value">{ratings.sillage}</span></label>
        <input
          type="range"
          min="1"
          max="5"
          value={ratings.sillage}
          onChange={(e) => handleRatingChange("sillage", e.target.value)}
          className="rating-slider"
        />
      </div>

      <div className="review-form-section">
        <label className="form-label">Tartósság (Longevity): <span className="rating-value">{ratings.longevity}</span></label>
        <input
          type="range"
          min="1"
          max="5"
          value={ratings.longevity}
          onChange={(e) => handleRatingChange("longevity", e.target.value)}
          className="rating-slider"
        />
      </div>

      <div className="review-form-section">
        <label className="form-label">Ár/Érték arány (Value): <span className="rating-value">{ratings.value}</span></label>
        <input
          type="range"
          min="1"
          max="5"
          value={ratings.value}
          onChange={(e) => handleRatingChange("value", e.target.value)}
          className="rating-slider"
        />
      </div>

      <div className="review-form-section">
        <label className="form-label">Összbenyomás (Overall): <span className="rating-value">{ratings.overall}</span></label>
        <input
          type="range"
          min="1"
          max="5"
          value={ratings.overall}
          onChange={(e) => handleRatingChange("overall", e.target.value)}
          className="rating-slider"
        />
      </div>

      <div className="review-form-section">
        <label className="form-label">Megjegyzés:</label>
        <textarea
          className="form-control"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Írd ide a véleményed..."
          rows="3"
        />
      </div>

      <button type="submit" className="btn-submit">
        Értékelés beküldése
      </button>
    </form>
  );
};

export default ReviewForm;
