import React, { useState, useEffect, useCallback, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUser, updateUser } from '../services/userService';
import { AuthContext } from '../App';
import placeholderImage from '../assets/placeholder.png';
const Profile = () => {
    const { isLoggedIn, user: contextUser, login: contextLogin, logout: contextLogout } = useContext(AuthContext);
    const navigate = useNavigate();

    // --- State ---
    const [user, setUser] = useState(contextUser || null);
    const [loading, setLoading] = useState(!contextUser);
    const [updateLoading, setUpdateLoading] = useState(false); // Csak a gombhoz
    const [editing, setEditing] = useState(false);
    const [error, setError] = useState(''); // Fetch/General error
    const [updateError, setUpdateError] = useState(''); // Update specific error
    const [validationErrors, setValidationErrors] = useState({});
    const [updateSuccess, setUpdateSuccess] = useState('');

    // Form state
    const [newName, setNewName] = useState('');
    const [newEmail, setNewEmail] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [profileImage, setProfileImage] = useState('');

    // --- Init Form ---
    const initializeForm = useCallback((userData) => {
        if (!userData) return;
        setNewName(userData.name || '');
        setNewEmail(userData.email || '');
        setPhoneNumber(userData.phone || '');
        const currentImageUrl = userData.profile_picture_url || '';
        setImageUrl(currentImageUrl);
        setProfileImage(currentImageUrl);
        setNewPassword('');
        setValidationErrors({});
        setUpdateError('');
        setUpdateSuccess('');
    }, []);

    // --- Fetch User ---
    const fetchUser = useCallback(async () => {
        if (!isLoggedIn || loading) return;
        setLoading(true); setError('');
        try {
            const userData = await getUser();
            if (!userData) throw new Error("User data fetch returned null/undefined.");
            setUser(userData);
            contextLogin(userData, localStorage.getItem('token'));
            initializeForm(userData);
        } catch (err) {
            setError(err.message || 'Profil betöltési hiba.');
            setUser(null); // Clear user on error
            // Optional: Logout/redirect on auth error
        } finally {
            setLoading(false);
        }
    }, [isLoggedIn, loading, contextLogin, initializeForm]); // Removed some dependencies for simplicity in debugging

    // --- Initial Load Effect ---
     useEffect(() => {
         if (isLoggedIn) {
             if (!user) { // Fetch only if local state is null
                 fetchUser();
             } else { // If user exists (from context or previous fetch), initialize form
                 initializeForm(user);
                 if(loading) setLoading(false); // Ensure loading stops if initialized from context
             }
         }
     }, [isLoggedIn, user, fetchUser, initializeForm, loading]); // Monitor user as well

    // --- Validation ---
    const validateForm = useCallback(() => {
        // ... (validation logic - keep as is) ...
        const errors = {};
        if (!newName.trim()) errors.name = 'Név megadása kötelező!';
        if (!newEmail.trim()) {
          errors.email = 'Email cím megadása kötelező!';
        } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(newEmail)) {
          errors.email = 'Érvénytelen email cím formátum!';
        }
        if (newPassword && newPassword.length < 6) {
          errors.password = 'Jelszó min. 6 karakter!';
        }
        if (phoneNumber && phoneNumber.trim() && !/^\+?[0-9\s-()]{7,}$/.test(phoneNumber)) {
          errors.phoneNumber = 'Érvénytelen telefonszám!';
        }
        const trimmedImageUrl = imageUrl.trim();
        if (trimmedImageUrl && !/^https?:\/\/.+\..+/i.test(trimmedImageUrl)) {
          errors.imageUrl = 'Érvénytelen URL (http(s)://...)!';
        }
        setValidationErrors(errors);
        return Object.keys(errors).length === 0;
    }, [newName, newEmail, newPassword, phoneNumber, imageUrl]);

    // --- Handle Update (SUPER SIMPLIFIED + LOGGING) ---
    const handleEdit = async (e) => {
        e.preventDefault();
        console.log("[handleEdit] Starting...");
        if (!validateForm()) {
            console.log("[handleEdit] Validation failed.");
            return;
        }

        setUpdateLoading(true); // <<< START Loading
        console.log("[handleEdit] updateLoading set to true");
        setUpdateError('');
        setUpdateSuccess('');

        const dataToUpdate = {
            name: newName.trim(),
            email: newEmail.trim(),
            phone: phoneNumber.trim() || null,
            profile_picture_url: imageUrl.trim() || null,
        };
        if (newPassword.trim()) {
            dataToUpdate.password = newPassword.trim();
        }

        try {
            console.log("[handleEdit] Calling updateUser with data:", dataToUpdate);
            // --- THE API CALL ---
            const response = await updateUser(dataToUpdate);
            // --- AFTER THE API CALL ---
            console.log("[handleEdit] updateUser call finished. Response:", response);

            // --- MINIMAL STATE UPDATE ---
            setEditing(false); // Exit edit mode
            setUpdateSuccess('Mentés sikeresnek tűnik. Frissítsd az oldalt a változásokért.'); // Inform user
             console.log("[handleEdit] Exited edit mode, set success message.");
             // ITT NEM HÍVJUK MEG A fetchUser-t ÉS NEM FRISSÍTJÜK KÖZVETLENÜL A state-et

        } catch (updateErr) {
            // --- ERROR DURING API CALL ---
            console.error("[handleEdit] Error during updateUser call:", updateErr);
            setUpdateError(updateErr.message || 'Hiba történt a mentés során.');
             console.log("[handleEdit] Set updateError.");
        } finally {
            // --- THIS MUST ALWAYS RUN ---
            console.log("[handleEdit] Entering finally block...");
            setUpdateLoading(false); // <<< STOP Loading
            console.log("[handleEdit] updateLoading set to false. Process finished.");
        }
    };

    // --- Logout ---
    const handleLogout = () => { /* ... (as before) ... */ };
    // --- Cancel Edit ---
    const handleCancelEdit = () => { /* ... (as before, uses initializeForm) ... */ };

    // --- Render Logic ---
    if (loading) { /* ... Spinner ... */ }
    if (!user) { /* ... Error / Login prompt ... */ }

    // --- Main Profile Render ---
    return (
        <div className="container py-5">
            <div className="profile-card">
                {/* ... Title, Messages ... */}
                 {updateSuccess && <div className="alert alert-success">{updateSuccess}</div>}
                 {updateError && <div className="alert alert-danger">{updateError}</div>}
                 {error && !editing && <div className="alert alert-danger">{error}</div>}

                <div className="profile-content">
                    {/* --- Image Section --- */}
                    <div className="profile-image-section">
                    <img
                src={profileImage || placeholderImage} // Használhatod alapértelmezettnek is
                alt={`${user.name} profilképe`}
                className="profile-image"
                key={profileImage}
                onError={(e) => {
                    console.log("Original image failed to load, using local fallback.");
                    e.target.onerror = null; // Prevent infinite loop
                    e.target.src = placeholderImage; // Használd az importált képet
                }}
            />
                        {editing && (
                            <div className="mt-3">
                                <label htmlFor="profileImageUrl" className="form-label">Profilkép URL</label>
                                <input
                                    type="text"
                                    className={`form-control ${validationErrors.imageUrl ? 'is-invalid' : ''}`}
                                    id="profileImageUrl"
                                    value={imageUrl} // Input state
                                    // Preview-t most nem frissítjük mentés nélkül
                                    onChange={(e) => setImageUrl(e.target.value)}
                                    placeholder="Kép URL-je (pl. https://...)"
                                    disabled={updateLoading}
                                />
                                {validationErrors.imageUrl && <div className="invalid-feedback">{validationErrors.imageUrl}</div>}
                            </div>
                        )}
                    </div>

                    {/* --- Details / Form Section --- */}
                    <h3 className="profile-subtitle mb-3">Felhasználói adatok</h3>
                    {!editing ? (
                        // Display Mode (uses 'user' state)
                        <div className="profile-details">
                           {/* ... (adatok megjelenítése a 'user' state alapján) ... */}
                           <p><strong>Név:</strong> {user.name}</p>
                           <p><strong>Email:</strong> {user.email}</p>
                           <p><strong>Telefonszám:</strong> {user.phone || 'Nincs megadva'}</p>
                           <p><strong>Regisztráció:</strong> {user.created_at ? new Date(user.created_at).toLocaleDateString('hu-HU') : 'Ismeretlen'}</p>
                            <div className="profile-actions mt-4">
                                <button className="btn btn-outline-primary" onClick={() => setEditing(true)} disabled={updateLoading}>Adatok szerkesztése</button>
                                <button className="btn btn-outline-danger ms-2" onClick={handleLogout} disabled={updateLoading}>Kilépés</button>
                            </div>
                        </div>
                    ) : (
                        // Editing Mode (uses form states: newName, newEmail etc.)
                        <form id="profileForm" onSubmit={handleEdit}>
                           {/* ... (Input mezők, mint korábban, a form state-ekkel összekötve) ... */}
                           {/* Name */}
                           <div className="mb-3"> <label>Név</label> <input value={newName} onChange={(e) => setNewName(e.target.value)} className={`form-control ${validationErrors.name ? 'is-invalid' : ''}`} disabled={updateLoading} required /> {validationErrors.name && <div className="invalid-feedback">{validationErrors.name}</div>}</div>
                           {/* Email */}
                           <div className="mb-3"> <label>Email</label> <input type="email" value={newEmail} onChange={(e) => setNewEmail(e.target.value)} className={`form-control ${validationErrors.email ? 'is-invalid' : ''}`} disabled={updateLoading} required /> {validationErrors.email && <div className="invalid-feedback">{validationErrors.email}</div>}</div>
                            {/* Phone */}
                           <div className="mb-3"> <label>Telefon</label> <input type="tel" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} className={`form-control ${validationErrors.phoneNumber ? 'is-invalid' : ''}`} placeholder="Opcionális" disabled={updateLoading} /> {validationErrors.phoneNumber && <div className="invalid-feedback">{validationErrors.phoneNumber}</div>}</div>
                           {/* Password */}
                           <div className="mb-3"> <label>Új jelszó</label> <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} className={`form-control ${validationErrors.password ? 'is-invalid' : ''}`} placeholder="Üresen hagyva nem változik" disabled={updateLoading} /> {validationErrors.password && <div className="invalid-feedback">{validationErrors.password}</div>}</div>

                            {/* Buttons */}
                            <div className="profile-actions mt-4">
                                <button type="submit" className="btn btn-primary" disabled={updateLoading}>
                                    {updateLoading ? <><span className="spinner-border spinner-border-sm me-2"></span>Mentés...</> : 'Mentés'}
                                </button>
                                <button type="button" className="btn btn-outline-secondary ms-2" onClick={handleCancelEdit} disabled={updateLoading}>
                                    Mégse
                                </button>
                            </div>
                        </form>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Profile;
