import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllNotes } from '../services/noteService';

const Sidebar = () => {
  const [brands, setBrands] = useState([]);
  const [notes, setNotes] = useState([]);
  const [selectedBrands, setSelectedBrands] = useState([]); // Több márka kiválasztásához
  const [scentFilter, setScentFilter] = useState('');
  const [genderFilter, setGenderFilter] = useState('');
  const [sortOption, setSortOption] = useState('name-asc');
  const [isOpen, setIsOpen] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Márkák betöltése
        const brandsResponse = await fetch('http://localhost:5000/api/brands');
        const brandsData = await brandsResponse.json();
        console.log('Betöltött márkák:', brandsData);
        setBrands(Array.isArray(brandsData) ? brandsData : []);

        // Illatjegyek betöltése
        const notesData = await getAllNotes();
        console.log('Betöltött illatjegyek:', notesData);
        setNotes(Array.isArray(notesData) ? notesData : []);

        setError(null);
      } catch (error) {
        console.error('Hiba az adatok betöltésénél:', error);
        setError('Nem sikerült betölteni az adatokat.');
        setBrands([]);
        setNotes([]);
      }
    };
    fetchData();
  }, []);

  const handleBrandChange = (brandName) => {
    const updatedSelection = selectedBrands.includes(brandName)
      ? selectedBrands.filter((name) => name !== brandName)
      : [...selectedBrands, brandName];
    setSelectedBrands(updatedSelection);
  };

  const handleSearch = () => {
    const queryParams = new URLSearchParams();
    if (selectedBrands.length > 0) {
      queryParams.set('brands', selectedBrands.join(',')); // Pl. brands=Chanel,Dior
    }
    if (scentFilter) queryParams.set('scent', scentFilter);
    if (genderFilter) queryParams.set('gender', genderFilter);
    if (sortOption) queryParams.set('sort', sortOption);
    navigate(`/kereses?${queryParams.toString()}`);
    setIsOpen(false);
  };

  const toggleSidebar = () => setIsOpen(!isOpen);

  return (
    <>
      {windowWidth <= 991 && (
        <button className="filter-toggle" onClick={toggleSidebar}>
          <i className="fas fa-filter"></i> {isOpen ? 'Bezár' : 'Szűrők'}
        </button>
      )}

      <div className={`sidebar d-flex flex-column ${windowWidth <= 991 && !isOpen ? 'hidden' : ''}`}>
        {error && <div className="alert alert-danger">{error}</div>}

        <div className="mb-3">
          <label htmlFor="sort" className="form-label">
            Rendezés
          </label>
          <select
            id="sort"
            className="form-select"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
          >
            <option value="name-asc">Név (A-Z)</option>
            <option value="name-desc">Név (Z-A)</option>
            <option value="price-asc">Ár (növekvő)</option>
            <option value="price-desc">Ár (csökkenő)</option>
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Márka</label>
          {brands.length > 0 ? (
            <div className="checkbox-group" style={{ maxHeight: '150px', overflowY: 'auto' }}>
              {brands.map((brand) => (
                <div key={brand.id} className="form-check">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id={`brand-${brand.id}`}
                    value={brand.name}
                    checked={selectedBrands.includes(brand.name)}
                    onChange={() => handleBrandChange(brand.name)}
                  />
                  <label className="form-check-label" htmlFor={`brand-${brand.id}`}>
                    {brand.name}
                  </label>
                </div>
              ))}
            </div>
          ) : (
            <p>Nincsenek márkák</p>
          )}
        </div>

        <div className="mb-3">
          <label htmlFor="scent" className="form-label">
            Illatjegy
          </label>
          <select
            id="scent"
            className="form-select"
            value={scentFilter}
            onChange={(e) => setScentFilter(e.target.value)}
          >
            <option value="">Összes</option>
            {notes.length > 0 ? (
              notes.map((note) => (
                <option key={note.id} value={note.name}>
                  {note.name}
                </option>
              ))
            ) : (
              <option value="">Nincsenek illatjegyek</option>
            )}
          </select>
        </div>

        <div className="mb-3">
          <label htmlFor="gender" className="form-label">
            Nem
          </label>
          <select
            id="gender"
            className="form-select"
            value={genderFilter}
            onChange={(e) => setGenderFilter(e.target.value)}
          >
            <option value="">Összes</option>
            <option value="male">Férfi</option>
            <option value="female">Női</option>
            <option value="unisex">Unisex</option>
          </select>
        </div>

        <div className="mt-3">
          <button className="btn btn-peach w-100" onClick={handleSearch}>
            Keresés
          </button>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
