import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';
import '../style.css';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [activeItem, setActiveItem] = useState('home');

  // Navigációs elemek
  const navItems = [
    { id: 'home', label: 'Főoldal', href: '#' },
    { id: 'about', label: 'Rólunk', href: '#about' },
    { id: 'services', label: 'Szolgáltatások', href: '#services' },
    { id: 'portfolio', label: 'Portfólió', href: '#portfolio' },
    { id: 'contact', label: 'Kapcsolat', href: '#contact' }
  ];

  // Követjük a görgetést a navbar stílusának változtatásához
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Menüpont kattintás kezelése
  const handleItemClick = (id) => {
    setActiveItem(id);
    setIsOpen(false);
  };

  return (
    <nav className={`fixed w-full z-50 transition-all duration-300 ${scrolled ? 'bg-white shadow-md py-2' : 'bg-transparent py-4'}`}>
      <div className="max-w-6xl mx-auto px-4">
        <div className="flex justify-between items-center">
          
          {/* Logo */}
          <div className="flex items-center">
            <a href="#" className="font-bold text-xl text-blue-600">CégNév</a>
          </div>
          
          {/* Asztali navigáció */}
          <div className="hidden md:flex space-x-8">
            {navItems.map(item => (
              <a 
                key={item.id}
                href={item.href}
                className={`transition-colors duration-200 ${activeItem === item.id 
                  ? 'text-blue-600 font-medium' 
                  : 'text-gray-600 hover:text-blue-500'}`}
                onClick={() => handleItemClick(item.id)}
              >
                {item.label}
              </a>
            ))}
          </div>
          
          {/* Mobil menü gomb */}
          <div className="md:hidden flex items-center">
            <button 
              className="outline-none" 
              onClick={() => setIsOpen(!isOpen)}
            >
              {isOpen ? (
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              ) : (
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              )}
            </button>
          </div>
        </div>
      </div>
      
      {/* Mobil menü */}
      <div className={`md:hidden ${isOpen ? 'block' : 'hidden'} transition-all duration-300`}>
        <div className="px-2 pt-2 pb-3 space-y-1 bg-white shadow-md">
          {navItems.map(item => (
            <a
              key={item.id}
              href={item.href}
              className={`block px-3 py-2 rounded-md text-base font-medium ${
                activeItem === item.id 
                  ? 'bg-blue-500 text-white' 
                  : 'text-gray-700 hover:bg-blue-100 hover:text-blue-500'
              }`}
              onClick={() => handleItemClick(item.id)}
            >
              {item.label}
            </a>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
