// frontend/src/pages/Home.js
import React, { useState, useEffect } from 'react';
import PerfumeCard from '../components/PerfumeCard';
import { getFeaturedPerfumes } from '../services/perfumeService';

const Home = () => {
  const [featuredPerfumes, setFeaturedPerfumes] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFeaturedPerfumes = async () => {
      try {
        const perfumes = await getFeaturedPerfumes();
        setFeaturedPerfumes(perfumes);
      } catch (error) {
        setError('Nem sikerült betölteni a kiemelt parfümök listáját!');
      }
    };
    fetchFeaturedPerfumes();
  }, []);

  return (
    <div className="container">
      <div className="hero-section">
        <h1>Üdvözöljük a Parfümvilágban</h1>
        <p>Merülj el az illatok lenyűgöző világában, és találd meg a tökéletes parfümöt, amely kifejezi egyéniségedet.</p>
        <button className="btn btn-primary">Keresés indítása</button>
      </div>
      <h2>Kiemelt Parfümjeink</h2>
      <div className="row" id="perfumeList">
        {error && <div className="alert alert-danger">{error}</div>}
        {featuredPerfumes.length === 0 && !error && (
          <div id="noResults">
            <i className="fas fa-search"></i>
            <h4>Nincs találat a keresési feltételeknek megfelelően</h4>
            <p>Próbálj meg kevesebb vagy más szűrőfeltételt beállítani</p>
          </div>
        )}
        {featuredPerfumes.map(p => (
          <PerfumeCard key={p.id} perfume={p} />
        ))}
      </div>
    </div>
  );
};

export default Home;
