import React, { useState, useEffect, useCallback, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getUser, updateUser } from "../services/userService";
import { AuthContext } from "../App"; // Import AuthContext

const Profile = () => {
  const {
    isLoggedIn,
    user: contextUser,
    login: contextLogin,
    logout: contextLogout,
  } = useContext(AuthContext);
  const [user, setUser] = useState(contextUser);
  const [loading, setLoading] = useState(!contextUser); // Initial loading state
  const [updateLoading, setUpdateLoading] = useState(false); // Separate state for update operation
  const [editing, setEditing] = useState(false);
  const [error, setError] = useState("");
  const [validationErrors, setValidationErrors] = useState({});
  const [updateSuccess, setUpdateSuccess] = useState("");

  // Form state
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [profileImage, setProfileImage] = useState(""); // For display

  const navigate = useNavigate();

  // --- Fetch User Data ---
  const fetchUser = useCallback(async () => {
    if (!isLoggedIn) {
      navigate("/bejelentkezes");
      return;
    }
    console.log("Fetching user data..."); // Log fetch start
    setLoading(true);
    setError("");
    try {
      const userData = await getUser();
      console.log("User data fetched:", userData); // Log fetched data
      if (!userData) {
        throw new Error("Nem érkeztek felhasználói adatok.");
      }
      setUser(userData);
      contextLogin(userData, localStorage.getItem("token")); // Update context

      // Initialize form state ONLY after successful fetch
      setNewName(userData.name || "");
      setNewEmail(userData.email || "");
      setPhoneNumber(userData.phone || "");
      const initialImageUrl = userData.profile_picture_url || "";
      setImageUrl(initialImageUrl);
      setProfileImage(initialImageUrl);
    } catch (err) {
      console.error("Profile fetch error:", err);
      setError(err.message || "Hiba történt a profil betöltése közben.");
      setUser(null);
      // Optional logout on auth error
      if (err.message.includes("hitelesít") || err.response?.status === 401) {
        console.log("Auth error during fetch, logging out.");
        contextLogout();
        navigate("/bejelentkezes");
      }
    } finally {
      console.log("Fetching finished."); // Log fetch end
      setLoading(false);
    }
  }, [isLoggedIn, navigate, contextLogin, contextLogout]);

  useEffect(() => {
    // Fetch only if not editing and user data is not already present
    if (!editing && !user) {
      fetchUser();
    } else if (user) {
      // Ensure form state is initialized if user data comes from context initially
      setNewName(user.name || "");
      setNewEmail(user.email || "");
      setPhoneNumber(user.phone || "");
      const initialImageUrl = user.profile_picture_url || "";
      setImageUrl(initialImageUrl);
      setProfileImage(initialImageUrl);
      if (loading) setLoading(false); // Stop loading if data was from context
    } else if (!isLoggedIn) {
      navigate("/bejelentkezes"); // Redirect if somehow got here without login
    }
  }, [user, editing, isLoggedIn, fetchUser, loading, navigate]); // Add loading, navigate

  // --- Form Validation ---
  const validateForm = useCallback(() => {
    // ... (Validation logic remains the same as previous answer) ...
    const errors = {};
    if (!newName.trim()) errors.name = "Név megadása kötelező!";
    if (!newEmail.trim()) {
      errors.email = "Email cím megadása kötelező!";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(newEmail)) {
      errors.email = "Érvénytelen email cím formátum!";
    }
    if (newPassword && newPassword.length < 6) {
      errors.password =
        "A jelszónak legalább 6 karakter hosszúnak kell lennie!";
    }
    if (
      phoneNumber &&
      phoneNumber.trim() &&
      !/^\+?[0-9\s-()]{7,}$/.test(phoneNumber)
    ) {
      errors.phoneNumber = "Érvénytelen telefonszám formátum!";
    }
    if (imageUrl && imageUrl.trim() && !/^https?:\/\/.+\..+/i.test(imageUrl)) {
      errors.imageUrl =
        "Érvénytelen URL formátum! (http:// vagy https:// kell)";
    }
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  }, [newName, newEmail, newPassword, phoneNumber, imageUrl]);

  // --- Handle Update ---
  const handleEdit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setUpdateLoading(true); // Use separate loading state for update
    setError("");
    setUpdateSuccess("");

    try {
      const updatedData = {
        name: newName.trim(),
        email: newEmail.trim(),
        phone: phoneNumber.trim() || null,
        profile_picture_url: imageUrl.trim() || null,
      };
      if (newPassword.trim()) {
        updatedData.password = newPassword.trim();
      }

      console.log("Attempting update with data:", updatedData); // Log data before sending
      const responseData = await updateUser(updatedData);
      console.log("Update successful, response:", responseData); // Log success

      // Backend might return the updated user or just success message
      // It's safer to re-fetch or use the data sent if response is minimal
      // For now, let's assume backend returns the updated user (without password)
      // If not, uncomment the fetchUser() call below
      const refreshedUser = responseData.user || { ...user, ...updatedData }; // Optimistic update or use response

      // Manually remove password from refreshedUser if it came from updatedData
      delete refreshedUser.password;

      setUser(refreshedUser);
      contextLogin(refreshedUser, localStorage.getItem("token")); // Update context

      setProfileImage(refreshedUser.profile_picture_url || ""); // Update display image
      setImageUrl(refreshedUser.profile_picture_url || ""); // Sync URL input state

      setEditing(false);
      setNewPassword("");
      setUpdateSuccess("Adatok sikeresen frissítve!");
      setTimeout(() => setUpdateSuccess(""), 4000);

      // Optional: Re-fetch user data to be absolutely sure it's current
      // await fetchUser();
    } catch (err) {
      console.error("Profile update error details:", err); // Log detailed error
      setError(err.message || "Hiba történt az adatok mentése közben.");
    } finally {
      console.log("Update process finished."); // Log update end
      setUpdateLoading(false); // Stop update loading indicator
    }
  };

  // --- Logout ---
  const handleLogout = () => {
    if (window.confirm("Biztosan ki szeretne lépni?")) {
      contextLogout();
      navigate("/");
    }
  };

  // --- Retry Initial Fetch ---
  const handleRetry = () => {
    setError("");
    setLoading(true); // Ensure loading state is set for retry
    fetchUser();
  };

  // --- Render Logic ---

  if (loading) {
    // Show spinner during initial load
    return (
      <div className="container text-center p-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Profil betöltése...</span>
        </div>
      </div>
    );
  }

  if (error && !user) {
    // Show error if initial load failed
    return (
      <div className="container py-5">
        <div className="alert alert-danger" role="alert">
          <h4>Hiba</h4>
          <p>{error}</p>
          <button
            className="btn btn-primary me-2"
            onClick={() => navigate("/bejelentkezes")}
          >
            Bejelentkezés
          </button>
          <button className="btn btn-outline-secondary" onClick={handleRetry}>
            Újrapróbálás
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    // Fallback if user is somehow null after loading/error checks
    return (
      <div className="container py-5 text-center">
        <p>Felhasználói adatok nem érhetők el.</p>
        <button
          className="btn btn-primary"
          onClick={() => navigate("/bejelentkezes")}
        >
          Bejelentkezés
        </button>
      </div>
    );
  }

  // --- Main Profile Render ---
  return (
    <div className="container py-5">
      <div className="profile-card">
        <h1 className="profile-title mb-4">Profil</h1>

        {updateSuccess && (
          <div className="alert alert-success">{updateSuccess}</div>
        )}
        {error && editing && <div className="alert alert-danger">{error}</div>}

        <div className="profile-content">
          {/* Image Section */}
          <div className="profile-image-section">
            <img
              src={
                profileImage || "https://via.placeholder.com/150?text=Profil"
              }
              alt={`${user.name} profilképe`}
              className="profile-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/150?text=Hiba";
              }}
            />
            {editing && (
              <div className="mt-3">
                <label htmlFor="profileImageUrl" className="form-label">
                  Profilkép URL
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.imageUrl ? "is-invalid" : ""
                  }`}
                  id="profileImageUrl"
                  value={imageUrl}
                  onChange={(e) => {
                    const url = e.target.value;
                    setImageUrl(url);
                    setProfileImage(url);
                  }}
                  placeholder="Kép URL-je (pl. https://...)"
                  aria-describedby="imageUrlError"
                  disabled={updateLoading} // Disable during update
                />
                {validationErrors.imageUrl && (
                  <div id="imageUrlError" className="invalid-feedback">
                    {validationErrors.imageUrl}
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Details / Form */}
          <h3 className="profile-subtitle mb-3">Felhasználói adatok</h3>
          {!editing ? (
            <div className="profile-details">
              <p>
                <strong>Név:</strong> {user.name}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Telefonszám:</strong> {user.phone || "Nincs megadva"}
              </p>
              <p>
                <strong>Regisztráció:</strong>{" "}
                {user.created_at
                  ? new Date(user.created_at).toLocaleDateString("hu-HU")
                  : "Ismeretlen"}
              </p>
              <div className="profile-actions mt-4">
                <button
                  className="btn btn-outline-primary"
                  onClick={() => setEditing(true)}
                >
                  Adatok szerkesztése
                </button>
                <button
                  className="btn btn-outline-danger ms-2"
                  onClick={handleLogout}
                >
                  Kilépés
                </button>
              </div>
            </div>
          ) : (
            <form id="profileForm" onSubmit={handleEdit}>
              {/* Name Input */}
              <div className="mb-3">
                <label htmlFor="editName" className="form-label">
                  Név
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.name ? "is-invalid" : ""
                  }`}
                  id="editName"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  required
                  aria-describedby="nameError"
                  disabled={updateLoading}
                />
                {validationErrors.name && (
                  <div id="nameError" className="invalid-feedback">
                    {validationErrors.name}
                  </div>
                )}
              </div>
              {/* Email Input */}
              <div className="mb-3">
                <label htmlFor="editEmail" className="form-label">
                  Email cím
                </label>
                <input
                  type="email"
                  className={`form-control ${
                    validationErrors.email ? "is-invalid" : ""
                  }`}
                  id="editEmail"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  required
                  aria-describedby="emailError"
                  disabled={updateLoading}
                />
                {validationErrors.email && (
                  <div id="emailError" className="invalid-feedback">
                    {validationErrors.email}
                  </div>
                )}
              </div>
              {/* Phone Input */}
              <div className="mb-3">
                <label htmlFor="editPhoneNumber" className="form-label">
                  Telefonszám (opcionális)
                </label>
                <input
                  type="tel"
                  className={`form-control ${
                    validationErrors.phoneNumber ? "is-invalid" : ""
                  }`}
                  id="editPhoneNumber"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="Pl. +36 30 123 4567"
                  aria-describedby="phoneNumberError"
                  disabled={updateLoading}
                />
                {validationErrors.phoneNumber && (
                  <div id="phoneNumberError" className="invalid-feedback">
                    {validationErrors.phoneNumber}
                  </div>
                )}
              </div>
              {/* Password Input */}
              <div className="mb-3">
                <label htmlFor="editPassword" className="form-label">
                  Új jelszó (üresen hagyva nem változik)
                </label>
                <input
                  type="password"
                  className={`form-control ${
                    validationErrors.password ? "is-invalid" : ""
                  }`}
                  id="editPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  aria-describedby="passwordError"
                  placeholder="Min. 6 karakter"
                  disabled={updateLoading}
                />
                {validationErrors.password && (
                  <div id="passwordError" className="invalid-feedback">
                    {validationErrors.password}
                  </div>
                )}
              </div>
              {/* Action Buttons */}
              <div className="profile-actions mt-4">
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={updateLoading}
                >
                  {updateLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Mentés...
                    </>
                  ) : (
                    "Mentés"
                  )}
                </button>
                <button
                  type="button"
                  className="btn btn-outline-secondary ms-2"
                  onClick={() => {
                    setEditing(false);
                    setError(""); // Clear errors on cancel
                    setUpdateSuccess("");
                    setValidationErrors({});
                    // Reset form to current user data
                    setNewName(user.name || "");
                    setNewEmail(user.email || "");
                    setPhoneNumber(user.phone || "");
                    const currentImageUrl = user.profile_picture_url || "";
                    setImageUrl(currentImageUrl);
                    setProfileImage(currentImageUrl);
                    setNewPassword("");
                  }}
                  disabled={updateLoading}
                >
                  Mégse
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
