import React, { useState } from 'react';
import axios from 'axios';
import './style.css'; // Új CSS fájl a szűrő stílusokhoz

const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [showFilter, setShowFilter] = useState(false);
  const [filters, setFilters] = useState({
    gender: '',
    brand: '',
    minPrice: '',
    maxPrice: '',
  });

  // Szűrési logika (példa, a backend API-tól függ)
  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/perfumes/all`, {
        params: {
          search: searchTerm,
          gender: filters.gender,
          brand: filters.brand,
          minPrice: filters.minPrice,
          maxPrice: filters.maxPrice,
        },
      });
      setResults(response.data);
    } catch (error) {
      console.error('Hiba a keresés során:', error);
    }
  };

  // Szűrő ablak megnyitása/zárása
  const toggleFilter = () => {
    setShowFilter(!showFilter);
  };

  // Szűrők alkalmazása
  const applyFilters = () => {
    handleSearch();
    setShowFilter(false);
  };

  return (
    <div className="search-page">
      <div className="search-container">
        <h1 className="search-title">Keresés</h1>
        <p className="search-subtitle">Keresd meg kedvenc parfümödet!</p>

        <div className="search-input-group">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Keresés parfüm neve alapján..."
            className="form-control"
          />
          <button onClick={handleSearch} className="btn btn-peach">
            Keresés
          </button>
          <button onClick={toggleFilter} className="btn btn-outline-peach ml-2">
            Szűrők <i className="fas fa-filter"></i>
          </button>
        </div>

        {/* Szűrő Modális Ablak */}
        {showFilter && (
          <div className="filter-modal">
            <div className="filter-content">
              <h3>Szűrők</h3>
              <div className="filter-group">
                <label className="form-label">Nem</label>
                <select
                  value={filters.gender}
                  onChange={(e) => setFilters({ ...filters, gender: e.target.value })}
                  className="form-control"
                >
                  <option value="">Mind</option>
                  <option value="male">Férfi</option>
                  <option value="female">Nő</option>
                  <option value="unisex">Unisex</option>
                </select>
              </div>
              <div className="filter-group">
                <label className="form-label">Márka</label>
                <input
                  type="text"
                  value={filters.brand}
                  onChange={(e) => setFilters({ ...filters, brand: e.target.value })}
                  placeholder="Márka neve"
                  className="form-control"
                />
              </div>
              <div className="filter-group">
                <label className="form-label">Minimum ár</label>
                <input
                  type="number"
                  value={filters.minPrice}
                  onChange={(e) => setFilters({ ...filters, minPrice: e.target.value })}
                  placeholder="Min. ár"
                  className="form-control"
                />
              </div>
              <div className="filter-group">
                <label className="form-label">Maximum ár</label>
                <input
                  type="number"
                  value={filters.maxPrice}
                  onChange={(e) => setFilters({ ...filters, maxPrice: e.target.value })}
                  placeholder="Max. ár"
                  className="form-control"
                />
              </div>
              <div className="filter-actions">
                <button onClick={applyFilters} className="btn btn-peach">
                  Alkalmaz
                </button>
                <button onClick={toggleFilter} className="btn btn-outline-peach ml-2">
                  Bezár
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Keresési Eredmények */}
        <h2 className="results-title">Eredmények</h2>
        {results.length > 0 ? (
          <div className="results-grid">
            {results.map((perfume) => (
              <div key={perfume.id} className="perfume-card">
                <img src={perfume.image_url} alt={perfume.name} className="perfume-card-img" />
                <div className="perfume-card-body">
                  <h3 className="perfume-card-title">{perfume.name}</h3>
                  <p className="perfume-card-subtitle">{perfume.brand}</p>
                  <p className="perfume-card-text">{perfume.description}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div id="noResults">
            <i className="fas fa-search"></i>
            <h4>Nincs találat</h4>
            <p>Próbálj meg más kulcsszavakat vagy állíts be szűrőket!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Search;
