// --- START OF FILE ReviewList.js ---

import React, { useState } from "react";

// Helper function to format date
const formatDate = (dateString) => {
  if (!dateString) return "";
  try {
    // Updated format to exclude time, only date
    return new Date(dateString).toLocaleDateString("hu-HU", {
      year: "numeric",
      month: "long", // Use 'long' for month name e.g., "április"
      day: "numeric",
    });
  } catch (e) {
    console.error("Error formatting date:", e);
    return dateString; // Return original if formatting fails
  }
};

// Helper function to render stars
const renderStars = (rating) => {
  const filledStars = Math.max(0, Math.min(5, Math.round(rating || 0))); // Ensure 0-5 range
  return (
    <>
      {"★".repeat(filledStars)}
      {"☆".repeat(5 - filledStars)}
    </>
  );
};

// --- Individual Review Item Component ---
// Moved inside ReviewList or can be separate, but defined here for clarity
const ReviewItem = ({ review, currentUserId, onEdit, onDelete }) => {
    const [showActions, setShowActions] = useState(false);

    // Check if the logged-in user is the author of this review
    const isOwnReview = review.user_id === currentUserId;

    const handleToggleActions = (e) => {
        e.stopPropagation(); // Prevent click bubbling if needed
        setShowActions(prev => !prev);
    };

    const handleEditClick = (e) => {
        e.stopPropagation();
        onEdit(review); // Pass the full review object to the edit handler
        setShowActions(false); // Close dropdown after clicking
    };

    const handleDeleteClick = (e) => {
        e.stopPropagation();
        onDelete(review.id); // Pass the review ID to the delete handler
        setShowActions(false); // Close dropdown after clicking
    };

    // Close dropdown if clicking outside
    // Basic implementation - might need improvement for complex scenarios
    useEffect(() => {
        const handleClickOutside = (event) => {
            // Check if the click is outside the dropdown area
            // This requires a ref on the dropdown container, let's simplify for now
            // and just close on any click outside the button - might not be ideal
             if (showActions) {
                 // A more robust solution would check if the click target is inside the dropdown
                 // For now, let's assume clicking anywhere else closes it (may close prematurely)
                 // A common pattern is to add a ref to the dropdown container and check event.target containment
                 // setShowActions(false); // Temporarily disable auto-close on outside click for simplicity
             }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [showActions]);


    return (
        <div className="review-item-custom card mb-3 position-relative">
            {/* Actions Button - Only show if it's the user's own review */}
            {isOwnReview && (
                <div className="position-absolute" style={{ top: '10px', right: '10px', zIndex: 2 }}>
                    <button
                        onClick={handleToggleActions}
                        className="btn btn-sm btn-light p-1"
                        aria-label="Értékelés műveletek"
                        style={{ lineHeight: 1, background: 'transparent', border: 'none' }}
                    >
                        <i className="fas fa-ellipsis-v"></i> {/* Vertical dots icon */}
                    </button>

                    {/* Dropdown for actions */}
                    {showActions && (
                         <div
                            className="position-absolute bg-white border rounded shadow-sm py-1"
                            style={{ top: '100%', right: '0', minWidth: '120px', marginTop: '2px' }} // Position dropdown below button
                         >
                            <button
                                onClick={handleEditClick}
                                className="dropdown-item d-block w-100 text-start px-3 py-1 small"
                            >
                                <i className="fas fa-edit me-2"></i>Szerkesztés
                            </button>
                            <button
                                onClick={handleDeleteClick}
                                className="dropdown-item d-block w-100 text-start px-3 py-1 small text-danger"
                             >
                                <i className="fas fa-trash-alt me-2"></i>Törlés
                            </button>
                        </div>
                    )}
                </div>
            )}

            {/* Card Body with Review Content */}
            <div className="card-body">
                <div className="review-header-custom d-flex justify-content-between align-items-center mb-2 pb-1 border-bottom">
                    <span className="review-author-custom fw-bold">
                        {review.author || "Ismeretlen felhasználó"}
                        {/* Optional: Indicate user's own review */}
                        {/* {isOwnReview && <span className="text-muted small ms-1">(Te)</span>} */}
                    </span>
                    <span className="review-date-custom text-muted">
                        {formatDate(review.created_at)}
                    </span>
                </div>

                {/* Ratings Grid */}
                <div className="review-ratings-grid mb-2">
                    <div className="rating-item">
                        <span className="rating-category">Illatcsík:</span>
                        <span className="rating-stars">{renderStars(review.scent_trail_rating)}</span>
                    </div>
                    <div className="rating-item">
                        <span className="rating-category">Tartósság:</span>
                        <span className="rating-stars">{renderStars(review.longevity_rating)}</span>
                    </div>
                    <div className="rating-item">
                        <span className="rating-category">Ár/Érték:</span>
                        <span className="rating-stars">{renderStars(review.value_rating)}</span>
                    </div>
                    <div className="rating-item">
                        <span className="rating-category">Összbenyomás:</span>
                        <span className="rating-stars">{renderStars(review.overall_impression)}</span>
                    </div>
                </div>

                {/* Review Comment */}
                {review.review_text && (
                    <p className="review-comment-custom card-text bg-light p-2 rounded border mt-2">
                        {review.review_text}
                    </p>
                )}
            </div>
        </div>
    );
};

// --- Main ReviewList Component ---
const ReviewList = ({ reviews, loading, currentUserId, onEdit, onDelete }) => {
  // reviews: Array of review objects passed from PerfumeDetail
  // loading: Boolean indicating if reviews are loading (passed from PerfumeDetail)
  // currentUserId: ID of the logged-in user (passed from PerfumeDetail)
  // onEdit: Function to call when edit is clicked (passed from PerfumeDetail)
  // onDelete: Function to call when delete is clicked (passed from PerfumeDetail)

  if (loading) {
    return <div className="text-center my-4 text-muted">Értékelések betöltése...</div>;
  }

  if (!reviews || reviews.length === 0) {
    return <p className="text-center text-muted mt-4">Még nincsenek értékelések ehhez a parfümhöz.</p>;
  }

  return (
    <div className="review-list-custom mt-4">
      {/* Map through the reviews passed as props */}
      {reviews.map((review) => (
        <ReviewItem
          key={review.id} // Unique key for each item
          review={review}
          currentUserId={currentUserId}
          onEdit={onEdit}
          onDelete={onDelete}
        />
      ))}
    </div>
  );
};

export default ReviewList;
// --- END OF FILE ReviewList.js ---
