import React, { useState, useEffect, useCallback, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getUser, updateUser } from "../services/userService";
import { AuthContext } from "../App";

const Profile = () => {
  const {
    isLoggedIn,
    user: contextUser,
    login: contextLogin,
    logout: contextLogout,
  } = useContext(AuthContext);
  const [user, setUser] = useState(contextUser || null);
  const [loading, setLoading] = useState(!contextUser);
  const [updateLoading, setUpdateLoading] = useState(false); // State for update specifically
  const [editing, setEditing] = useState(false);
  const [error, setError] = useState("");
  const [validationErrors, setValidationErrors] = useState({});
  const [updateSuccess, setUpdateSuccess] = useState("");

  // Form state
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [profileImage, setProfileImage] = useState(""); // For display

  const navigate = useNavigate();

  // --- Initialize Form State ---
  const initializeForm = useCallback((userData) => {
    if (!userData) return;
    console.log("Initializing form with:", userData);
    setNewName(userData.name || "");
    setNewEmail(userData.email || "");
    setPhoneNumber(userData.phone || "");
    const initialImageUrl = userData.profile_picture_url || "";
    setImageUrl(initialImageUrl);
    setProfileImage(initialImageUrl);
    setNewPassword("");
    setValidationErrors({});
    setError("");
    setUpdateSuccess("");
  }, []); // Empty dependency array, relies on userData argument

  // --- Fetch User Data (if needed) ---
  const fetchUser = useCallback(async () => {
    if (!isLoggedIn) {
      navigate("/bejelentkezes");
      return;
    }
    if (loading) return; // Prevent re-fetching if already loading

    console.log("Fetching user data...");
    setLoading(true);
    setError("");
    try {
      const userData = await getUser();
      console.log("User data fetched:", userData);
      if (!userData) throw new Error("Nem érkeztek felhasználói adatok.");

      setUser(userData);
      contextLogin(userData, localStorage.getItem("token")); // Update context
      initializeForm(userData); // Initialize form AFTER successful fetch
    } catch (err) {
      console.error("Profile fetch error:", err);
      setError(err.message || "Hiba történt a profil betöltése közben.");
      setUser(null);
      if (err.message.includes("hitelesít") || err.response?.status === 401) {
        contextLogout();
        navigate("/bejelentkezes");
      }
    } finally {
      setLoading(false);
    }
  }, [
    isLoggedIn,
    navigate,
    contextLogin,
    contextLogout,
    loading,
    initializeForm,
  ]); // Add loading, initForm

  // --- Effect to Fetch or Initialize Form ---
  useEffect(() => {
    if (isLoggedIn) {
      if (contextUser) {
        // If user data is already in context
        console.log("User data found in context, initializing form.");
        setUser(contextUser); // Ensure local state is synced
        initializeForm(contextUser);
        if (loading) setLoading(false); // Ensure loading is stopped
      } else if (!loading) {
        // If not in context and not already loading
        fetchUser();
      }
    } else {
      navigate("/bejelentkezes"); // Redirect if not logged in
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isLoggedIn, contextUser, navigate]); // Rerun if login status or contextUser changes

  // --- Form Validation ---
  const validateForm = useCallback(() => {
    const errors = {};
    if (!newName.trim()) errors.name = "Név megadása kötelező!";
    if (!newEmail.trim()) {
      errors.email = "Email cím megadása kötelező!";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(newEmail)) {
      errors.email = "Érvénytelen email cím formátum!";
    }
    if (newPassword && newPassword.length < 6) {
      errors.password =
        "A jelszónak legalább 6 karakter hosszúnak kell lennie!";
    }
    if (
      phoneNumber &&
      phoneNumber.trim() &&
      !/^\+?[0-9\s-()]{7,}$/.test(phoneNumber)
    ) {
      errors.phoneNumber = "Érvénytelen telefonszám formátum!";
    }
    const trimmedImageUrl = imageUrl.trim();
    if (trimmedImageUrl && !/^https?:\/\/.+\..+/i.test(trimmedImageUrl)) {
      errors.imageUrl =
        "Érvénytelen URL formátum! (http:// vagy https:// kell)";
    }
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  }, [newName, newEmail, newPassword, phoneNumber, imageUrl]);

  // --- Handle Update ---
  const handleEdit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setUpdateLoading(true); // Start update loading
    setError("");
    setUpdateSuccess("");

    const updatedData = {
      name: newName.trim(),
      email: newEmail.trim(),
      phone: phoneNumber.trim() || null,
      profile_picture_url: imageUrl.trim() || null,
    };
    if (newPassword.trim()) {
      updatedData.password = newPassword.trim();
    }

    try {
      console.log("Attempting update with data:", updatedData);
      // Assuming updateUser returns { success: true, user: updatedUserData }
      const response = await updateUser(updatedData);
      console.log("Update API response:", response);

      if (!response || !response.user) {
        // If backend doesn't return the full user, we have to trust the sent data or re-fetch
        console.warn(
          "Backend did not return updated user object. Using submitted data for UI update."
        );
        // Create a temporary user object based on what was sent (excluding password)
        const optimisticUser = {
          ...user, // Start with current user state
          name: updatedData.name,
          email: updatedData.email,
          phone: updatedData.phone,
          profile_picture_url: updatedData.profile_picture_url,
          // DO NOT include updatedData.password here
        };
        setUser(optimisticUser); // Update local state optimistically
        contextLogin(optimisticUser, localStorage.getItem("token")); // Update context optimistically
        setProfileImage(optimisticUser.profile_picture_url || ""); // Update display image
      } else {
        // If backend *does* return the updated user object
        console.log("Backend returned updated user, updating state.");
        setUser(response.user); // Update local state with response
        contextLogin(response.user, localStorage.getItem("token")); // Update context with response
        setProfileImage(response.user.profile_picture_url || ""); // Update display image
        setImageUrl(response.user.profile_picture_url || ""); // Sync URL input state
      }

      setEditing(false);
      setNewPassword("");
      setUpdateSuccess("Adatok sikeresen frissítve!");
      setTimeout(() => setUpdateSuccess(""), 4000);
    } catch (err) {
      console.error("Profile update process error:", err);
      setError(err.message || "Hiba történt az adatok mentése közben.");
    } finally {
      console.log("Update process finished.");
      setUpdateLoading(false); // <<< --- **EZ A LEGFONTOSABB A VÉGTELEN TÖLTÉS ELLEN**
    }
  };

  // --- Logout ---
  const handleLogout = () => {
    if (window.confirm("Biztosan ki szeretne lépni?")) {
      contextLogout();
      navigate("/");
    }
  };

  // --- Cancel Edit ---
  const handleCancelEdit = () => {
    setEditing(false);
    if (user) {
      // Reset form from current user state
      initializeForm(user);
    }
    // Clear errors only related to editing
    setError("");
    setValidationErrors({});
    setUpdateSuccess("");
  };

  // --- Render Logic ---

  if (loading) {
    return (
      <div className="container text-center p-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Profil betöltése...</span>
        </div>
      </div>
    );
  }

  if (!user) {
    // If still no user after loading (error occurred)
    return (
      <div className="container py-5">
        <div className="alert alert-danger" role="alert">
          <h4>Hiba</h4>
          <p>{error || "Felhasználói adatok nem tölthetők be."}</p>
          <button
            className="btn btn-primary me-2"
            onClick={() => navigate("/bejelentkezes")}
          >
            Bejelentkezés
          </button>
          {!error.includes("hitelesít") && (
            <button
              className="btn btn-outline-secondary"
              onClick={() => fetchUser()}
            >
              Újrapróbálás
            </button>
          )}
        </div>
      </div>
    );
  }

  // --- Main Profile Render ---
  return (
    <div className="container py-5">
      <div className="profile-card">
        <h1 className="profile-title mb-4">Profil</h1>

        {updateSuccess && (
          <div className="alert alert-success">{updateSuccess}</div>
        )}
        {/* Show general error only when NOT editing, or update error when editing */}
        {error && !editing && <div className="alert alert-danger">{error}</div>}
        {error && editing && <div className="alert alert-danger">{error}</div>}

        <div className="profile-content">
          {/* Image Section */}
          <div className="profile-image-section">
            <img
              // Use profileImage state for display
              src={
                profileImage || "https://via.placeholder.com/150?text=Profil"
              }
              alt={`${user.name} profilképe`}
              className="profile-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/150?text=Hiba";
              }}
            />
            {editing && (
              <div className="mt-3">
                <label htmlFor="profileImageUrl" className="form-label">
                  Profilkép URL
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.imageUrl ? "is-invalid" : ""
                  }`}
                  id="profileImageUrl"
                  value={imageUrl}
                  onChange={(e) => {
                    const url = e.target.value;
                    setImageUrl(url);
                    setProfileImage(url); // Update preview
                  }}
                  placeholder="Kép URL-je (pl. https://...)"
                  aria-describedby="imageUrlError"
                  disabled={updateLoading}
                />
                {validationErrors.imageUrl && (
                  <div id="imageUrlError" className="invalid-feedback">
                    {validationErrors.imageUrl}
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Details / Form Section */}
          <h3 className="profile-subtitle mb-3">Felhasználói adatok</h3>
          {!editing ? (
            // --- Display Mode ---
            <div className="profile-details">
              <p>
                <strong>Név:</strong> {user.name}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Telefonszám:</strong> {user.phone || "Nincs megadva"}
              </p>
              <p>
                <strong>Regisztráció:</strong>{" "}
                {user.created_at
                  ? new Date(user.created_at).toLocaleDateString("hu-HU")
                  : "Ismeretlen"}
              </p>
              <div className="profile-actions mt-4">
                <button
                  className="btn btn-outline-primary"
                  onClick={() => setEditing(true)}
                  disabled={updateLoading}
                >
                  Adatok szerkesztése
                </button>
                <button
                  className="btn btn-outline-danger ms-2"
                  onClick={handleLogout}
                  disabled={updateLoading}
                >
                  Kilépés
                </button>
              </div>
            </div>
          ) : (
            // --- Editing Mode ---
            <form id="profileForm" onSubmit={handleEdit}>
              {/* Input fields using state: newName, newEmail, phoneNumber, imageUrl, newPassword */}
              {/* Name */}
              <div className="mb-3">
                <label htmlFor="editName" className="form-label">
                  Név
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.name ? "is-invalid" : ""
                  }`}
                  id="editName"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  required
                  disabled={updateLoading}
                  aria-describedby="nameError"
                />
                {validationErrors.name && (
                  <div id="nameError" className="invalid-feedback">
                    {validationErrors.name}
                  </div>
                )}
              </div>
              {/* Email */}
              <div className="mb-3">
                <label htmlFor="editEmail" className="form-label">
                  Email cím
                </label>
                <input
                  type="email"
                  className={`form-control ${
                    validationErrors.email ? "is-invalid" : ""
                  }`}
                  id="editEmail"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  required
                  disabled={updateLoading}
                  aria-describedby="emailError"
                />
                {validationErrors.email && (
                  <div id="emailError" className="invalid-feedback">
                    {validationErrors.email}
                  </div>
                )}
              </div>
              {/* Phone */}
              <div className="mb-3">
                <label htmlFor="editPhoneNumber" className="form-label">
                  Telefonszám (opcionális)
                </label>
                <input
                  type="tel"
                  className={`form-control ${
                    validationErrors.phoneNumber ? "is-invalid" : ""
                  }`}
                  id="editPhoneNumber"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="Pl. +36 30 123 4567"
                  disabled={updateLoading}
                  aria-describedby="phoneNumberError"
                />
                {validationErrors.phoneNumber && (
                  <div id="phoneNumberError" className="invalid-feedback">
                    {validationErrors.phoneNumber}
                  </div>
                )}
              </div>
              {/* Password */}
              <div className="mb-3">
                <label htmlFor="editPassword" className="form-label">
                  Új jelszó (üresen hagyva nem változik)
                </label>
                <input
                  type="password"
                  className={`form-control ${
                    validationErrors.password ? "is-invalid" : ""
                  }`}
                  id="editPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  placeholder="Min. 6 karakter"
                  disabled={updateLoading}
                  aria-describedby="passwordError"
                />
                {validationErrors.password && (
                  <div id="passwordError" className="invalid-feedback">
                    {validationErrors.password}
                  </div>
                )}
              </div>
              {/* Action Buttons */}
              <div className="profile-actions mt-4">
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={updateLoading}
                >
                  {updateLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Mentés...
                    </>
                  ) : (
                    "Mentés"
                  )}
                </button>
                <button
                  type="button"
                  className="btn btn-outline-secondary ms-2"
                  onClick={handleCancelEdit}
                  disabled={updateLoading}
                >
                  Mégse
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
