// --- START OF FILE ReviewForm.js ---

import React, { useState, useEffect, useContext } from "react"; // Added useEffect
import { AuthContext } from "../App";
import { createReview, updateReview } from "../services/reviewService"; // Import updateReview

const ReviewForm = ({
  perfumeId,
  initialData, // New prop for editing
  onReviewSubmitted, // Callback for new review success
  onReviewUpdated, // Callback for update success
  onCancelEdit // Callback to cancel editing
}) => {
  const { isLoggedIn } = useContext(AuthContext);
  const isEditing = !!initialData; // Check if we are in edit mode

  const [ratings, setRatings] = useState({
    sillage: 0,
    longevity: 0,
    value: 0,
    overall: 0,
  });
  const [comment, setComment] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(""); // Success message state removed, parent handles it
  const [loading, setLoading] = useState(false);

  // Effect to populate form when initialData changes (for editing)
  useEffect(() => {
    if (isEditing && initialData) {
      setRatings({
        sillage: initialData.scent_trail_rating || 0,
        longevity: initialData.longevity_rating || 0,
        value: initialData.value_rating || 0,
        overall: initialData.overall_impression || 0,
      });
      setComment(initialData.review_text || "");
      setError(""); // Clear any previous errors when starting edit
    } else {
      // Reset form if not editing or initialData is cleared
       setRatings({ sillage: 0, longevity: 0, value: 0, overall: 0 });
       setComment("");
    }
  }, [initialData, isEditing]); // Depend on initialData and isEditing

  const handleRatingChange = (category, value) => {
    const numValue = Math.max(0, Math.min(5, parseInt(value, 10)));
    setRatings((prevRatings) => ({ ...prevRatings, [category]: numValue }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isLoggedIn) {
      setError("Be kell jelentkezni az értékeléshez!");
      return;
    }

    if (Object.values(ratings).some((rating) => rating === 0)) {
      setError("Kérlek, adj értékelést minden szempontra (minimum 1)!");
      return;
    }
    // Comment is optional for update maybe? Keep it required for now.
    if (!comment.trim()) {
        setError("Kérlek, írj véleményt!");
        return;
    }


    setError("");
    // setSuccess(""); // Parent handles success message
    setLoading(true);

    const reviewDataPayload = {
      sillage: ratings.sillage,
      longevity: ratings.longevity,
      value: ratings.value,
      overall: ratings.overall,
      comment: comment,
    };

    try {
      if (isEditing) {
        // --- UPDATE ---
        const result = await updateReview(initialData.id, reviewDataPayload);
        if (onReviewUpdated) {
          onReviewUpdated(result.review); // Pass updated review to parent
        }
         // No need to reset form here, parent will likely hide it or pass null initialData
      } else {
        // --- CREATE ---
        const result = await createReview(perfumeId, reviewDataPayload);
        if (onReviewSubmitted) {
          onReviewSubmitted(result.review); // Pass new review to parent
        }
         // Reset form only after successful creation
         setRatings({ sillage: 0, longevity: 0, value: 0, overall: 0 });
         setComment("");
      }

    } catch (err) {
      setError(err.message || `Hiba történt az értékelés ${isEditing ? 'frissítésekor' : 'beküldésekor'}!`);
      setTimeout(() => setError(""), 7000);
    } finally {
      setLoading(false);
    }
  };

  // If not logged in, PerfumeDetail shows the message, so this can return null
  if (!isLoggedIn && !isEditing) { // Don't render form if not logged in AND not editing
    return null;
  }

  // Render only if logged in OR if editing (even if logged out, though unlikely scenario)
  if (!isLoggedIn && isEditing) {
      // This case shouldn't normally happen due to how edit is triggered,
      // but handle defensively.
       return <div className="alert alert-warning">Hiba: Szerkesztéshez be kell jelentkezni.</div>;
  }


  return (
    <form className="review-form-custom mb-4" onSubmit={handleSubmit}>
      <h4 className="mb-3">{isEditing ? "Értékelés szerkesztése" : "Írj értékelést!"}</h4>
      {error && <div className="alert alert-danger">{error}</div>}
      {/* Success message is now handled by the parent */}

      {/* Rating Sliders */}
      {/* ... (Slider inputs remain the same, using `ratings` state) ... */}
      <div className="review-form-section">
        <label htmlFor="sillageRating" className="form-label">
          Illatfelhő (Sillage):
        </label>
        <input
          type="range" id="sillageRating" min="0" max="5" step="1"
          value={ratings.sillage}
          onChange={(e) => handleRatingChange("sillage", e.target.value)}
          className="rating-slider" disabled={loading}
        />
        <span className="slider-value">{ratings.sillage}/5</span>
      </div>
       <div className="review-form-section">
        <label htmlFor="longevityRating" className="form-label">
          Tartósság (Longevity):
        </label>
        <input
          type="range" id="longevityRating" min="0" max="5" step="1"
          value={ratings.longevity}
          onChange={(e) => handleRatingChange("longevity", e.target.value)}
          className="rating-slider" disabled={loading}
        />
        <span className="slider-value">{ratings.longevity}/5</span>
      </div>
      <div className="review-form-section">
        <label htmlFor="valueRating" className="form-label">
          Ár/Érték arány (Value):
        </label>
        <input
          type="range" id="valueRating" min="0" max="5" step="1"
          value={ratings.value}
          onChange={(e) => handleRatingChange("value", e.target.value)}
          className="rating-slider" disabled={loading}
        />
        <span className="slider-value">{ratings.value}/5</span>
      </div>
       <div className="review-form-section">
        <label htmlFor="overallRating" className="form-label">
          Összbenyomás (Overall):
        </label>
        <input
          type="range" id="overallRating" min="0" max="5" step="1"
          value={ratings.overall}
          onChange={(e) => handleRatingChange("overall", e.target.value)}
          className="rating-slider" disabled={loading}
        />
        <span className="slider-value">{ratings.overall}/5</span>
      </div>


      {/* Comment Textarea */}
      <div className="review-form-section">
        <label htmlFor="reviewComment" className="form-label">Vélemény:</label>
        <textarea
          id="reviewComment"
          className="form-control"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Írd ide a véleményed..."
          rows="4"
          required
          disabled={loading}
        />
      </div>

      {/* Submit/Cancel Buttons */}
      <div className="d-flex gap-2 mt-3"> {/* Use flexbox for button layout */}
        <button type="submit" className="btn-submit flex-grow-1" disabled={loading}>
          {loading ? (isEditing ? "Frissítés..." : "Küldés...") : (isEditing ? "Értékelés frissítése" : "Értékelés beküldése")}
        </button>
        {isEditing && (
          <button
            type="button" // Important: type="button" to prevent form submission
            className="btn btn-secondary" // Standard secondary button
            onClick={onCancelEdit}
            disabled={loading}
          >
            Mégse
          </button>
        )}
      </div>
    </form>
  );
};

export default ReviewForm;
// --- END OF FILE ReviewForm.js ---
