import React, { useState, useEffect, useCallback, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getUser, updateUser } from "../services/userService";
import { AuthContext } from "../App";

const Profile = () => {
  const {
    isLoggedIn,
    user: contextUser,
    login: contextLogin,
    logout: contextLogout,
  } = useContext(AuthContext);
  // Initialize local user state from context, or null if not available initially
  const [user, setUser] = useState(contextUser || null);
  const [loading, setLoading] = useState(!contextUser); // Load only if context is empty initially
  const [updateLoading, setUpdateLoading] = useState(false);
  const [editing, setEditing] = useState(false);
  const [error, setError] = useState("");
  const [validationErrors, setValidationErrors] = useState({});
  const [updateSuccess, setUpdateSuccess] = useState("");

  // Form state - initialized in useEffect after user data is confirmed
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [profileImage, setProfileImage] = useState(""); // For display

  const navigate = useNavigate();

  // --- Function to Initialize Form State from User Data ---
  const initializeForm = useCallback((userData) => {
    if (!userData) return;
    setNewName(userData.name || "");
    setNewEmail(userData.email || "");
    setPhoneNumber(userData.phone || "");
    const initialImageUrl = userData.profile_picture_url || "";
    setImageUrl(initialImageUrl);
    setProfileImage(initialImageUrl); // Set display image based on fetched data
    setNewPassword(""); // Always clear password field when initializing
    setValidationErrors({}); // Clear validation errors
    setError(""); // Clear general errors
    setUpdateSuccess(""); // Clear success message
  }, []); // No dependencies needed here

  // --- Fetch User Data ---
  const fetchUser = useCallback(
    async (force = false) => {
      // Skip fetch if not logged in
      if (!isLoggedIn) {
        navigate("/bejelentkezes");
        return null; // Return null to indicate no user fetched
      }
      // Skip fetch if already loading or if data exists and not forced
      if (loading || (!force && user)) {
        if (user) initializeForm(user); // Ensure form is initialized if skipping fetch
        return user; // Return existing user
      }

      console.log("Fetching user data...");
      setLoading(true);
      setError("");
      try {
        const userData = await getUser();
        console.log("User data fetched:", userData);
        if (!userData) throw new Error("Nem érkeztek felhasználói adatok.");

        setUser(userData); // Update local state
        contextLogin(userData, localStorage.getItem("token")); // Update context
        initializeForm(userData); // Initialize form with fetched data
        setLoading(false); // Set loading false here after success
        return userData; // Return fetched data
      } catch (err) {
        console.error("Profile fetch error:", err);
        setError(err.message || "Hiba történt a profil betöltése közben.");
        setUser(null); // Clear user on error
        if (err.message.includes("hitelesít") || err.response?.status === 401) {
          console.log("Auth error during fetch, logging out.");
          contextLogout();
          navigate("/bejelentkezes");
        }
        setLoading(false); // Set loading false here after error
        return null; // Return null on error
      }
      // Removed finally block to set loading false inside try/catch
    },
    [
      isLoggedIn,
      navigate,
      contextLogin,
      contextLogout,
      user,
      loading,
      initializeForm,
    ]
  ); // Add dependencies

  // --- Effect to Fetch User Data on Mount ---
  useEffect(() => {
    fetchUser(); // Fetch on initial mount or if user becomes null
  }, [fetchUser]); // Depend only on the memoized fetchUser

  // --- Form Validation ---
  const validateForm = useCallback(() => {
    const errors = {};
    if (!newName.trim()) errors.name = "Név megadása kötelező!";
    if (!newEmail.trim()) {
      errors.email = "Email cím megadása kötelező!";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(newEmail)) {
      errors.email = "Érvénytelen email cím formátum!";
    }
    if (newPassword && newPassword.length < 6) {
      errors.password =
        "A jelszónak legalább 6 karakter hosszúnak kell lennie!";
    }
    if (
      phoneNumber &&
      phoneNumber.trim() &&
      !/^\+?[0-9\s-()]{7,}$/.test(phoneNumber)
    ) {
      errors.phoneNumber = "Érvénytelen telefonszám formátum!";
    }
    // Trim URL before validation
    const trimmedImageUrl = imageUrl.trim();
    if (trimmedImageUrl && !/^https?:\/\/.+\..+/i.test(trimmedImageUrl)) {
      errors.imageUrl =
        "Érvénytelen URL formátum! (http:// vagy https:// kell)";
    }
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  }, [newName, newEmail, newPassword, phoneNumber, imageUrl]);

  // --- Handle Update ---
  const handleEdit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setUpdateLoading(true);
    setError("");
    setUpdateSuccess("");

    try {
      const updatedData = {
        name: newName.trim(),
        email: newEmail.trim(),
        phone: phoneNumber.trim() || null,
        profile_picture_url: imageUrl.trim() || null, // Use trimmed URL
      };
      if (newPassword.trim()) {
        updatedData.password = newPassword.trim();
      }

      console.log("Attempting update with data:", updatedData);
      await updateUser(updatedData); // Call update service
      console.log("Update API call successful");

      // --- CRITICAL FIX: Re-fetch user data AFTER successful update ---
      console.log("Re-fetching user data after update...");
      const freshUserData = await fetchUser(true); // Force re-fetch

      if (freshUserData) {
        console.log("Re-fetch successful, updating state.");
        setEditing(false); // Exit editing mode
        setUpdateSuccess("Adatok sikeresen frissítve!");
        setTimeout(() => setUpdateSuccess(""), 4000);
      } else {
        // This case should ideally not happen if fetchUser handles errors,
        // but as a fallback:
        throw new Error(
          "Sikeres frissítés, de nem sikerült frissíteni a megjelenített adatokat."
        );
      }
    } catch (err) {
      console.error("Profile update process error:", err);
      // Use the error message from the catch block in fetchUser if it happened there,
      // or the error from updateUser
      setError(err.message || "Hiba történt az adatok mentése közben.");
    } finally {
      console.log("Update process finished.");
      setUpdateLoading(false); // Ensure loading indicator stops
    }
  };

  // --- Logout ---
  const handleLogout = () => {
    if (window.confirm("Biztosan ki szeretne lépni?")) {
      contextLogout();
      navigate("/");
    }
  };

  // --- Cancel Edit ---
  const handleCancelEdit = () => {
    setEditing(false);
    // Reset form to current user state
    if (user) {
      initializeForm(user);
    }
    setError(""); // Clear errors on cancel
    setUpdateSuccess("");
    setValidationErrors({});
  };

  // --- Render Logic ---

  if (loading) {
    return (
      <div className="container text-center p-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Profil betöltése...</span>
        </div>
      </div>
    );
  }

  // If after loading, user is still null (due to fetch error), show error
  if (!user) {
    return (
      <div className="container py-5">
        <div className="alert alert-danger" role="alert">
          <h4>Hiba</h4>
          <p>{error || "Felhasználói adatok nem tölthetők be."}</p>{" "}
          {/* Show specific error if available */}
          <button
            className="btn btn-primary me-2"
            onClick={() => navigate("/bejelentkezes")}
          >
            Bejelentkezés
          </button>
          {/* Provide retry only if it makes sense (e.g., not auth error) */}
          {!error.includes("hitelesít") && (
            <button
              className="btn btn-outline-secondary"
              onClick={() => fetchUser(true)}
            >
              Újrapróbálás
            </button>
          )}
        </div>
      </div>
    );
  }

  // --- Main Profile Render (User is guaranteed to be non-null here) ---
  return (
    <div className="container py-5">
      <div className="profile-card">
        <h1 className="profile-title mb-4">Profil</h1>

        {updateSuccess && (
          <div className="alert alert-success">{updateSuccess}</div>
        )}
        {error && editing && <div className="alert alert-danger">{error}</div>}

        <div className="profile-content">
          {/* Image Section */}
          <div className="profile-image-section">
            <img
              // Use profileImage state for display, which is updated on fetch/edit
              src={
                profileImage || "https://via.placeholder.com/150?text=Profil"
              }
              alt={`${user.name} profilképe`}
              className="profile-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/150?text=Kép?";
              }}
            />
            {editing && (
              <div className="mt-3">
                <label htmlFor="profileImageUrl" className="form-label">
                  Profilkép URL
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.imageUrl ? "is-invalid" : ""
                  }`}
                  id="profileImageUrl"
                  value={imageUrl}
                  onChange={(e) => {
                    const url = e.target.value;
                    setImageUrl(url);
                    setProfileImage(url); // Update display preview immediately
                  }}
                  placeholder="Kép URL-je (pl. https://...)"
                  aria-describedby="imageUrlError"
                  disabled={updateLoading}
                />
                {validationErrors.imageUrl && (
                  <div id="imageUrlError" className="invalid-feedback">
                    {validationErrors.imageUrl}
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Details / Form */}
          <h3 className="profile-subtitle mb-3">Felhasználói adatok</h3>
          {!editing ? (
            <div className="profile-details">
              <p>
                <strong>Név:</strong> {user.name}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Telefonszám:</strong> {user.phone || "Nincs megadva"}
              </p>
              <p>
                <strong>Regisztráció:</strong>{" "}
                {user.created_at
                  ? new Date(user.created_at).toLocaleDateString("hu-HU")
                  : "Ismeretlen"}
              </p>
              <div className="profile-actions mt-4">
                <button
                  className="btn btn-outline-primary"
                  onClick={() => setEditing(true)}
                >
                  Adatok szerkesztése
                </button>
                <button
                  className="btn btn-outline-danger ms-2"
                  onClick={handleLogout}
                >
                  Kilépés
                </button>
              </div>
            </div>
          ) : (
            <form id="profileForm" onSubmit={handleEdit}>
              {/* Name Input */}
              <div className="mb-3">
                <label htmlFor="editName" className="form-label">
                  Név
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.name ? "is-invalid" : ""
                  }`}
                  id="editName"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  required
                  aria-describedby="nameError"
                  disabled={updateLoading}
                />
                {validationErrors.name && (
                  <div id="nameError" className="invalid-feedback">
                    {validationErrors.name}
                  </div>
                )}
              </div>
              {/* Email Input */}
              <div className="mb-3">
                <label htmlFor="editEmail" className="form-label">
                  Email cím
                </label>
                <input
                  type="email"
                  className={`form-control ${
                    validationErrors.email ? "is-invalid" : ""
                  }`}
                  id="editEmail"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  required
                  aria-describedby="emailError"
                  disabled={updateLoading}
                />
                {validationErrors.email && (
                  <div id="emailError" className="invalid-feedback">
                    {validationErrors.email}
                  </div>
                )}
              </div>
              {/* Phone Input */}
              <div className="mb-3">
                <label htmlFor="editPhoneNumber" className="form-label">
                  Telefonszám (opcionális)
                </label>
                <input
                  type="tel"
                  className={`form-control ${
                    validationErrors.phoneNumber ? "is-invalid" : ""
                  }`}
                  id="editPhoneNumber"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="Pl. +36 30 123 4567"
                  aria-describedby="phoneNumberError"
                  disabled={updateLoading}
                />
                {validationErrors.phoneNumber && (
                  <div id="phoneNumberError" className="invalid-feedback">
                    {validationErrors.phoneNumber}
                  </div>
                )}
              </div>
              {/* Password Input */}
              <div className="mb-3">
                <label htmlFor="editPassword" className="form-label">
                  Új jelszó (üresen hagyva nem változik)
                </label>
                <input
                  type="password"
                  className={`form-control ${
                    validationErrors.password ? "is-invalid" : ""
                  }`}
                  id="editPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  aria-describedby="passwordError"
                  placeholder="Min. 6 karakter"
                  disabled={updateLoading}
                />
                {validationErrors.password && (
                  <div id="passwordError" className="invalid-feedback">
                    {validationErrors.password}
                  </div>
                )}
              </div>
              {/* Action Buttons */}
              <div className="profile-actions mt-4">
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={updateLoading}
                >
                  {updateLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Mentés...
                    </>
                  ) : (
                    "Mentés"
                  )}
                </button>
                <button
                  type="button"
                  className="btn btn-outline-secondary ms-2"
                  onClick={handleCancelEdit} // Use cancel handler
                  disabled={updateLoading}
                >
                  Mégse
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
