import React, { useState, useEffect } from 'react';
import PerfumeCard from '../components/PerfumeCard';
import { getAllPerfumes } from '../services/perfumeService';

const Search = () => {
  const [perfumes, setPerfumes] = useState([]);
  const [initialPerfumes, setInitialPerfumes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedBrands, setSelectedBrands] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedScents, setSelectedScents] = useState([]);
  const [sortOption, setSortOption] = useState('name-asc');
  const [currentPage, setCurrentPage] = useState(1);
  const [error, setError] = useState('');
  const [isSortDropdownOpen, setIsSortDropdownOpen] = useState(false);
  
  // Pagination settings
  const perfumesPerPage = 50;
  const [totalPages, setTotalPages] = useState(1);

  // Filter options from database
  const [brands, setBrands] = useState([]);
  const [scents, setScents] = useState([]);
  const categories = ['male', 'female', 'unisex']; // Static for now

  // Filter visibility toggles
  const [showMoreBrands, setShowMoreBrands] = useState(false);
  const [showMoreScents, setShowMoreScents] = useState(false);

  useEffect(() => {
    const fetchPerfumes = async () => {
      try {
        const perfumesData = await getAllPerfumes();
        setInitialPerfumes(perfumesData);
        setPerfumes(perfumesData.slice(0, perfumesPerPage));
        setTotalPages(Math.ceil(perfumesData.length / perfumesPerPage));

        // Extract unique brands and scents dynamically
        const uniqueBrands = [...new Set(perfumesData.map(p => p.brand))];
        const uniqueScents = [...new Set(perfumesData.flatMap(p => p.scents || []))];
        setBrands(uniqueBrands);
        setScents(uniqueScents);
      } catch (error) {
        setError('Nem sikerült betölteni a parfümök listáját!');
        setPerfumes([]);
      }
    };
    fetchPerfumes();
  }, []);

  const applyFiltersAndSort = () => {
    let filtered = [...initialPerfumes];

    // Search by name or brand
    if (searchTerm) {
      filtered = filtered.filter(
        p =>
          p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          p.brand.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by brands
    if (selectedBrands.length > 0) {
      filtered = filtered.filter(p => selectedBrands.includes(p.brand));
    }

    // Filter by categories (gender)
    if (selectedCategories.length > 0) {
      filtered = filtered.filter(p => selectedCategories.includes(p.gender));
    }

    // Filter by scents
    if (selectedScents.length > 0) {
      filtered = filtered.filter(p =>
        p.scents.some(scent => selectedScents.includes(scent))
      );
    }

    // Apply sorting
    filtered = applySorting(filtered, sortOption);

    // Update perfumes with pagination
    const startIndex = (currentPage - 1) * perfumesPerPage;
    const endIndex = startIndex + perfumesPerPage;
    setPerfumes(filtered.slice(startIndex, endIndex));
    setTotalPages(Math.ceil(filtered.length / perfumesPerPage));
  };

  const applySorting = (list, option) => {
    return [...list].sort((a, b) => {
      switch (option) {
        case 'name-asc':
          return a.name.localeCompare(b.name);
        case 'name-desc':
          return b.name.localeCompare(a.name);
        case 'price-asc':
          return a.price - b.price;
        case 'price-desc':
          return b.price - a.price;
        default:
          return 0;
      }
    });
  };

  const handlePageChange = page => {
    setCurrentPage(page);
    applyFiltersAndSort();
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleFilterChange = (setter, value, prev) => {
    setter(
      prev.includes(value)
        ? prev.filter(item => item !== value)
        : [...prev, value]
    );
  };

  const toggleSortDropdown = () => setIsSortDropdownOpen(!isSortDropdownOpen);

  const handleSortSelection = option => {
    setSortOption(option);
    setIsSortDropdownOpen(false);
    setCurrentPage(1);
    applyFiltersAndSort();
  };

  return (
    <div className="search-page container-fluid">
      <div className="row">
        {/* Left Sidebar Filters */}
        <div className="col-md-3 col-lg-2 filter-sidebar">
          <div className="search-container">
            <h2 className="search-title">Keresés</h2>
            <input
              type="text"
              className="form-control mb-3"
              placeholder="Keresés név vagy márka szerint..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && applyFiltersAndSort()}
            />

            {/* Brands Filter */}
            <div className="filter-section">
              <h5>Márkák</h5>
              {brands.slice(0, showMoreBrands ? brands.length : 5).map(brand => (
                <div className="form-check" key={brand}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    value={brand}
                    id={`brand-${brand}`}
                    checked={selectedBrands.includes(brand)}
                    onChange={() =>
                      handleFilterChange(setSelectedBrands, brand, selectedBrands)
                    }
                  />
                  <label className="form-check-label" htmlFor={`brand-${brand}`}>
                    {brand}
                  </label>
                </div>
              ))}
              {brands.length > 5 && (
                <button
                  className="btn btn-outline-peach mt-2"
                  onClick={() => setShowMoreBrands(!showMoreBrands)}
                >
                  {showMoreBrands ? 'Kevesebb' : 'Több'}
                </button>
              )}
            </div>

            {/* Categories Filter */}
            <div className="filter-section">
              <h5>Kategóriák</h5>
              {categories.map(category => (
                <div className="form-check" key={category}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    value={category}
                    id={`cat-${category}`}
                    checked={selectedCategories.includes(category)}
                    onChange={() =>
                      handleFilterChange(
                        setSelectedCategories,
                        category,
                        selectedCategories
                      )
                    }
                  />
                  <label className="form-check-label" htmlFor={`cat-${category}`}>
                    {category === 'male' ? 'Férfi' : category === 'female' ? 'Női' : 'Unisex'}
                  </label>
                </div>
              ))}
            </div>

            {/* Scents Filter */}
            <div className="filter-section">
              <h5>Illat típus</h5>
              {scents.slice(0, showMoreScents ? scents.length : 5).map(scent => (
                <div className="form-check" key={scent}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    value={scent}
                    id={`scent-${scent}`}
                    checked={selectedScents.includes(scent)}
                    onChange={() =>
                      handleFilterChange(setSelectedScents, scent, selectedScents)
                    }
                  />
                  <label className="form-check-label" htmlFor={`scent-${scent}`}>
                    {scent}
                  </label>
                </div>
              ))}
              {scents.length > 5 && (
                <button
                  className="btn btn-outline-peach mt-2"
                  onClick={() => setShowMoreScents(!showMoreScents)}
                >
                  {showMoreScents ? 'Kevesebb' : 'Több'}
                </button>
              )}
            </div>

            {/* Sorting */}
            <div className="sort-filter">
              <h5>Rendezés</h5>
              <button className="sort-toggle" onClick={toggleSortDropdown}>
                {sortOption === 'name-asc'
                  ? 'Név A-Z'
                  : sortOption === 'name-desc'
                  ? 'Név Z-A'
                  : sortOption === 'price-asc'
                  ? 'Ár növekvő'
                  : 'Ár csökkenő'}
                <i className="fas fa-caret-down"></i>
              </button>
              {isSortDropdownOpen && (
                <div className="sort-dropdown-menu">
                  <button
                    className="sort-dropdown-item"
                    onClick={() => handleSortSelection('name-asc')}
                  >
                    Név A-Z
                  </button>
                  <button
                    className="sort-dropdown-item"
                    onClick={() => handleSortSelection('name-desc')}
                  >
                    Név Z-A
                  </button>
                  <button
                    className="sort-dropdown-item"
                    onClick={() => handleSortSelection('price-asc')}
                  >
                    Ár növekvő
                  </button>
                  <button
                    className="sort-dropdown-item"
                    onClick={() => handleSortSelection('price-desc')}
                  >
                    Ár csökkenő
                  </button>
                </div>
              )}
            </div>

            <button className="btn btn-peach mt-3" onClick={applyFiltersAndSort}>
              Szűrés
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="col-md-9 col-lg-10">
          <h1 className="search-title mb-4">Katalógus</h1>
          {error && <div className="alert alert-danger">{error}</div>}
          <div className="row" id="perfumeList">
            {perfumes.length === 0 && !error ? (
              <div id="noResults" className="text-center w-100">
                <i className="fas fa-search fa-2x mb-3"></i>
                <h4>Nincs találat</h4>
                <p>Próbálj meg más szűrőfeltételeket!</p>
              </div>
            ) : (
              perfumes.map(p => <PerfumeCard key={p.id} perfume={p} />)
            )}
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <nav className="pagination mt-4">
              <ul className="pagination justify-content-center">
                <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                  <button
                    className="page-link"
                    onClick={() => handlePageChange(currentPage - 1)}
                  >
                    Előző
                  </button>
                </li>
                {Array.from({ length: totalPages }, (_, i) => i + 1)
                  .slice(
                    Math.max(0, currentPage - 3),
                    Math.min(totalPages, currentPage + 2)
                  )
                  .map(page => (
                    <li
                      className={`page-item ${currentPage === page ? 'active' : ''}`}
                      key={page}
                    >
                      <button
                        className="page-link"
                        onClick={() => handlePageChange(page)}
                      >
                        {page}
                      </button>
                    </li>
                  ))}
                <li
                  className={`page-item ${
                    currentPage === totalPages ? 'disabled' : ''
                  }`}
                >
                  <button
                    className="page-link"
                    onClick={() => handlePageChange(currentPage + 1)}
                  >
                    Következő
                  </button>
                </li>
              </ul>
            </nav>
          )}
        </div>
      </div>
    </div>
  );
};

export default Search;
