// frontend/src/components/PerfumeCard.js
import React from 'react';

const PerfumeCard = ({ perfume }) => {
  return (
    <div className="col-md-4 mb-4">
      <div className="card perfume-card">
        <img src={perfume.image_url} className="card-img-top" alt={perfume.name} />
        <div className="card-body">
          <h5 className="card-title">{perfume.name}</h5>
          <p className="card-text">{perfume.brand}</p>
          <p className="card-text">√År: {perfume.price} Ft</p>
          <p className="card-text">Illatok: {perfume.scents.split(',').map((scent, index) => (
            <span key={index} className="scent-tag">{scent}</span>
          ))}</p>
        </div>
      </div>
    </div>
  );
};

export default PerfumeCard;
