import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearch = () => {
    if (searchTerm.trim()) {
      navigate(`/search?query=${encodeURIComponent(searchTerm)}`);
    } else {
      navigate('/search');
    }
    setSearchTerm(''); // Törli a keresőmezőt a keresés után
  };

  return (
    <header className="header bg-light p-3 shadow-sm">
      <div className="container d-flex justify-content-between align-items-center">
        <h1 className="logo">Parfümvilág</h1>
        <div className="search-bar d-flex">
          <input
            type="text"
            className="form-control me-2"
            placeholder="Keresés parfümökre..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          />
          <button className="btn btn-peach" onClick={handleSearch}>
            Keresés
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
