import React, { useState } from "react";

const ReviewForm = ({ perfumeId }) => {
  const [ratings, setRatings] = useState({
    sillage: 0,
    longevity: 0,
    value: 0,
    overall: 0,
  });
  const [comment, setComment] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleRatingChange = (category, value) => {
    setRatings({ ...ratings, [category]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    try {
      const response = await fetch("/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          perfumeId,
          ratings,
          comment,
          author: "Felhasználó", // Ezt lecserélheted valódi felhasználónévre, pl. bejelentkezés alapján
          date: new Date().toISOString(), // Dátum automatikusan generálva
        }),
      });

      if (!response.ok) {
        throw new Error("Hiba történt az értékelés beküldésekor!");
      }

      setSuccess("Értékelés sikeresen beküldve!");
      setRatings({ sillage: 0, longevity: 0, value: 0, overall: 0 });
      setComment("");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <form className="review-form-custom" onSubmit={handleSubmit}>
      {error && <div className="alert alert-danger">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}

      <div className="review-form-section">
        <label className="form-label">Illatélmény (Sillage):</label>
        <div className="rating-stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${ratings.sillage >= star ? "filled" : ""}`}
              onClick={() => handleRatingChange("sillage", star)}
            >
              ★
            </span>
          ))}
        </div>
      </div>

      <div className="review-form-section">
        <label className="form-label">Tartósság (Longevity):</label>
        <div className="rating-stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${ratings.longevity >= star ? "filled" : ""}`}
              onClick={() => handleRatingChange("longevity", star)}
            >
              ★
            </span>
          ))}
        </div>
      </div>

      <div className="review-form-section">
        <label className="form-label">Ár/Érték arány (Value):</label>
        <div className="rating-stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${ratings.value >= star ? "filled" : ""}`}
              onClick={() => handleRatingChange("value", star)}
            >
              ★
            </span>
          ))}
        </div>
      </div>

      <div className="review-form-section">
        <label className="form-label">Összbenyomás (Overall):</label>
        <div className="rating-stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${ratings.overall >= star ? "filled" : ""}`}
              onClick={() => handleRatingChange("overall", star)}
            >
              ★
            </span>
          ))}
        </div>
      </div>

      <div className="review-form-section">
        <label className="form-label">Megjegyzés:</label>
        <textarea
          className="form-control"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Írd ide a véleményed..."
          rows="3"
        />
      </div>

      <button type="submit" className="btn-submit">
        Értékelés beküldése
      </button>
    </form>
  );
};

export default ReviewForm;
