// frontend/src/components/PerfumeCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const PerfumeCard = ({ perfume }) => {
  const { id, name, brand, description, price, image_url, scents = [] } = perfume;

  return (
    <div className="col-lg-4 col-md-6 mb-4">
      <div className="card perfume-card">
        <img src={image_url} className="card-img-top" alt={name} />
        <div className="card-body">
          <h5 className="card-title">{name}</h5>
          <p className="card-subtitle mb-2 text-muted">{brand}</p>
          <p className="card-text">Kategória: {perfume.gender}</p>
          <p className="card-text">Illatok: {scents.join(', ')}</p>
          <p className="card-text">{description}</p>
          <p className="price-display">{new Intl.NumberFormat('hu-HU').format(price)} Ft</p>
          <Link to={`/parfume/${id}`} className="btn btn-primary">Részletek</Link>
        </div>
      </div>
    </div>
  );
};

export default PerfumeCard;
