import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllPerfumes } from '../services/perfumeService'; // Feltételezem, hogy ez létezik

const Sidebar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [allPerfumes, setAllPerfumes] = useState([]);
  const navigate = useNavigate();

  // Parfümök betöltése egyszer, a komponens mountolásakor
  useEffect(() => {
    const fetchPerfumes = async () => {
      try {
        const perfumesData = await getAllPerfumes();
        setAllPerfumes(perfumesData);
      } catch (error) {
        console.error('Hiba a parfümök betöltésekor:', error);
      }
    };
    fetchPerfumes();
  }, []);

  // Javaslatok frissítése beírás közben
  useEffect(() => {
    if (searchTerm.trim()) {
      const filtered = allPerfumes
        .filter((p) => p.name.toLowerCase().includes(searchTerm.toLowerCase()))
        .slice(0, 5); // Az első 5 találat
      setSuggestions(filtered);
    } else {
      setSuggestions([]);
    }
  }, [searchTerm, allPerfumes]);

  const handleSearch = () => {
    if (searchTerm.trim()) {
      navigate(`/search?query=${encodeURIComponent(searchTerm)}`);
    } else {
      navigate('/search');
    }
    setSuggestions([]); // Dropdown eltüntetése keresés után
  };

  return (
    <div className="sidebar bg-light p-3" style={{ width: '250px', height: '100vh', position: 'fixed' }}>
      <h2 className="sidebar-title">Keresés</h2>
      <div className="position-relative">
        <input
          type="text"
          className="form-control mb-3"
          placeholder="Keresés név szerint..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {suggestions.length > 0 && (
          <ul className="list-group position-absolute w-100" style={{ zIndex: 1000, maxHeight: '200px', overflowY: 'auto' }}>
            {suggestions.map((perfume) => (
              <li
                key={perfume.id}
                className="list-group-item list-group-item-action"
                onClick={() => {
                  setSearchTerm(perfume.name);
                  navigate(`/search?query=${encodeURIComponent(perfume.name)}`);
                  setSuggestions([]);
                }}
                style={{ cursor: 'pointer' }}
              >
                {perfume.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      <button className="btn btn-peach w-100" onClick={handleSearch}>
        Keresés
      </button>
    </div>
  );
};

export default Sidebar;
