import React, { useState, useEffect, useCallback, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getUser, updateUser } from "../services/userService";
import { AuthContext } from "../App";

const Profile = () => {
  // State from Context
  const {
    isLoggedIn,
    user: contextUser,
    login: contextLogin,
    logout: contextLogout,
  } = useContext(AuthContext);

  // Local State
  const [user, setUser] = useState(contextUser || null);
  const [loading, setLoading] = useState(!contextUser);
  const [updateLoading, setUpdateLoading] = useState(false);
  const [editing, setEditing] = useState(false);
  const [error, setError] = useState("");
  const [updateError, setUpdateError] = useState("");
  const [validationErrors, setValidationErrors] = useState({});
  const [updateSuccess, setUpdateSuccess] = useState("");

  // Form state
  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [profileImage, setProfileImage] = useState("");

  const navigate = useNavigate();

  // --- Function to Initialize Form State ---
  const initializeForm = useCallback((userData) => {
    if (!userData) return;
    setNewName(userData.name || "");
    setNewEmail(userData.email || "");
    setPhoneNumber(userData.phone || "");
    const currentImageUrl = userData.profile_picture_url || "";
    setImageUrl(currentImageUrl);
    setProfileImage(currentImageUrl); // Set display image state
    setNewPassword("");
    setValidationErrors({});
    setUpdateError(""); // Clear update specific errors on init/cancel
    setUpdateSuccess("");
  }, []);

  // --- Fetch User Data ---
  const fetchUser = useCallback(async () => {
    if (!isLoggedIn || loading) return; // Skip if not logged in or already loading

    console.log("[fetchUser] Fetching user data...");
    setLoading(true);
    setError("");
    try {
      const userData = await getUser();
      if (!userData) throw new Error("Nem érkeztek felhasználói adatok.");
      console.log("[fetchUser] Data received, updating state:", userData);
      setUser(userData);
      contextLogin(userData, localStorage.getItem("token")); // Update context
      initializeForm(userData); // Initialize form with fresh data
    } catch (err) {
      console.error("[fetchUser] Error:", err);
      setError(err.message || "Hiba történt a profil betöltése közben.");
      setUser(null);
      if (err.message.includes("hitelesít") || err.response?.status === 401) {
        contextLogout();
        navigate("/bejelentkezes");
      }
    } finally {
      setLoading(false);
      console.log("[fetchUser] Fetch finished.");
    }
  }, [
    isLoggedIn,
    loading,
    navigate,
    contextLogin,
    contextLogout,
    initializeForm,
  ]); // Added loading, initializeForm

  // --- Effect to Fetch or Initialize ---
  useEffect(() => {
    if (isLoggedIn) {
      if (contextUser && !user) {
        // If context has user but local state doesn't (e.g., after login)
        console.log("Effect: Initializing from contextUser");
        setUser(contextUser);
        initializeForm(contextUser);
        if (loading) setLoading(false);
      } else if (!contextUser && !loading) {
        // If no user anywhere and not loading, fetch
        console.log("Effect: No user, fetching...");
        fetchUser();
      } else if (contextUser && user && !editing) {
        // If user exists, ensure form is synced if not editing
        initializeForm(user);
      }
    } else {
      // Optional: Redirect if not logged in
      // navigate('/bejelentkezes');
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isLoggedIn, contextUser, user, loading, editing]); // Re-run if login status or user context changes

  // --- Form Validation ---
  const validateForm = useCallback(() => {
    // ... (ugyanaz a validációs logika) ...
    const errors = {};
    if (!newName.trim()) errors.name = "Név megadása kötelező!";
    if (!newEmail.trim()) {
      errors.email = "Email cím megadása kötelező!";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(newEmail)) {
      errors.email = "Érvénytelen email cím formátum!";
    }
    if (newPassword && newPassword.length < 6) {
      errors.password =
        "A jelszónak legalább 6 karakter hosszúnak kell lennie!";
    }
    if (
      phoneNumber &&
      phoneNumber.trim() &&
      !/^\+?[0-9\s-()]{7,}$/.test(phoneNumber)
    ) {
      errors.phoneNumber = "Érvénytelen telefonszám formátum!";
    }
    const trimmedImageUrl = imageUrl.trim();
    if (trimmedImageUrl && !/^https?:\/\/.+\..+/i.test(trimmedImageUrl)) {
      errors.imageUrl =
        "Érvénytelen URL formátum! (http:// vagy https:// kell)";
    }
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  }, [newName, newEmail, newPassword, phoneNumber, imageUrl]);

  // --- Handle Update (Simplified - No Re-fetch) ---
  const handleEdit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setUpdateLoading(true);
    setUpdateError("");
    setUpdateSuccess("");

    // Prepare data with correct backend field names
    const dataToUpdate = {
      name: newName.trim(),
      email: newEmail.trim(),
      phone: phoneNumber.trim() || null,
      profile_picture_url: imageUrl.trim() || null, // Use DB name
    };
    if (newPassword.trim()) {
      dataToUpdate.password = newPassword.trim();
    }

    try {
      console.log("[handleEdit] Attempting update with data:", dataToUpdate);
      // Assume updateUser service call is successful (backend log confirmed this)
      // We still need the call to actually persist the data
      const response = await updateUser(dataToUpdate);
      console.log("[handleEdit] Update API response:", response); // See what backend returns

      // --- CRITICAL: Update state IMMEDIATELY after successful API call ---
      // Create the updated user object based on the data we *sent*
      // (More reliable than waiting for backend response structure if it's inconsistent)
      const updatedUserObject = {
        ...user, // Start with previous user data
        name: dataToUpdate.name,
        email: dataToUpdate.email,
        phone: dataToUpdate.phone,
        profile_picture_url: dataToUpdate.profile_picture_url,
        // DO NOT include password here
      };

      console.log(
        "[handleEdit] Updating local state and context with:",
        updatedUserObject
      );
      setUser(updatedUserObject); // Update local state
      contextLogin(updatedUserObject, localStorage.getItem("token")); // Update context (and localStorage)
      setProfileImage(updatedUserObject.profile_picture_url || ""); // Update display image state

      setEditing(false); // Exit editing mode
      setNewPassword(""); // Clear password field
      setUpdateSuccess("Adatok sikeresen frissítve!");
      setTimeout(() => setUpdateSuccess(""), 4000);
    } catch (updateErr) {
      console.error("[handleEdit] Profile update process error:", updateErr);
      setUpdateError(
        updateErr.message || "Hiba történt az adatok mentése közben."
      );
    } finally {
      // --- ENSURE loading state is set to false ---
      console.log("[handleEdit] Update process finished.");
      setUpdateLoading(false); // STOP update loading indicator
    }
  };

  // --- Logout ---
  const handleLogout = () => {
    if (window.confirm("Biztosan ki szeretne lépni?")) {
      contextLogout();
      navigate("/");
    }
  };

  // --- Cancel Edit ---
  const handleCancelEdit = () => {
    setEditing(false);
    if (user) {
      // Reset form from current user state
      initializeForm(user);
    }
    // Clear errors relevant to editing
    setUpdateError("");
    setValidationErrors({});
    setUpdateSuccess("");
  };

  // --- Render Logic ---

  // Initial Loading State
  if (loading) {
    return;
  }

  // Error State (after initial load attempt)
  if (!user) {
    return;
  }

  // --- Main Profile Render ---
  return (
    <div className="container py-5">
      <div className="profile-card">
        <h1 className="profile-title mb-4">Profil</h1>
        {updateSuccess && (
          <div className="alert alert-success">{updateSuccess}</div>
        )}
        {updateError && <div className="alert alert-danger">{updateError}</div>}{" "}
        {/* Show update specific error */}
        <div className="profile-content">
          {/* Image Section */}
          <div className="profile-image-section">
            <img
              // Use profileImage state for the src attribute
              src={
                profileImage || "https://via.placeholder.com/150?text=Profil"
              }
              alt={`${user.name} profilképe`}
              className="profile-image"
              key={profileImage} // Add key to force re-render if URL changes but React doesn't detect it
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://via.placeholder.com/150?text=Hiba";
              }}
            />
            {editing && (
              <div className="mt-3">
                <label htmlFor="profileImageUrl" className="form-label">
                  Profilkép URL
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.imageUrl ? "is-invalid" : ""
                  }`}
                  id="profileImageUrl"
                  value={imageUrl}
                  onChange={(e) => {
                    const url = e.target.value;
                    setImageUrl(url); // Update input state
                    setProfileImage(url); // Update preview state
                  }}
                  placeholder="Kép URL-je (pl. https://...)"
                  aria-describedby="imageUrlError"
                  disabled={updateLoading}
                />
                {validationErrors.imageUrl && (
                  <div id="imageUrlError" className="invalid-feedback">
                    {validationErrors.imageUrl}
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Details / Form Section */}
          <h3 className="profile-subtitle mb-3">Felhasználói adatok</h3>
          {!editing ? (
            // --- Display Mode ---
            <div className="profile-details">
              {/* Display data directly from the 'user' state object */}
              <p>
                <strong>Név:</strong> {user.name}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Telefonszám:</strong> {user.phone || "Nincs megadva"}
              </p>
              <p>
                <strong>Regisztráció:</strong>{" "}
                {user.created_at
                  ? new Date(user.created_at).toLocaleDateString("hu-HU")
                  : "Ismeretlen"}
              </p>
              <div className="profile-actions mt-4">
                <button
                  className="btn btn-outline-primary"
                  onClick={() => setEditing(true)}
                  disabled={updateLoading}
                >
                  Adatok szerkesztése
                </button>
                <button
                  className="btn btn-outline-danger ms-2"
                  onClick={handleLogout}
                  disabled={updateLoading}
                >
                  Kilépés
                </button>
              </div>
            </div>
          ) : (
            // --- Editing Mode ---
            <form id="profileForm" onSubmit={handleEdit}>
              {/* Name */}
              <div className="mb-3">
                <label htmlFor="editName" className="form-label">
                  Név
                </label>
                <input
                  type="text"
                  className={`form-control ${
                    validationErrors.name ? "is-invalid" : ""
                  }`}
                  id="editName"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  required
                  disabled={updateLoading}
                  aria-describedby="nameError"
                />
                {validationErrors.name && (
                  <div id="nameError" className="invalid-feedback">
                    {validationErrors.name}
                  </div>
                )}
              </div>
              {/* Email */}
              <div className="mb-3">
                <label htmlFor="editEmail" className="form-label">
                  Email cím
                </label>
                <input
                  type="email"
                  className={`form-control ${
                    validationErrors.email ? "is-invalid" : ""
                  }`}
                  id="editEmail"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  required
                  disabled={updateLoading}
                  aria-describedby="emailError"
                />
                {validationErrors.email && (
                  <div id="emailError" className="invalid-feedback">
                    {validationErrors.email}
                  </div>
                )}
              </div>
              {/* Phone */}
              <div className="mb-3">
                <label htmlFor="editPhoneNumber" className="form-label">
                  Telefonszám (opcionális)
                </label>
                <input
                  type="tel"
                  className={`form-control ${
                    validationErrors.phoneNumber ? "is-invalid" : ""
                  }`}
                  id="editPhoneNumber"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="Pl. +36 30 123 4567"
                  disabled={updateLoading}
                  aria-describedby="phoneNumberError"
                />
                {validationErrors.phoneNumber && (
                  <div id="phoneNumberError" className="invalid-feedback">
                    {validationErrors.phoneNumber}
                  </div>
                )}
              </div>
              {/* Password */}
              <div className="mb-3">
                <label htmlFor="editPassword" className="form-label">
                  Új jelszó (üresen hagyva nem változik)
                </label>
                <input
                  type="password"
                  className={`form-control ${
                    validationErrors.password ? "is-invalid" : ""
                  }`}
                  id="editPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  placeholder="Min. 6 karakter"
                  disabled={updateLoading}
                  aria-describedby="passwordError"
                />
                {validationErrors.password && (
                  <div id="passwordError" className="invalid-feedback">
                    {validationErrors.password}
                  </div>
                )}
              </div>
              {/* Action Buttons */}
              <div className="profile-actions mt-4">
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={updateLoading}
                >
                  {updateLoading ? (
                    <>
                      <span
                        className="spinner-border spinner-border-sm me-2"
                        role="status"
                        aria-hidden="true"
                      ></span>
                      Mentés...
                    </>
                  ) : (
                    "Mentés"
                  )}
                </button>
                <button
                  type="button"
                  className="btn btn-outline-secondary ms-2"
                  onClick={handleCancelEdit}
                  disabled={updateLoading}
                >
                  Mégse
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
