import React, { useState, useEffect, useContext } from "react"; // Import useContext
import { Link, NavLink, useNavigate } from "react-router-dom"; // Import useNavigate
import { AuthContext } from "../App"; // Import AuthContext
import "../style.css";

const Navbar = () => {
  const { isLoggedIn, user, logout } = useContext(AuthContext); // Use context state and logout function
  const [isOpen, setIsOpen] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const navigate = useNavigate(); // Use navigate for redirection after logout

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleLogout = () => {
    if (window.confirm("Biztosan ki szeretne lépni?")) {
      logout(); // Call the logout function from context
      navigate("/"); // Redirect to home page
      if (windowWidth <= 991) {
        // Close menu on mobile after logout
        setIsOpen(false);
      }
    }
  };

  const toggleMenu = () => setIsOpen(!isOpen);
  const closeMenu = () => setIsOpen(false); // Helper to close menu on link click

  return (
    <nav className="navbar navbar-dark">
      <div className="container">
        <div className="navbar-content">
          {windowWidth <= 991 && (
            <button className="navbar-toggler" onClick={toggleMenu}>
              <i className="fas fa-bars"></i>
            </button>
          )}
          <Link className="navbar-brand" to="/" onClick={closeMenu}>
            {" "}
            {/* Close menu on brand click */}
            Parfümvilág
          </Link>
          {/* Always render nav-links div for correct structure, control display with class */}
          <div
            className={`nav-links ${
              isOpen || windowWidth > 991 ? "active" : ""
            }`}
          >
            <ul className="navbar-nav navbar-left">
              <li className="nav-item">
                <NavLink
                  className={({ isActive }) =>
                    `nav-link ${isActive ? "active" : ""}`
                  }
                  to="/katalogus"
                  onClick={closeMenu} // Close menu on link click
                >
                  Hírek
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
                  className={({ isActive }) =>
                    `nav-link ${isActive ? "active" : ""}`
                  }
                  to="/kereses"
                  onClick={closeMenu} // Close menu on link click
                >
                  Keresés
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
                  className={({ isActive }) =>
                    `nav-link ${isActive ? "active" : ""}`
                  }
                  to="/rolunk"
                  onClick={closeMenu} // Close menu on link click
                >
                  Rólunk
                </NavLink>
              </li>
              {isLoggedIn && (
                <li className="nav-item">
                  <NavLink
                    className={({ isActive }) =>
                      `nav-link ${isActive ? "active" : ""}`
                    }
                    to="/kedvencek"
                    onClick={closeMenu} // Close menu on link click
                  >
                    Kedvencek
                  </NavLink>
                </li>
              )}
            </ul>
            <ul className="navbar-nav navbar-right">
              {isLoggedIn && user ? (
                <>
                  <li className="nav-item">
                    <NavLink
                      className={({ isActive }) =>
                        `nav-link profile-link ${isActive ? "active" : ""}`
                      }
                      to="/profil"
                      onClick={closeMenu} // Close menu on link click
                    >
                      <i className="fas fa-user-circle"></i>{" "}
                      {user?.name || "Profil"}{" "}
                      {/* Use user name from context */}
                    </NavLink>
                  </li>
                  <li className="nav-item">
                    <button
                      className="btn btn-outline-light logout-btn"
                      onClick={handleLogout} // Use the updated handler
                    >
                      Kilépés
                    </button>
                  </li>
                </>
              ) : (
                <>
                  <li className="nav-item">
                    <NavLink
                      className={({ isActive }) =>
                        `nav-link ${isActive ? "active" : ""}`
                      }
                      to="/bejelentkezes"
                      onClick={closeMenu} // Close menu on link click
                    >
                      Bejelentkezés
                    </NavLink>
                  </li>
                  <li className="nav-item">
                    <NavLink
                      className={({ isActive }) =>
                        `nav-link ${isActive ? "active" : ""}`
                      }
                      to="/regisztracio"
                      onClick={closeMenu} // Close menu on link click
                    >
                      Regisztráció
                    </NavLink>
                  </li>
                </>
              )}
            </ul>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
