// --- START OF FILE PerfumeDetail.js ---
// Importok maradnak, de add hozzá az új modalt
import EditReviewModal from '../components/EditReviewModal'; // <-- Új import
// ... többi import

const PerfumeDetail = () => {
  // ... state-ek (perfume, reviews, isFavorite, stb.)

  // MOST EZT HASZNÁLJUK A MODAL KEZELÉSÉRE:
  const [reviewToEditInModal, setReviewToEditInModal] = useState(null); // null | reviewObject

  // Távolítsd el vagy kommenteld ki az 'editingReview' state-et, ha volt ilyen
  // const [editingReview, setEditingReview] = useState(null); // <-- Ezt töröld/kommenteld

  // ... többi state (loading, error, message, stb.)

  // --- Handlerek ---

  // Ezt hívja meg a ReviewItem "Szerkesztés" gombja
  const handleOpenEditModal = useCallback((review) => {
    console.log("Opening edit modal for review:", review);
    setReviewToEditInModal(review);
  }, []);

  // Ezt hívja meg az EditReviewModal "Mégse" gombja vagy az overlay kattintás
  const handleCloseEditModal = useCallback(() => {
    console.log("Closing edit modal");
    setReviewToEditInModal(null);
  }, []);

  // Ezt hívja meg az EditReviewModal sikeres mentés után
  const handleReviewUpdateFromModal = useCallback((updatedReviewData) => {
    console.log("Received updated review from modal:", updatedReviewData);
    setReviews(prevReviews =>
      prevReviews.map(review =>
        review.id === updatedReviewData.id ? updatedReviewData : review
      )
    );
    handleCloseEditModal(); // Bezárja a modalt
    setMessage("Értékelés sikeresen frissítve!");
    setTimeout(() => setMessage(""), 3000);
  }, [handleCloseEditModal]); // Függ a handleCloseEditModal-tól

  // A handleDeleteReview, handleToggleFavorite, stb. változatlanok maradnak...
  const handleReviewSubmitted = useCallback((newReview) => {
    console.log("PerfumeDetail: handleReviewSubmitted called with:", newReview);
    setReviews((prevReviews) => [newReview, ...prevReviews]);
    // setEditingReview(null); // <-- Ezt töröld/kommenteld
    setMessage("Értékelés sikeresen elküldve!");
    setTimeout(() => setMessage(""), 3000);
  }, []);

    const handleDeleteReview = useCallback(async (reviewId) => {
        console.log("PerfumeDetail: handleDeleteReview called for ID:", reviewId);
        if (!window.confirm("Biztosan törölni szeretnéd ezt az értékelést?")) {
          return;
        }
        setMessage("");
        try {
          await deleteReviewService(reviewId);
          setReviews((prevReviews) => prevReviews.filter((r) => r.id !== reviewId));
          setMessage("Értékelés sikeresen törölve.");
          setTimeout(() => setMessage(""), 3000);
        } catch (error) {
          console.error("Hiba az értékelés törlésekor:", error);
          setMessage(`Hiba történt a törléskor: ${error.message || "Nem sikerült törölni."}`);
          setTimeout(() => setMessage(""), 5000);
        }
  }, []); // Dependency array might need `setMessage` if it changes, but usually stable

   // ... többi handler (useCallback-be csomagolva ideális esetben) ...
   const handleToggleFavorite = useCallback(async () => { /* ... */ }, [isLoggedIn, isFavorite, loadingFavoriteToggle, perfume]);
   const handleImageZoom = useCallback(() => setIsImageZoomed(true), []);
   const handleImageClose = useCallback(() => setIsImageZoomed(false), []);
   const handleCloseLoginPrompt = useCallback(() => setShowLoginPrompt(false), []);
   const handleLoginRedirect = useCallback(() => navigate("/bejelentkezes"), [navigate]);


  // ... useEffect-ek (fetchPerfume, fetchReviews, checkFavoriteStatus) változatlanok ...
  // --- Fetch Perfume Data ---
  useEffect(() => {
    const currentId = parseInt(id, 10);
    if (isNaN(currentId)) { /* ... error handling ... */ return; }
    let isMounted = true;
    const fetchPerfumeData = async () => { /* ... fetch logic ... */ };
    fetchPerfumeData();
    return () => { isMounted = false; };
  }, [id]);

  // --- Fetch Reviews ---
  const fetchReviews = useCallback(async () => { /* ... fetch logic ... */ }, [id]);
  useEffect(() => { fetchReviews(); }, [fetchReviews]);

  // --- Fetch Initial Favorite Status ---
  useEffect(() => { /* ... check status logic ... */ }, [isLoggedIn, perfume]);


  // ... formattedPrice változatlan ...
  const formattedPrice = () => { /* ... price formatting ... */ };

  // --- Render Logic ---
  return (
    <div className="perfume-detail-container">
      <div className="container">
        {/* ... Back Button, Perfume Loading/Error ... */}

        {perfume && !loadingPerfume && (
          <>
            {/* ... Perfume Header, Image, Info, Stores ... */}

            {/* --- Reviews Section --- */}
            <div className="reviews-section mt-4" id="review-form-section">
              <h2 className="reviews-title">Értékelések</h2>

              {/* Review Form (CSAK ÚJ értékeléshez) */}
              {isLoggedIn && (
                <ReviewForm
                  perfumeId={perfume.id}
                  key={'new-review-form'} // Fix kulcs az új formnak
                  onReviewSubmitted={handleReviewSubmitted}
                  // Itt már NEM kellenek: initialData, onReviewUpdated, onCancelEdit
                />
              )}
              {!isLoggedIn && (
                 <p className="login-required-message">
                    Bejelentkezés szükséges az értékelés írásához.
                    <button className="btn btn-link" onClick={() => setShowLoginPrompt(true)}>
                        Bejelentkezés
                    </button>
                 </p>
              )}


              {/* Review List */}
              {reviewError && !loadingReviews && <div className="alert alert-warning">{reviewError}</div>}
              <ReviewList
                reviews={reviews}
                loading={loadingReviews}
                currentUserId={user?.id}
                onEdit={handleOpenEditModal} // <-- Módosított prop: Modalt nyit
                onDelete={handleDeleteReview}
              />
            </div>
          </>
        )}

         {/* --- Edit Review Modal (feltételes renderelés) --- */}
         {reviewToEditInModal && (
            <EditReviewModal
                // Key prop helps reset modal state when different review is chosen
                key={reviewToEditInModal.id}
                reviewData={reviewToEditInModal}
                onUpdate={handleReviewUpdateFromModal} // Handler for successful update
                onClose={handleCloseEditModal}       // Handler to close modal
            />
         )}


        {/* ... Message Prompt, Login Prompt ... */}
         {message && (
          <div className="message-prompt">
            <p>{message}</p>
            <button onClick={() => setMessage("")}>Bezár</button>
          </div>
        )}
        {showLoginPrompt && (
          <LoginPromptModal
            onClose={handleCloseLoginPrompt}
            onLoginRedirect={handleLoginRedirect}
          />
        )}

      </div> {/* End Container */}
    </div> // End Root Div
  );
};

export default PerfumeDetail;
// --- END OF FILE PerfumeDetail.js ---
