// Parfumvilag_2.0\frontend\src\pages\Home.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import PerfumeCard from '../components/PerfumeCard';
import axios from 'axios';
import '../style.css';

const Home = () => {
  const [featuredPerfumes, setFeaturedPerfumes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFeaturedPerfumes = async () => {
      try {
        // Lekérdezzük az összes parfümöt az API-ból
        const response = await axios.get('/api/perfumes');
        const allPerfumes = response.data;

        // Véletlenszerűen kiválasztunk 3 parfümöt
        const shuffleArray = (array) => {
          const shuffled = [...array];
          for (let i = shuffled.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
          }
          return shuffled.slice(0, 3); // Az első 3 elemet vesszük
        };

        const randomPerfumes = shuffleArray(allPerfumes);
        setFeaturedPerfumes(randomPerfumes);
      } catch (err) {
        console.error('Error fetching perfumes:', err);
        setError('Nem sikerült betölteni a parfümöket.');
        setFeaturedPerfumes([]);
      }
    };

    fetchFeaturedPerfumes();
  }, []);

  return (
    <div className="home-wrapper">
      <section className="hero-section">
        <div className="hero-content">
          <h1>Üdvözöljük a Parfümvilágban</h1>
          <p>
            Találd meg álmaid parfümjét ajánlásainkkal és árösszehasonlításainkkal – egyszerűen, gyorsan, stílusosan!
          </p>
          <div className="hero-actions">
            <Link to="/kereses" className="btn btn-peach hero-btn">Keresés indítása</Link>
            <Link to="/katalogus" className="btn btn-outline-peach ms-3 hero-btn">Hírek felfedezése</Link>
          </div>
        </div>
      </section>

      <section className="featured-section container my-5">
        <h2 className="section-title text-center mb-4">Ajánlott Parfümjeink</h2>
        {error && <div className="alert alert-danger text-center">{error}</div>}
        <div className="row g-3" id="perfumeList">
          {featuredPerfumes.length > 0 ? (
            featuredPerfumes.map((perfume) => (
              <div key={perfume.id} className="col-lg-4 col-md-6 col-12">
                <PerfumeCard
                  perfume={{
                    id: perfumes.id,
                    name: perfumes.name,
                    brandId: perfumes.brand_id, // Ha a PerfumeCard használja
                    gender: perfumes.gender,
                    type: perfumes.type,
                    description: perfumes.description,
                    imageUrl: perfumes.image_url // Frissítve az image_url mezőre
                  }}
                />
              </div>
            ))
          ) : (
            <div className="col-12 text-center">
              <div id="noResults">
                <i className="fas fa-search fa-2x mb-3"></i>
                <h4>Nincs találat</h4>
                <p>Próbálja meg később újra!</p>
              </div>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Home;
