// backend/routes/userRoutes.js
const express = require('express');
const router = express.Router();
const db = require('../db');
const jwt = require('jsonwebtoken');
const bcrypt = require('bcryptjs');
const path = require('path');
const fs = require('fs');

// Middleware az autentikációhoz
const authenticateToken = (req, res, next) => {
  const token = req.headers['authorization']?.split(' ')[1];
  if (!token) return res.status(401).json({ error: 'Hozzáférés megtagadva' });

  jwt.verify(token, 'your_jwt_secret', (err, user) => {
    if (err) return res.status(403).json({ error: 'Érvénytelen token' });
    req.user = user;
    next();
  });
};

// Felhasználó adatainak lekérése
router.get('/', authenticateToken, (req, res) => {
  const userId = req.user.id;
  const sql = 'SELECT * FROM users WHERE id = ?';
  db.query(sql, [userId], (err, results) => {
    if (err) {
      console.error('Hiba a felhasználó lekérdezésekor:', err);
      return res.status(500).json({ error: 'Szerver hiba' });
    }
    if (results.length === 0) {
      return res.status(404).json({ error: 'Felhasználó nem található' });
    }
    res.json(results[0]);
  });
});

// Felhasználó adatainak frissítése
router.put('/', authenticateToken, (req, res) => {
  const userId = req.user.id;
  const { name, email, password, phoneNumber, profileImage } = req.body;
  let finalProfileImage = profileImage;

  // Ha fájlt töltöttek fel, mentsük a fájlrendszerbe
  if (req.files && req.files.profileImage) {
    const file = req.files.profileImage;
    if (!file.mimetype.startsWith('image/')) {
      return res.status(400).json({ error: 'Csak képfájlokat lehet feltölteni!' });
    }
    if (file.size > 5 * 1024 * 1024) {
      return res.status(400).json({ error: 'A kép mérete nem lehet nagyobb 5 MB-nál!' });
    }

    const uploadDir = path.join(__dirname, '../uploads');
    if (!fs.existsSync(uploadDir)) {
      fs.mkdirSync(uploadDir);
    }

    const fileName = `${Date.now()}-${file.name}`;
    const filePath = path.join(uploadDir, fileName);
    file.mv(filePath, (err) => {
      if (err) {
        console.error('Hiba a fájl mentésekor:', err);
        return res.status(500).json({ error: 'Hiba a fájl mentésekor' });
      }
      finalProfileImage = `/uploads/${fileName}`;
    });
  }

  let sql = 'UPDATE users SET name = ?, email = ?, phoneNumber = ?, profileImage = ?';
  const params = [name, email, phoneNumber || null, finalProfileImage || null];

  if (password) {
    const hashedPassword = bcrypt.hashSync(password, 8);
    sql += ', password = ?';
    params.push(hashedPassword);
  }

  sql += ', updated_at = NOW() WHERE id = ?';
  params.push(userId);

  db.query(sql, params, (err, result) => {
    if (err) {
      console.error('Hiba a felhasználó frissítésekor:', err);
      return res.status(500).json({ error: 'Szerver hiba' });
    }
    if (result.affectedRows === 0) {
      return res.status(404).json({ error: 'Felhasználó nem található' });
    }
    // Frissített felhasználó visszaadása
    db.query('SELECT * FROM users WHERE id = ?', [userId], (err, results) => {
      if (err) {
        console.error('Hiba a frissített felhasználó lekérdezésekor:', err);
        return res.status(500).json({ error: 'Szerver hiba' });
      }
      res.json(results[0]);
    });
  });
});

module.exports = router;
