import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getPerfumeById } from "../services/perfumeService";
import ReviewForm from "../components/ReviewForm";
import ReviewList from "../components/ReviewList";
import "../style.css";

const PerfumeDetail = () => {
  const { id } = useParams();
  const [perfume, setPerfume] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [isImageZoomed, setIsImageZoomed] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  // Parfüm adatainak betöltése
  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const perfumeData = await getPerfumeById(id);
        setPerfume(perfumeData);
        setError("");
      } catch (err) {
        setError("Nem sikerült betölteni a parfüm részleteit!");
      } finally {
        setLoading(false);
      }
    };

    fetchPerfume();
  }, [id]);

  // Kedvencek ellenőrzése az API-n keresztül
  useEffect(() => {
    const checkIfFavorite = async () => {
      if (!isLoggedIn) return;

      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setMessage('Hiányzó autentikációs token. Kérlek, jelentkezz be újra.');
          return;
        }

        const response = await fetch('/api/favorites', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error(`Hiba a kedvencek lekérdezésekor: ${response.status} - ${response.statusText}`);
        }

        const favorites = await response.json();
        setIsFavorite(favorites.some((fav) => fav.perfume_id === parseInt(id)));
      } catch (error) {
        console.error('Hiba a kedvencek ellenőrzésekor:', error);
        setMessage(`Nem sikerült ellenőrizni a kedvenceket: ${error.message}`);
      }
    };

    checkIfFavorite();
  }, [id, isLoggedIn]);

  // Kedvencekhez adás/törlés
  const toggleFavorite = async () => {
    if (!isLoggedIn) {
      setMessage('Be kell jelentkezni a kedvencek kezeléséhez.');
      setTimeout(() => navigate('/bejelentkezes'), 2000);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('user_id');

      if (!token || !userId) {
        setMessage('Hiányzó autentikációs adatok. Kérlek, jelentkezz be újra.');
        return;
      }

      if (isFavorite) {
        // Törlés a kedvencekből
        const response = await fetch(`/api/favorites/${id}`, {
          method: 'DELETE',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Hiba a kedvenc törlésekor: ${response.status} - ${errorText}`);
        }

        setMessage('Parfüm eltávolítva a kedvencekből.');
      } else {
        // Hozzáadás a kedvencekhez
        const response = await fetch('/api/favorites', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            user_id: userId,
            perfume_id: id,
          }),
        });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Hiba a kedvenc hozzáadásakor: ${response.status} - ${errorText}`);
        }

        setMessage('Parfüm hozzáadva a kedvencekhez.');
      }

      setIsFavorite(!isFavorite);
    } catch (error) {
      console.error('Hiba a kedvencek kezelésekor:', error);
      setMessage(`Hiba történt: ${error.message}`);
    }
  };

  const noteTags = (perfume?.notes || []).map((note, index) => (
    <span key={index} className="scent-tag me-2">
      {note.trim()}
    </span>
  ));

  return (
    <div className="perfume-detail-page">
      <div className="container mt-5">
        <button
          className="btn btn-secondary mb-4"
          onClick={() => window.history.back()}
        >
          Vissza a kereséshez
        </button>

        {loading && <div className="text-center">Betöltés...</div>}
        {error && <div className="alert alert-danger">{error}</div>}

        {perfume && (
          <>
            {/* Kép és leírás egymás mellett */}
            <div className="row mb-5">
              {/* Kép bal oldalon */}
              <div className="col-md-5 col-lg-4">
                {isImageZoomed ? (
                  <div className="zoomed-image-container">
                    <img
                      src={perfume.image_url || "https://via.placeholder.com/400"}
                      alt={perfume.name}
                      className="zoomed-image"
                    />
                    <button
                      className="close-zoom-btn"
                      onClick={() => setIsImageZoomed(false)}
                    >
                      ×
                    </button>
                  </div>
                ) : (
                  <img
                    src={perfume.image_url || "https://via.placeholder.com/400"}
                    alt={perfume.name}
                    className="img-fluid perfume-detail-image"
                    onClick={() => setIsImageZoomed(true)}
                  />
                )}
              </div>

              {/* Leírás jobb oldalon */}
              <div className="col-md-7 col-lg-8">
                <div className="perfume-detail-card">
                  <div className="d-flex align-items-center mb-3">
                    <h2 className="perfume-detail-title mb-0">{perfume.name}</h2>
                    <button
                      className={`favorite-btn ${isFavorite ? 'active' : ''} ms-3`}
                      onClick={(e) => {
                        e.preventDefault();
                        toggleFavorite();
                      }}
                    />
                  </div>
                  <p className="perfume-detail-text">
                    <strong>Márka:</strong> {perfume.brand}
                  </p>
                  <p className="perfume-detail-text">
                    <strong>Kategória:</strong>{" "}
                    {perfume.gender === "female" ? "Női" : perfume.gender === "male" ? "Férfi" : "Unisex"}
                  </p>
                  <p className="perfume-detail-text">
                    <strong>Illatok:</strong> {perfume?.notes && noteTags}
                  </p>
                  <p className="price-display">
                    {new Intl.NumberFormat("hu-HU").format(perfume.price)} Ft
                  </p>
                  <p className="perfume-detail-text">
                    <strong>Leírás:</strong> {perfume.description || "Nincs leírás"}
                  </p>
                </div>
              </div>
            </div>

            {/* Értékelések szekció */}
            <div className="row">
              <div className="col-12">
                <div className="perfume-detail-card">
                  <h3 className="perfume-detail-subtitle">Értékelések</h3>
                  <ReviewForm perfumeId={perfume?.id} className="review-form-custom" />
                  <ReviewList perfumeId={perfume?.id} className="review-list-custom" />
                </div>
              </div>
            </div>
          </>
        )}

        {/* Visszajelzés üzenet */}
        {message && (
          <div className="message-prompt">
            <p>{message}</p>
            <button onClick={() => setMessage('')}>Bezár</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default PerfumeDetail;
