import React from 'react';
import { Link } from 'react-router-dom';
import '../style.css';

const PerfumeCard = ({ perfume }) => {
  const { 
    id, 
    name, 
    brand, 
    description, 
    price, 
    image_url, 
    notes = [] // "notes" használata
  } = perfume;

  // "notes" mező kezelése
  const noteTags = notes.map((note, index) => (
    <span key={index} className="scent-tag me-2">
      {note.trim()}
    </span>
  ));

  return (
    <div className="col-lg-4 col-md-6 mb-4">
      <div className="card perfume-card">
        <img 
          src={image_url || 'https://via.placeholder.com/220'} 
          alt={name} 
          className="card-img-top" 
        />
        <div className="card-body">
          <h5 className="card-title">{name}</h5>
          <p className="card-subtitle mb-2 text-muted">{brand}</p>
          <div className="scent-tags">
            {noteTags} {/* "notes" használata */}
          </div>
          <p className="card-text">{description || 'Nincs leírás'}</p>
          <p className="price-display">
            {new Intl.NumberFormat('hu-HU').format(price)} Ft
          </p>
          <Link to={`/parfume/${id}`} className="btn btn-primary">
            Részletek
          </Link>
        </div>
      </div>
    </div>
  );
};

export default PerfumeCard;
