      // ...
      reviews.map((review) => (
          <ReviewItem
            key={review.id}
            review={review}
            currentUserId={currentUserId}
            onOpenEditModal={onOpenEditModal} // <-- Itt is átadjuk
            onDelete={onDelete}
          />
      ))
      // ...
  );
}
