import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import PerfumeCard from '../components/PerfumeCard';
import axios from 'axios';
import '../style.css';

const Home = () => {
  const [featuredPerfumes, setFeaturedPerfumes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFeaturedPerfumes = async () => {
      try {
        const response = await axios.get('/api/featured-perfumes');
        const perfumesData = await Promise.all(
          response.data.map(async (fp) => {
            const perfumeResponse = await axios.get(`/api/perfumes/${fp.perfume_id}`);
            return perfumeResponse.data;
          })
        );
        setFeaturedPerfumes(perfumesData);
      } catch (err) {
        console.error('Error fetching featured perfumes:', err);
        setError('Nem sikerült betölteni a kiemelt parfümöket.');
        setFeaturedPerfumes([]);
      }
    };
    fetchFeaturedPerfumes();
  }, []);

  // Updated carousel items for a perfume recommendation and price comparison site
  const carouselItems = [
    {
      src: 'https://images.unsplash.com/photo-1585203892280-cdcd274e6f89?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80',
      alt: 'Luxus parfüm ajánló',
      title: 'Fedezd fel a legjobb parfümöket!',
      text: 'Hasonlítsd össze az árakat és válaszd ki a tökéletes illatot ajánlásaink alapján.',
    },
    {
      src: 'https://www.bing.com/images/search?view=detailV2&ccid=PFOBcgq5&id=FDE5D51E1960B51A59547D7C41C4325439A8044C&thid=OIP.PFOBcgq50GHy7vfGXXv9eQHaEK&mediaurl=https%3a%2f%2fnlc.p3k.hu%2fuploads%2f2023%2f01%2fparfumok-a1920.jpg&cdnurl=https%3a%2f%2fth.bing.com%2fth%2fid%2fR.3c5381720ab9d061f2eef7c65d7bfd79%3frik%3dTASoOVQyxEF8fQ%26pid%3dImgRaw%26r%3d0&exph=1080&expw=1920&q=parfumok&simid=608051629319138890&FORM=IRPRST&ck=CCBBFBB576F7E641F84C025AE37B1803&selectedIndex=1&itb=0',
      alt: 'Prémium parfümök',
      title: 'Prémium illatok egy helyen',
      text: 'Ajánljuk a legnépszerűbb parfümöket, és megtalálhatod a legjobb árakat is.',
    },
    {
      src: 'https://images.unsplash.com/photo-1512496012209-82a3a2d62cd7?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80',
      alt: 'Egyedi parfümök',
      title: 'Egyedi illatok ajánlva neked',
      text: 'Szakértői ajánlások és árösszehasonlítás egyedi parfümökhöz – próbáld ki most!',
    },
  ];

  return (
    <div className="home-wrapper">
      {/* Slideshow (Carousel) */}
      <div id="homeCarousel" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          {carouselItems.map((item, index) => (
            <div key={index} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
              <img src={item.src} className="d-block w-100 carousel-img" alt={item.alt} />
              <div className="carousel-caption d-none d-md-block">
                <h3>{item.title}</h3>
                <p>{item.text}</p>
                <Link to="/kereses" className="btn btn-outline-primary carousel-btn">Kezdj el keresni</Link>
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#homeCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Előző</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#homeCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Következő</span>
        </button>
      </div>

      {/* Hero Section (Enhanced with Recommendation Focus) */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Üdvözöljük a Parfümvilágban</h1>
          <p>
            Találd meg álmaid parfümjét ajánlásainkkal és árösszehasonlításainkkal – egyedi illatok, professzionális tippek!
          </p>
          <div className="hero-actions">
            <Link to="/kereses" className="btn btn-primary hero-btn">Keresés indítása</Link>
            <Link to="/katalogus" className="btn btn-outline-primary ms-3 hero-btn">Hírek felfedezése</Link>
          </div>
        </div>
      </section>

      {/* Featured Section (Refined for Recommendations) */}
      <section className="featured-section container my-5">
        <h2 className="section-title text-center mb-5">Ajánlott Parfümjeink</h2>
        {error && <div className="alert alert-danger text-center">{error}</div>}
        <div className="row g-4" id="perfumeList">
          {featuredPerfumes.length > 0 ? (
            featuredPerfumes.map((perfume) => (
              <div key={perfume.id} className="col-lg-4 col-md-6 col-12">
                <PerfumeCard perfume={perfume} />
              </div>
            ))
          ) : (
            <div className="col-12 text-center">
              <div id="noResults">
                <i className="fas fa-search fa-3x mb-3"></i>
                <h4>Nincs találat</h4>
                <p>Próbálj meg később újra!</p>
              </div>
            </div>
          )}
        </div>
        <div className="text-center mt-5">
          <Link to="/kereses" className="btn btn-outline-primary explore-btn">További parfümök ajánlása</Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
