import React from 'react';
import { Link } from 'react-router-dom';
import '../style.css';

const PerfumeCard = ({ perfume }) => {
  const { 
    id, 
    name, 
    brand, 
    description, 
    price, 
    image_url, 
    scents // Ellenőrizd a mező nevét!
  } = perfume;

  // 1. Ellenőrzés a mező értékére
  const scentTags = (scents || '').split(',').map(s => s.trim());

  return (
    <div className="col-lg-4 col-md-6 mb-4">
      <div className="card perfume-card">
        <img 
          src={image_url || 'https://via.placeholder.com/220'} 
          alt={name} 
          className="card-img-top" 
        />
        <div className="card-body">
          <h5 className="card-title">{name}</h5>
          <h6 className="card-subtitle mb-2 text-muted">{brand}</h6>
          <div className="scent-tags">
            {scentTags.map((scent, index) => (
              <span key={index} className="scent-tag">{scent}</span>
            ))}
          </div>
          <p className="card-text">{description || 'Nincs leírás'}</p>
          <p className="price-display">{new Intl.NumberFormat('hu-HU').format(price)} Ft</p>
          <Link to={`/parfume/${id}`} className="btn btn-primary">Részletek</Link>
        </div>
      </div>
    </div>  
  );
};

export default PerfumeCard;
