import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [perfumes, setPerfumes] = useState([]);
  const [brands, setBrands] = useState([]);
  const [notes, setNotes] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [savedPerfumes, setSavedPerfumes] = useState([]);
  const [searchLogs, setSearchLogs] = useState([]);
  const [stores, setStores] = useState([]);
  const [terms, setTerms] = useState('');
  const [newTerms, setNewTerms] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/bejelentkezes');
      return;
    }

    const fetchData = async () => {
      setLoading(true);
      try {
        const config = {
          headers: { 'x-auth-token': token },
        };

        const [
          usersRes,
          perfumesRes,
          brandsRes,
          notesRes,
          reviewsRes,
          savedPerfumesRes,
          searchLogsRes,
          storesRes,
          termsRes,
        ] = await Promise.all([
          axios.get('http://localhost:5000/api/admin/users', config),
          axios.get('http://localhost:5000/api/admin/perfumes', config),
          axios.get('http://localhost:5000/api/admin/brands', config),
          axios.get('http://localhost:5000/api/admin/notes', config),
          axios.get('http://localhost:5000/api/admin/reviews', config),
          axios.get('http://localhost:5000/api/admin/saved-perfumes', config),
          axios.get('http://localhost:5000/api/admin/search-logs', config),
          axios.get('http://localhost:5000/api/admin/stores', config),
          axios.get('http://localhost:5000/api/admin/terms-and-conditions', config),
        ]);

        setUsers(usersRes.data);
        setPerfumes(perfumesRes.data);
        setBrands(brandsRes.data);
        setNotes(notesRes.data);
        setReviews(reviewsRes.data);
        setSavedPerfumes(savedPerfumesRes.data);
        setSearchLogs(searchLogsRes.data);
        setStores(storesRes.data);
        setTerms(termsRes.data.content);
        setNewTerms(termsRes.data.content);
      } catch (err) {
        setError('Hiba történt az adatok betöltése közben!');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Biztosan törölni szeretnéd ezt a felhasználót?')) {
      try {
        const config = {
          headers: { 'x-auth-token': localStorage.getItem('token') },
        };
        await axios.delete(`http://localhost:5000/api/admin/users/${userId}`, config);
        setUsers(users.filter(user => user.id !== userId));
        alert('Felhasználó sikeresen törölve!');
      } catch (err) {
        setError('Hiba történt a felhasználó törlése közben!');
      }
    }
  };

  const handleUpdateTerms = async () => {
    try {
      const config = {
        headers: { 'x-auth-token': localStorage.getItem('token') },
      };
      await axios.put('http://localhost:5000/api/admin/terms-and-conditions', { content: newTerms }, config);
      setTerms(newTerms);
      alert('ÁSZF sikeresen frissítve!');
    } catch (err) {
      setError('Hiba történt az ÁSZF frissítése közben!');
    }
  };

  if (loading) return <div>Betöltés...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="admin-dashboard container py-5">
      <h1 className="admin-title">Adminisztrátori Felület</h1>

      {/* Felhasználók kezelése */}
      <section className="admin-section">
        <h2>Felhasználók</h2>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Név</th>
              <th>Email</th>
              <th>Műveletek</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button className="btn btn-danger" onClick={() => handleDeleteUser(user.id)}>Törlés</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Parfümök kezelése */}
      <section className="admin-section">
        <h2>Parfümök</h2>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Név</th>
              <th>Márka ID</th>
            </tr>
          </thead>
          <tbody>
            {perfumes.map(perfume => (
              <tr key={perfume.id}>
                <td>{perfume.id}</td>
                <td>{perfume.name}</td>
                <td>{perfume.brand_id}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Márkák kezelése */}
      <section className="admin-section">
        <h2>Márkák</h2>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Név</th>
            </tr>
          </thead>
          <tbody>
            {brands.map(brand => (
              <tr key={brand.id}>
                <td>{brand.id}</td>
                <td>{brand.name}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Illatjegyek kezelése */}
      <section className="admin-section">
        <h2>Illatjegyek</h2>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Név</th>
              <th>Típus</th>
            </tr>
          </thead>
          <tbody>
            {notes.map(note => (
              <tr key={note.id}>
                <td>{note.id}</td>
                <td>{note.name}</td>
                <td>{note.type}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Értékelések kezelése */}
      <section className="admin-section">
        <h2>Értékelések</h2>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Parfüm ID</th>
              <th>Felhasználó ID</th>
              <th>Értékelés</th>
            </tr>
          </thead>
          <tbody>
            {reviews.map(review => (
              <tr key={review.id}>
                <td>{review.id}</td>
                <td>{review.perfume_id}</td>
                <td>{review.user_id}</td>
                <td>{review.overall_impression}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* ÁSZF kezelése */}
      <section className="admin-section">
        <h2>ÁSZF Szerkesztése</h2>
        <textarea
          className="form-control"
          rows="10"
          value={newTerms}
          onChange={(e) => setNewTerms(e.target.value)}
        />
        <button className="btn btn-peach mt-3" onClick={handleUpdateTerms}>ÁSZF Mentése</button>
      </section>
    </div>
  );
};

export default AdminDashboard;
