// src/pages/Search.js
import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import { getAllPerfumes } from "../services/perfumeService"; // Service import
import Sidebar from "../components/Sidebar";
import PerfumeCard from "../components/PerfumeCard";
import "../style.css"; // Győződj meg róla, hogy a stíluslap importálva van

// Helper for Pagination Buttons
const PageButton = ({ page, currentPage, onClick }) => (
  <li className={`page-item ${currentPage === page ? "active" : ""}`}>
    <button className="page-link" onClick={() => onClick(page)}>
      {page}
    </button>
  </li>
);

const Search = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [perfumes, setPerfumes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [totalPages, setTotalPages] = useState(1);
  // Az aktuális oldalt mindig a searchParams-ból olvassuk ki, vagy 1 ha nincs
  const currentPage = parseInt(searchParams.get("page") || "1", 10);
  const [searchTerm, setSearchTerm] = useState(searchParams.get("query") || ""); // Keresőmező state

  // Effect to fetch perfumes when searchParams change
  useEffect(() => {
    const fetchPerfumes = async () => {
      setLoading(true);
      setError("");
      try {
        // Extract all relevant parameters from the current URL search params
        const params = {
          query: searchParams.get("query") || "",
          brand: searchParams.get("brand") || "",
          note: searchParams.get("note") || "",
          gender: searchParams.get("gender") || "",
          sort: searchParams.get("sort") || "name-asc",
          min_price: searchParams.get("min_price"), // Pass null if not present
          max_price: searchParams.get("max_price"), // Pass null if not present
          page: String(currentPage), // Pass current page from state/URL
          per_page: 24, // Or make this configurable
        };

        console.log("Search.js: Fetching perfumes with params:", params);
        const response = await getAllPerfumes(params); // Call the service

        // Update state with the response
        setPerfumes(response.perfumes || []); // Ensure perfumes is always an array
        setTotalPages(response.totalPages || 1); // Ensure totalPages is at least 1
        // No need to setCurrentPage here, it's derived from searchParams
      } catch (err) {
        console.error("Search.js: Error fetching perfumes:", err);
        setError(err.message || "Hiba a parfümök betöltésekor.");
        setPerfumes([]); // Clear list on error
        setTotalPages(1);
        // setCurrentPage(1); // Reset page? Optional.
      } finally {
        setLoading(false);
      }
    };

    fetchPerfumes();
    // Dependency array includes searchParams to re-fetch when URL changes
  }, [searchParams, currentPage]); // Added currentPage for clarity, though it's derived from searchParams

  // Handler for the search input field change
  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handler for submitting the search term (e.g., pressing Enter or clicking button)
  const handleSearchSubmit = () => {
    const newParams = new URLSearchParams(searchParams);
    if (searchTerm.trim()) {
      newParams.set("query", searchTerm.trim());
    } else {
      newParams.delete("query");
    }
    newParams.set("page", "1"); // Reset to page 1 on new search
    setSearchParams(newParams);
  };

  // Handler for changing the page
  const handlePageChange = (page) => {
    // Ensure page is within valid range
    const newPage = Math.max(1, Math.min(page, totalPages));
    const newParams = new URLSearchParams(searchParams);
    newParams.set("page", String(newPage));
    setSearchParams(newParams); // Update URL, which triggers useEffect
    window.scrollTo({ top: 0, behavior: "smooth" }); // Scroll to top
  };

  // --- Pagination Logic ---
  const getPageNumbers = () => {
    const pages = [];
    const maxPagesToShow = 5; // Max number of page buttons to show
    const halfPages = Math.floor(maxPagesToShow / 2);
    let startPage, endPage;

    if (totalPages <= maxPagesToShow) {
      // Show all pages if total is less than or equal to max
      startPage = 1;
      endPage = totalPages;
    } else if (currentPage <= halfPages + 1) {
      // Near the beginning
      startPage = 1;
      endPage = maxPagesToShow;
    } else if (currentPage + halfPages >= totalPages) {
      // Near the end
      startPage = totalPages - maxPagesToShow + 1;
      endPage = totalPages;
    } else {
      // In the middle
      startPage = currentPage - halfPages;
      endPage = currentPage + halfPages;
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }
    return {
      pages,
      showStartEllipsis: startPage > 1,
      showEndEllipsis: endPage < totalPages,
    };
  };

  const {
    pages: pageNumbers,
    showStartEllipsis,
    showEndEllipsis,
  } = getPageNumbers();
  // --- End Pagination Logic ---

  // Determine main content margin based on sidebar visibility (driven by window width)
  // This assumes Sidebar component handles its own visibility correctly based on windowWidth
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);
  const mainContentMarginLeft = windowWidth > 991 ? "250px" : "0"; // Match sidebar width

  return (
    // Main container using flex display
    <div className="d-flex flex-wrap flex-lg-nowrap">
      {" "}
      {/* Flex wrap for mobile, nowrap for larger */}
      <Sidebar /> {/* Sidebar component */}
      {/* Main content area taking remaining space */}
      <div
        className="flex-grow-1"
        style={{
          marginLeft: mainContentMarginLeft,
          transition: "margin-left 0.3s ease-in-out",
          width: "100%",
        }} // Apply margin and transition
      >
        <div className="search-page container-fluid mt-4 px-lg-4 px-md-3 px-2">
          {" "}
          {/* Fluid container with padding */}
          {/* Search Input Row */}
          <div className="row justify-content-center mb-4">
            <div className="col-md-8 col-lg-6">
              <div className="input-group shadow-sm">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Parfüm neve..."
                  value={searchTerm}
                  onChange={handleSearchTermChange}
                  onKeyPress={(e) => e.key === "Enter" && handleSearchSubmit()}
                />
                <button
                  className="btn btn-primary"
                  onClick={handleSearchSubmit}
                >
                  <i className="fas fa-search"></i> Keresés
                </button>
              </div>
              {/* Suggestions could be added here if needed */}
            </div>
          </div>
          {/* Results Title (Optional) */}
          <h2 className="search-results-title h4 text-center text-muted mb-4">
            {searchParams.toString() ? "Szűrt eredmények" : "Minden Parfüm"}
          </h2>
          {/* Loading Indicator */}
          {loading && (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Betöltés...</span>
              </div>
            </div>
          )}
          {/* Error Message */}
          {error && !loading && (
            <div className="alert alert-danger text-center">{error}</div>
          )}
          {/* Perfume List Grid */}
          {/* Bootstrap row with gutter spacing */}
          <div className="row g-3" id="perfumeList">
            {!loading && !error && perfumes.length === 0 && (
              <div className="col-12">
                {" "}
                {/* Full width for no results */}
                <div
                  id="noResults"
                  className="text-center card p-4 border-0 shadow-sm"
                >
                  <i className="fas fa-box-open fa-3x mb-3 text-secondary"></i>
                  <h4>Nincs a szűrésnek megfelelő parfüm</h4>
                  <p className="text-muted mb-0">
                    Próbálj meg más keresési feltételeket megadni.
                  </p>
                </div>
              </div>
            )}
            {!loading &&
              !error &&
              perfumes.map((p) => (
                // Bootstrap columns for responsive layout + stretch alignment
                <div
                  key={p.id}
                  className="col-xl-3 col-lg-4 col-md-6 col-sm-6 col-12 d-flex align-items-stretch"
                >
                  <PerfumeCard perfume={p} /> {/* Pass perfume data */}
                </div>
              ))}
          </div>
          {/* Pagination */}
          {!loading && !error && totalPages > 1 && (
            <nav
              className="mt-5 d-flex justify-content-center"
              aria-label="Perfume pagination"
            >
              <ul className="pagination shadow-sm">
                {/* Previous Button */}
                <li
                  className={`page-item ${currentPage === 1 ? "disabled" : ""}`}
                >
                  <button
                    className="page-link"
                    onClick={() => handlePageChange(currentPage - 1)}
                    aria-label="Előző"
                  >
                    « {/* « */}
                  </button>
                </li>

                {/* First Page & Ellipsis */}
                {showStartEllipsis && (
                  <>
                    <PageButton
                      page={1}
                      currentPage={currentPage}
                      onClick={handlePageChange}
                    />
                    <li className="page-item disabled">
                      <span className="page-link">...</span>
                    </li>
                  </>
                )}

                {/* Page Numbers */}
                {pageNumbers.map((page) => (
                  <PageButton
                    key={page}
                    page={page}
                    currentPage={currentPage}
                    onClick={handlePageChange}
                  />
                ))}

                {/* End Ellipsis & Last Page */}
                {showEndEllipsis && (
                  <>
                    <li className="page-item disabled">
                      <span className="page-link">...</span>
                    </li>
                    <PageButton
                      page={totalPages}
                      currentPage={currentPage}
                      onClick={handlePageChange}
                    />
                  </>
                )}

                {/* Next Button */}
                <li
                  className={`page-item ${
                    currentPage === totalPages ? "disabled" : ""
                  }`}
                >
                  <button
                    className="page-link"
                    onClick={() => handlePageChange(currentPage + 1)}
                    aria-label="Következő"
                  >
                    » {/* » */}
                  </button>
                </li>
              </ul>
            </nav>
          )}
          {/* Pagination Info */}
          {!loading && !error && totalPages > 0 && (
            <div className="text-center text-muted mt-2 mb-4">
              {currentPage}. oldal / {totalPages} (Összesen{" "}
              {perfumes.length > 0
                ? totalPages * 24 - (24 - perfumes.length)
                : 0}{" "}
              találat) {/* Estimate total count */}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Search;
