// --- START OF FILE PerfumeDetail.js ---

// 1. Imports - MINDEN SZÜKSÉGES IMPORT ITT VAN
import React, { useState, useEffect, useContext, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";

// Service Imports
import { getPerfumeById } from "../services/perfumeService";
import {
  getMyFavoriteIds,
  addFavorite,
  removeFavorite,
} from "../services/savedPerfumeService";
import {
  getReviewsForPerfume,
  deleteReview as deleteReviewService,
  updateReview as updateReviewService
} from "../services/reviewService";

// Context Import
import { AuthContext } from "../App";

// Component Imports
import ReviewForm from "../components/ReviewForm"; // Az új értékeléshez
import ReviewList from "../components/ReviewList";
import EditReviewModal from '../components/EditReviewModal'; // A szerkesztő modalhoz
import LoginPromptModal from '../components/LoginPromptModal'; // A bejelentkezési modalhoz (ha külön fájlban van)

// CSS Import
import "../style.css";

// Login Prompt Modal Component (Ha NINCS külön fájlban)
// const LoginPromptModal = ({ onClose, onLoginRedirect }) => (
//    <>
//      <div className="login-prompt-overlay" onClick={onClose}></div>
//      <div className="login-prompt">
//        {/* ... modal content ... */}
//      </div>
//    </>
// );

const PerfumeDetail = () => {
  // 2. Hooks - useParams, useNavigate, useContext
  const { id } = useParams(); // Get ID from URL
  const navigate = useNavigate(); // For navigation
  const { isLoggedIn, user } = useContext(AuthContext); // Get auth state and user info

  // 3. State Variables - MINDEN STATE ITT VAN DEFINIÁLVA
  const [perfume, setPerfume] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [reviewToEditInModal, setReviewToEditInModal] = useState(null); // For the edit modal
  const [isFavorite, setIsFavorite] = useState(false);
  const [showLoginPrompt, setShowLoginPrompt] = useState(false);
  const [isImageZoomed, setIsImageZoomed] = useState(false);
  const [message, setMessage] = useState("");

  // Loading states
  const [loadingPerfume, setLoadingPerfume] = useState(true);
  const [loadingReviews, setLoadingReviews] = useState(true);
  const [loadingFavoriteCheck, setLoadingFavoriteCheck] = useState(false);
  const [loadingFavoriteToggle, setLoadingFavoriteToggle] = useState(false);
  // Consider adding loading state for delete/update if needed

  // Error states
  const [perfumeError, setPerfumeError] = useState("");
  const [reviewError, setReviewError] = useState("");

  // 4. Fetching Logic (useEffect hooks)

  // --- Fetch Perfume Data ---
  useEffect(() => {
    const currentId = parseInt(id, 10);
    if (isNaN(currentId)) {
      setPerfumeError("Érvénytelen parfüm azonosító.");
      setLoadingPerfume(false);
      setLoadingReviews(false); // Stop loading reviews too
      return;
    }

    let isMounted = true;
    const fetchPerfumeData = async () => {
      console.log(`PerfumeDetail: Fetching perfume data for ID: ${currentId}`);
      setLoadingPerfume(true);
      setPerfumeError("");
      setPerfume(null);
      try {
        const perfumeData = await getPerfumeById(currentId);
        if (isMounted) {
          console.log("PerfumeDetail: Perfume data received:", perfumeData);
          setPerfume(perfumeData);
        }
      } catch (err) {
        console.error("Hiba a parfüm betöltésekor:", err);
        if (isMounted) {
          setPerfumeError(err.message || "A parfüm részleteinek betöltése sikertelen.");
        }
      } finally {
        if (isMounted) {
          setLoadingPerfume(false);
        }
      }
    };

    fetchPerfumeData();
    return () => { isMounted = false; };
  }, [id]); // Dependency: id

  // --- Fetch Reviews ---
  const fetchReviews = useCallback(async () => {
    const currentId = parseInt(id, 10);
    if (isNaN(currentId)) return;

    console.log(`PerfumeDetail: Fetching reviews for ID: ${currentId}`);
    setLoadingReviews(true);
    setReviewError("");
    try {
      const data = await getReviewsForPerfume(currentId);
      console.log("PerfumeDetail: Reviews data received:", data);
      setReviews(data || []);
    } catch (err) {
      console.error("Hiba az értékelések betöltésekor:", err);
      setReviewError(err.message || "Hiba az értékelések betöltésekor.");
      setReviews([]);
    } finally {
      setLoadingReviews(false);
    }
  }, [id]); // Dependency: id

  useEffect(() => {
    fetchReviews();
  }, [fetchReviews]); // Dependency: fetchReviews (memoized)

  // --- Fetch Initial Favorite Status ---
  useEffect(() => {
    let isMounted = true;
    const checkFavoriteStatus = async () => {
      if (!isLoggedIn || !perfume || !perfume.id) return;

      setLoadingFavoriteCheck(true);
      try {
        const favoriteIds = await getMyFavoriteIds();
        if (isMounted) {
          setIsFavorite(favoriteIds.includes(parseInt(perfume.id, 10)));
        }
      } catch (error) {
        console.error("Hiba a kedvenc állapot ellenőrzésekor:", error);
      } finally {
        if (isMounted) {
          setLoadingFavoriteCheck(false);
        }
      }
    };
    checkFavoriteStatus();
    return () => { isMounted = false; };
  }, [isLoggedIn, perfume]); // Dependency: isLoggedIn, perfume

  // 5. Handlers (useCallback memoization)

  const handleToggleFavorite = useCallback(async () => {
    if (!isLoggedIn) { setShowLoginPrompt(true); return; }
    if (loadingFavoriteToggle || !perfume || !perfume.id) return;

    setLoadingFavoriteToggle(true);
    setMessage("");
    const perfumeIdInt = parseInt(perfume.id, 10);
    try {
      if (isFavorite) {
        await removeFavorite(perfumeIdInt);
        setIsFavorite(false);
        setMessage("Parfüm eltávolítva a kedvencekből.");
      } else {
        await addFavorite(perfumeIdInt);
        setIsFavorite(true);
        setMessage("Parfüm hozzáadva a kedvencekhez.");
      }
      setTimeout(() => setMessage(""), 3000);
    } catch (error) {
      console.error("Hiba a kedvencek kezelésekor:", error);
      setMessage(`Hiba történt: ${error.message || "Nem sikerült módosítani."}`);
      setTimeout(() => setMessage(""), 5000);
    } finally {
      setLoadingFavoriteToggle(false);
    }
  }, [isLoggedIn, isFavorite, loadingFavoriteToggle, perfume, setShowLoginPrompt, setMessage]); // Added missing dependencies

  const handleReviewSubmitted = useCallback((newReview) => {
    setReviews((prevReviews) => [newReview, ...prevReviews]);
    setMessage("Értékelés sikeresen elküldve!");
    setTimeout(() => setMessage(""), 3000);
  }, [setReviews, setMessage]); // Added dependencies

  // Opens the modal
  const handleOpenEditModal = useCallback((review) => {
    setReviewToEditInModal(review);
  }, [setReviewToEditInModal]); // Added dependency

  // Closes the modal
  const handleCloseEditModal = useCallback(() => {
    setReviewToEditInModal(null);
  }, [setReviewToEditInModal]); // Added dependency

  // Called by modal on successful update
  const handleReviewUpdateFromModal = useCallback((updatedReviewData) => {
    setReviews(prevReviews =>
      prevReviews.map(review =>
        review.id === updatedReviewData.id ? updatedReviewData : review
      )
    );
    handleCloseEditModal(); // Close modal after update
    setMessage("Értékelés sikeresen frissítve!");
    setTimeout(() => setMessage(""), 3000);
  }, [handleCloseEditModal, setReviews, setMessage]); // Added dependencies

  const handleDeleteReview = useCallback(async (reviewId) => {
    if (!window.confirm("Biztosan törölni szeretnéd ezt az értékelést?")) return;

    setMessage("");
    try {
      await deleteReviewService(reviewId);
      setReviews((prevReviews) => prevReviews.filter((r) => r.id !== reviewId));
      setMessage("Értékelés sikeresen törölve.");
      setTimeout(() => setMessage(""), 3000);
    } catch (error) {
      console.error("Hiba az értékelés törlésekor:", error);
      setMessage(`Hiba történt a törléskor: ${error.message || "Nem sikerült."}`);
      setTimeout(() => setMessage(""), 5000);
    }
  }, [setReviews, setMessage]); // Added dependencies

  // Other UI handlers
  const handleImageZoom = useCallback(() => setIsImageZoomed(true), [setIsImageZoomed]);
  const handleImageClose = useCallback(() => setIsImageZoomed(false), [setIsImageZoomed]);
  const handleCloseLoginPrompt = useCallback(() => setShowLoginPrompt(false), [setShowLoginPrompt]);
  const handleLoginRedirect = useCallback(() => navigate("/bejelentkezes"), [navigate]);

  // 6. Helper Functions
  const formattedPrice = () => {
    if (!perfume || !perfume.stores || perfume.stores.length === 0) return "Ár információ nem elérhető";
    const prices = perfume.stores
      .map((store) => Number(store.price))
      .filter((price) => !isNaN(price) && price > 0);
    if (prices.length === 0) return "Ár információ nem elérhető";
    const minPrice = Math.min(...prices);
    return `${new Intl.NumberFormat("hu-HU", { style: 'currency', currency: 'HUF', maximumFractionDigits: 0 }).format(minPrice)}-tól`;
  };

  // 7. Render Logic
  return (
    <div className="perfume-detail-container">
      <div className="container">
        <button className="back-btn mb-3" onClick={() => navigate(-1)}> ← Vissza </button>

        {loadingPerfume && <div className="text-center text-muted my-5">Parfüm adatok betöltése...</div>}
        {perfumeError && !loadingPerfume && <div className="alert alert-danger text-center">{perfumeError}</div>}

        {perfume && !loadingPerfume && (
          <>
            {/* Perfume Header */}
            <div className="perfume-title-wrapper">
              <h1 className="perfume-title-top">{perfume.name}</h1>
              <button
                className={`favorite-btn ${isFavorite ? "active" : ""} ${loadingFavoriteCheck || loadingFavoriteToggle ? "disabled" : ""}`}
                onClick={handleToggleFavorite}
                disabled={loadingFavoriteCheck || loadingFavoriteToggle}
                aria-label={isFavorite ? "Eltávolítás a kedvencekből" : "Hozzáadás a kedvencekhez"}
              />
            </div>

            {/* Perfume Content */}
            <div className="perfume-detail-content">
              {/* Image Section */}
              <div className="perfume-image-wrapper">
                 {isImageZoomed ? (
                   <div className="zoomed-image-container" onClick={handleImageClose}>
                     <img src={perfume.image_url || "..."} alt={perfume.name} className="zoomed-image" onClick={(e) => e.stopPropagation()} />
                     <button className="close-zoom-btn" onClick={handleImageClose}>×</button>
                   </div>
                 ) : (
                   <img src={perfume.image_url || "..."} alt={perfume.name} className="perfume-image" onClick={handleImageZoom} style={{ cursor: "zoom-in" }}/>
                 )}
               </div>

              {/* Info Section */}
               <div className="perfume-info">
                 <p className="perfume-brand"><strong>Márka:</strong> {perfume.brand_name || "Ismeretlen"}</p>
                 <p className="perfume-gender"><strong>Kategória:</strong> {perfume.gender === "female" ? "Női" : perfume.gender === "male" ? "Férfi" : "Unisex"}</p>
                 <div className="perfume-notes mb-3">
                   <strong>Illatjegyek:</strong>{" "}
                   {perfume.notes?.length > 0 ? perfume.notes.map((note, i) => <span key={i} className="scent-tag">{note}</span>) : <span className="text-muted">Nincsenek megadva</span>}
                 </div>
                 <p className={`perfume-price ${!perfume.stores?.length ? "text-muted" : ""}`}>{formattedPrice()}</p>
                 <p className="perfume-description"><strong>Leírás:</strong> {perfume.description || "Nincs leírás."}</p>
               </div>

              {/* Stores Section */}
              <div className="stores-section">
                <h2 className="stores-title">Elérhető webáruházak</h2>
                {perfume.stores?.length > 0 ? (
                  <div className="stores-list">
                    {perfume.stores.map((store, index) => (
                      <div key={index} className="store-item">
                        <span className="store-name">{store.store_name}</span>
                        <a href={store.url} target="_blank" rel="noopener noreferrer" className="store-link"> Megnézem → </a>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="no-stores text-muted">Jelenleg nem listázott egy webáruházban sem.</p>
                )}
              </div>

              {/* Reviews Section */}
              <div className="reviews-section mt-4" id="review-form-section">
                <h2 className="reviews-title">Értékelések</h2>

                {/* NEW Review Form */}
                {isLoggedIn && (
                  <ReviewForm
                    perfumeId={perfume.id}
                    key={'new-review-form'}
                    onReviewSubmitted={handleReviewSubmitted}
                  />
                )}
                {!isLoggedIn && (
                  <p className="login-required-message">
                    Bejelentkezés szükséges az értékelés írásához.
                    <button className="btn btn-link" onClick={() => setShowLoginPrompt(true)}> Bejelentkezés </button>
                  </p>
                )}

                {/* Review List */}
                {reviewError && !loadingReviews && <div className="alert alert-warning">{reviewError}</div>}
                <ReviewList
                  reviews={reviews}
                  loading={loadingReviews}
                  currentUserId={user?.id} // User ID needed for edit/delete buttons
                  onEdit={handleOpenEditModal} // Prop name changed to reflect modal opening
                  onDelete={handleDeleteReview}
                />
              </div> {/* End Reviews Section */}
            </div> {/* End Perfume Detail Content */}
          </>
        )} {/* End Conditional Rendering of Perfume Details */}

        {/* EDIT Review Modal */}
        {reviewToEditInModal && (
          <EditReviewModal
            key={reviewToEditInModal.id} // Ensure modal resets when different review selected
            reviewData={reviewToEditInModal}
            onUpdate={handleReviewUpdateFromModal}
            onClose={handleCloseEditModal}
          />
        )}

        {/* Message Prompt */}
        {message && (
          <div className="message-prompt">
            <p>{message}</p>
            <button onClick={() => setMessage("")}>Bezár</button>
          </div>
        )}

        {/* Login Prompt */}
        {showLoginPrompt && (
          <LoginPromptModal
            onClose={handleCloseLoginPrompt}
            onLoginRedirect={handleLoginRedirect}
          />
        )}

      </div> {/* End Container */}
    </div> // End Root Div
  );
};

export default PerfumeDetail;
// --- END OF FILE PerfumeDetail.js ---
