// Parfumvilag_2.0\frontend\src\pages\Home.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style.css';

const Home = () => {
  const [recommendedPerfumes, setRecommendedPerfumes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API hívás a parfümök lekérdezésére
  useEffect(() => {
    const fetchPerfumes = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/perfumes'); // Explicit URL
        console.log('API válasz státusz:', response.status, response.statusText); // Hibakereséshez
        const text = await response.text(); // Először szövegként olvassuk be
        console.log('API válasz szöveg:', text); // Hibakereséshez

        if (!response.ok) throw new Error(`Hálózati hiba: ${response.status} - ${response.statusText}`);

        const data = JSON.parse(text); // Próbáljuk JSON-ná alakítani

        // Véletlenszerű parfümök kiválasztása
        const shuffleArray = (array) => {
          const shuffled = [...array];
          for (let i = shuffled.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
          }
          return shuffled.slice(0, 3); // Az első 3 elemet vesszük
        };

        setRecommendedPerfumes(shuffleArray(data));
      } catch (err) {
        console.error('Hiba részletei:', err); // Részletes hiba a konzolban
        setError(`Hiba: ${err.message}. Ellenőrizd a konzol naplókat további részletekért.`);
      } finally {
        setLoading(false);
      }
    };

    fetchPerfumes();
  }, []);

  if (loading) return <div className="loading">Betöltés...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="home-page">
      <div className="container">
        <h1 className="home-title">Üdvözöljük a Parfümvilágban!</h1>
        <p className="home-subtitle">Fedezze fel a legjobb parfümöket!</p>

        {/* Ajánlott parfümök szekció */}
        <section className="recommended-section">
          <h2 className="section-title">Ajánlott Parfümök</h2>
          <div className="recommended-grid">
            {recommendedPerfumes.map((perfume) => (
              <div key={perfume.id} className="recommended-item">
                <img
                  src={`/images/${perfume.image}`} // Kép elérési útja (valódi útra cserélni)
                  alt={perfume.name}
                  className="recommended-image"
                />
                <h3 className="recommended-name">{perfume.name}</h3>
                <p className="recommended-brand">{perfume.brand}</p>
                <p className="recommended-price">{perfume.price} Ft</p>
                <Link to={`/parfume/${perfume.id}`} className="btn btn-peach">
                  Részletek
                </Link>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
