// --- START OF FILE PerfumeDetail.js ---

import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getPerfumeById } from "../services/perfumeService";
import {
  getMyFavoriteIds,
  addFavorite,
  removeFavorite,
} from "../services/savedPerfumeService";
// Import review delete/update services
import { deleteReview as deleteReviewService, updateReview as updateReviewService, getReviewsForPerfume } from "../services/reviewService";
import { AuthContext } from "../App";
import ReviewForm from "../components/ReviewForm";
import ReviewList from "../components/ReviewList";
import "../style.css";

// Login Prompt Modal (Can be moved to a separate file)
const LoginPromptModal = ({ onClose, onLoginRedirect }) => (
  // ... (modal code remains the same)
  <>
    <div className="login-prompt-overlay" onClick={onClose} />
    <div className="login-prompt">
      <button className="close-btn" onClick={onClose}>
        ×
      </button>
      <h3>Bejelentkezés szükséges</h3>
      <p>
        Be kell jelentkezni a kedvencek kezeléséhez vagy értékelés írásához.
      </p>
      <button className="login-btn" onClick={onLoginRedirect}>
        Bejelentkezés
      </button>
      <button className="cancel-btn" onClick={onClose}>
        Mégse
      </button>
    </div>
  </>
);


const PerfumeDetail = () => {
  const { id } = useParams();
  const { isLoggedIn, user, token } = useContext(AuthContext); // Use context
  const [perfume, setPerfume] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const [isFavorite, setIsFavorite] = useState(false);
  const [loadingFavorite, setLoadingFavorite] = useState(false);
  const [message, setMessage] = useState("");
  const [showLoginPrompt, setShowLoginPrompt] = useState(false);
  const [isImageZoomed, setIsImageZoomed] = useState(false);
  const [reviews, setReviews] = useState([]); // State to hold reviews for the list
  const [loadingReviews, setLoadingReviews] = useState(true); // Separate loading state for reviews
  const [editingReview, setEditingReview] = useState(null); // State to hold the review being edited

  const navigate = useNavigate();

  // --- Fetch Perfume Data ---
  useEffect(() => {
    const fetchPerfumeData = async () => {
      setLoading(true); // Overall page loading
      setError("");
      try {
        const perfumeData = await getPerfumeById(id);
        setPerfume(perfumeData);
      } catch (err) {
        console.error("Hiba a parfüm betöltésekor:", err);
        setError(err.message || "A parfüm részleteinek betöltése sikertelen.");
      } finally {
        setLoading(false);
      }
    };
    fetchPerfumeData();
  }, [id]);

  // --- Fetch Reviews ---
  const fetchReviews = async () => {
      if (!id) return;
      setLoadingReviews(true);
      setError(""); // Clear previous errors specific to reviews
      try {
          const data = await getReviewsForPerfume(id);
          setReviews(data || []);
      } catch (err) {
          console.error("Hiba az értékelések betöltésekor:", err);
          setError("Hiba az értékelések betöltésekor: " + err.message);
          setReviews([]);
      } finally {
          setLoadingReviews(false);
      }
  };

  useEffect(() => {
      fetchReviews();
  }, [id]); // Fetch reviews when id changes

  // --- Handle Favorite Status ---
  useEffect(() => {
    let isMounted = true;
    const checkIfFavorite = async () => {
      if (!isLoggedIn || !id || !perfume) return;
      setLoadingFavorite(true);
      try {
        const favoriteIds = await getMyFavoriteIds();
        if (isMounted) {
          setIsFavorite(favoriteIds.includes(parseInt(id, 10)));
        }
      } catch (error) {
        if (isMounted) {
          console.error("Hiba a kedvenc állapot ellenőrzésekor:", error);
        }
      } finally {
        if (isMounted) {
          setLoadingFavorite(false);
        }
      }
    };
    checkIfFavorite();
    return () => { isMounted = false; };
  }, [id, isLoggedIn, token, perfume]);

  // --- Favorite Toggle Handler ---
  const handleToggleFavorite = async () => {
    if (!isLoggedIn) {
      setShowLoginPrompt(true);
      return;
    }
    if (loadingFavorite || !id) return;
    setLoadingFavorite(true);
    setMessage("");
    try {
      const perfumeIdInt = parseInt(id, 10);
      if (isFavorite) {
        await removeFavorite(perfumeIdInt);
        setIsFavorite(false);
        setMessage("Parfüm eltávolítva a kedvencekből.");
      } else {
        await addFavorite(perfumeIdInt);
        setIsFavorite(true);
        setMessage("Parfüm hozzáadva a kedvencekhez.");
      }
      setTimeout(() => setMessage(""), 3000);
    } catch (error) {
      console.error("Hiba a kedvencek kezelésekor:", error);
      setMessage(`Hiba történt: ${error.message || "Nem sikerült módosítani a kedvencet."}`);
      setTimeout(() => setMessage(""), 5000);
    } finally {
      setLoadingFavorite(false);
    }
  };

  // --- Review Handlers ---
  const handleReviewSubmitted = (newReview) => {
    // Add the new review to the beginning of the list immediately
    setReviews((prevReviews) => [newReview, ...prevReviews]);
    setEditingReview(null); // Ensure editing mode is turned off if it was on
    setMessage("Értékelés sikeresen elküldve!");
    setTimeout(() => setMessage(""), 3000);
  };

  const handleEditReview = (reviewToEdit) => {
      // Set the review to be edited, which will trigger the form update
      setEditingReview(reviewToEdit);
      // Optionally scroll to the form
      const formElement = document.getElementById('review-form-section'); // Add an ID to your form container if needed
      formElement?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleCancelEdit = () => {
      setEditingReview(null); // Clear editing state
  };

   const handleUpdateReview = (updatedReviewData) => {
      // Update the review in the local state
      setReviews(prevReviews =>
          prevReviews.map(review =>
              review.id === updatedReviewData.id ? updatedReviewData : review
          )
      );
      setEditingReview(null); // Exit editing mode
      setMessage("Értékelés sikeresen frissítve!");
      setTimeout(() => setMessage(""), 3000);
  };


  const handleDeleteReview = async (reviewId) => {
    if (!window.confirm("Biztosan törölni szeretnéd ezt az értékelést?")) {
      return;
    }
    setMessage("");
    try {
      await deleteReviewService(reviewId);
      // Remove the review from the local state
      setReviews((prevReviews) => prevReviews.filter((r) => r.id !== reviewId));
      setMessage("Értékelés sikeresen törölve.");
      setTimeout(() => setMessage(""), 3000);
    } catch (error) {
      console.error("Hiba az értékelés törlésekor:", error);
      setMessage(
        `Hiba történt a törléskor: ${
          error.message || "Nem sikerült törölni."
        }`
      );
      setTimeout(() => setMessage(""), 5000);
    }
  };

  // --- Helper Functions ---
  const formattedPrice = () => {
    // ... (formattedPrice code remains the same)
    if (!perfume || perfume.stores?.length === 0) {
      return "Ár információ nem elérhető";
    }
    const prices = perfume.stores
      .map((store) => Number(store.price))
      .filter((price) => !isNaN(price));

    if (prices.length === 0) {
      return "Ár információ nem elérhető";
    }

    const minPrice = Math.min(...prices);
    return new Intl.NumberFormat("hu-HU").format(minPrice) + " Ft";
  };

  // --- Render Logic ---
  return (
    <div className="perfume-detail-container">
      <div className="container">
        <button className="back-btn" onClick={() => navigate(-1)}>
          ← Vissza
        </button>

        {loading && (
          <div className="text-center text-muted my-5">Adatok betöltése...</div>
        )}
        {error && !loading && <div className="alert alert-danger text-center">{error}</div>}

        {perfume && !loading && (
          <>
            {/* --- Perfume Header --- */}
            <div className="perfume-title-wrapper">
              <h1 className="perfume-title-top">{perfume.name}</h1>
              <button
                className={`favorite-btn ${isFavorite ? "active" : ""} ${
                  loadingFavorite ? "disabled" : ""
                }`}
                onClick={handleToggleFavorite}
                disabled={loadingFavorite}
                aria-label={
                  isFavorite
                    ? "Eltávolítás a kedvencekből"
                    : "Hozzáadás a kedvencekhez"
                }
              />
            </div>

            <div className="perfume-detail-content">
              {/* --- Image Section --- */}
              <div className="perfume-image-wrapper">
                {/* ... (image zoom logic remains the same) ... */}
                {isImageZoomed ? (
                    <div
                      className="zoomed-image-container"
                      onClick={() => setIsImageZoomed(false)}
                    >
                      <img
                        src={ perfume.image_url || "https://via.placeholder.com/600?text=Nincs+kép"}
                        alt={perfume.name}
                        className="zoomed-image"
                        onClick={(e) => e.stopPropagation()}
                      />
                      <button
                        className="close-zoom-btn"
                        onClick={() => setIsImageZoomed(false)}
                      >
                        ×
                      </button>
                    </div>
                  ) : (
                    <img
                      src={ perfume.image_url || "https://via.placeholder.com/500?text=Nincs+kép" }
                      alt={perfume.name}
                      className="perfume-image"
                      onClick={() => setIsImageZoomed(true)}
                      style={{ cursor: "zoom-in" }}
                    />
                  )}
              </div>

              {/* --- Info Section --- */}
              <div className="perfume-info">
                {/* ... (perfume details remain the same) ... */}
                 <p className="perfume-brand">
                    <strong>Márka:</strong> {perfume.brand_name || "Ismeretlen"}
                  </p>
                  <p className="perfume-gender">
                    <strong>Kategória:</strong>{" "}
                    {perfume.gender === "female"
                      ? "Női"
                      : perfume.gender === "male"
                      ? "Férfi"
                      : "Unisex"}
                  </p>
                  <p className="perfume-notes">
                    <strong>Illatjegyek:</strong>{" "}
                    {perfume.notes && perfume.notes.length > 0
                      ? perfume.notes.map((note, index) => (
                          <span key={index} className="scent-tag">
                            {note}
                          </span>
                        ))
                      : "Nincsenek megadva"}
                  </p>
                  <p
                    className={`perfume-price ${
                      !perfume.stores || perfume.stores.length === 0
                        ? "text-muted"
                        : ""
                    }`}
                  >
                    {formattedPrice()}
                  </p>
                  <p className="perfume-description">
                    <strong>Leírás:</strong>{" "}
                    {perfume.description || "Nincs leírás."}
                  </p>
              </div>

              {/* --- Stores Section --- */}
              <div className="stores-section">
                 {/* ... (stores section remains the same) ... */}
                  <h2 className="stores-title">Elérhető webáruházak</h2>
                  {perfume.stores && perfume.stores.length > 0 ? (
                    <div className="stores-list">
                      {perfume.stores.map((store, index) => (
                        <div key={index} className="store-item">
                          <span className="store-name">{store.store_name}</span>
                          <a
                            href={store.url}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="store-link"
                          >
                            Megnézem →
                          </a>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="no-stores">
                      Jelenleg nem érhető el webáruházban.
                    </p>
                  )}
              </div>

              {/* --- Reviews Section --- */}
              <div className="reviews-section" id="review-form-section"> {/* Added ID here */}
                <h2 className="reviews-title">Értékelések</h2>
                {isLoggedIn ? (
                  <ReviewForm
                    perfumeId={id}
                    key={editingReview ? `edit-${editingReview.id}` : 'new'} // Force re-render when switching between new/edit
                    initialData={editingReview} // Pass the review to edit
                    onReviewSubmitted={handleReviewSubmitted} // For new reviews
                    onReviewUpdated={handleUpdateReview} // For updated reviews
                    onCancelEdit={handleCancelEdit} // To cancel editing
                  />
                ) : (
                  <p className="login-required-message">
                    Bejelentkezés szükséges az értékelés írásához.
                    <button
                      className="btn btn-link"
                      onClick={() => navigate("/bejelentkezes")}
                    >
                      Bejelentkezés
                    </button>
                  </p>
                )}
                <ReviewList
                    perfumeId={id}
                    reviews={reviews} // Pass fetched reviews
                    loading={loadingReviews} // Pass loading state
                    currentUserId={user?.id} // Pass current user ID
                    onEdit={handleEditReview} // Pass edit handler
                    onDelete={handleDeleteReview} // Pass delete handler
                />
              </div>
            </div>
          </>
        )}

        {/* --- General Message Prompt --- */}
        {message && (
          <div className="message-prompt">
            <p>{message}</p>
            <button onClick={() => setMessage("")}>Bezár</button>
          </div>
        )}

        {/* --- Login Prompt Modal --- */}
        {showLoginPrompt && (
          <LoginPromptModal
            onClose={() => setShowLoginPrompt(false)}
            onLoginRedirect={() => navigate("/bejelentkezes")}
          />
        )}
      </div>
    </div>
  );
};

export default PerfumeDetail;
