// frontend/src/pages/PerfumeDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getPerfumeById } from '../services/perfumeService';
import '../style.css';

const PerfumeDetail = () => {
  const { id } = useParams();
  const [perfume, setPerfume] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const perfumeData = await getPerfumeById(id);
        setPerfume(perfumeData);
        setError('');
      } catch (err) {
        setError('Nem sikerült betölteni a parfüm részleteit!');
      } finally {
        setLoading(false);
      }
    };

    fetchPerfume();
  }, [id]);

  return (
    <div className="container mt-5">
      {loading && <div>Loading...</div>}
      {error && <div className="alert alert-danger">{error}</div>}
      {perfume && (
        <div className="row">
          <div className="col-md-6">
            <img 
              src={perfume.image_url || 'https://via.placeholder.com/400'} 
              alt={perfume.name} 
              className="img-fluid mb-3" 
            />
            <h2>{perfume.name}</h2>
            <p><strong>Márka:</strong> {perfume.brand}</p>
            <p><strong>Ár:</strong> {new Intl.NumberFormat('hu-HU').format(perfume.price)} Ft</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default PerfumeDetail;
