import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import PerfumeCard from '../components/PerfumeCard';
import { getAllPerfumes } from '../services/perfumeService';

const Search = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [perfumes, setPerfumes] = useState([]);
  const [error, setError] = useState('');
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);

  const searchTerm = searchParams.get('query') || '';
  const brandFilter = searchParams.get('brand') || '';
  const scentFilter = searchParams.get('scent') || '';
  const genderFilter = searchParams.get('gender') || '';
  const sortOption = searchParams.get('sort') || 'name-asc';

  useEffect(() => {
    const fetchPerfumes = async () => {
      try {
        const response = await getAllPerfumes({
          query: searchTerm,
          brand: brandFilter,
          scent: scentFilter,
          gender: genderFilter,
          sort: sortOption,
          page: currentPage,
          per_page: 24
        });

        setPerfumes(response.perfumes);
        setTotalPages(response.totalPages);
        setError('');
      } catch (err) {
        setError('Nem sikerült betölteni a parfümök listáját!');
        setPerfumes([]);
      }
    };

    fetchPerfumes();
  }, [searchTerm, brandFilter, scentFilter, genderFilter, sortOption, currentPage]);

  // Lapozás kezelése
  const handlePageChange = (page) => {
    setCurrentPage(page); // Frissíti az aktuális oldalt
    setSearchParams({ ...Object.fromEntries(searchParams), page });
  };

  // Oldalszámok generálása
  const getPageNumbers = () => {
    const pages = [];
    const maxPagesToShow = 5;
    let startPage = Math.max(1, currentPage - 2);
    let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

    if (endPage - startPage + 1 < maxPagesToShow) {
      startPage = Math.max(1, endPage - maxPagesToShow + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    return pages;
  };

  return (
    <div className="d-flex">
      {/* Sidebar */}
      <Sidebar />

      {/* Fő tartalom */}
      <div
        className="flex-grow-1"
        style={{ marginLeft: windowWidth > 991 ? '250px' : '0', width: '100%' }}
      >
        <div className="search-page container-fluid mt-4">
          <div className="row justify-content-center mb-4">
            <div className="col-md-6 position-relative">
              <div className="input-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Keresés név szerint..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
          </div>

          {/* Parfüm lista */}
          <div className="row" id="perfumeList">
            {Array.isArray(perfumes) && perfumes.length > 0 ? (
              perfumes.map((p) => (
                <div key={p.id} className="col-lg-4 col-md-6 col-12 mb-4">
                  <PerfumeCard perfume={p} />
                </div>
              ))
            ) : (
              <div className="col-12 text-center">
                <div id="noResults">
                  <i className="fas fa-search"></i>
                  <h4>Nincs találat</h4>
                  <p>Próbálj meg kevesebb vagy más szűrőfeltételt beállítani</p>
                </div>
              </div>
            )}
          </div>

          {/* Lapozó */}
          {totalPages > 1 && (
            <nav className="pagination mt-4">
              <ul className="pagination-list justify-content-center align-items-center">
                {/* Előző oldal */}
                <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                  <button
                    className="page-link prev-next"
                    onClick={() => handlePageChange(currentPage - 1)}
                  >
                    Előző
                  </button>
                </li>

                {/* Oldalszámok */}
                <div className="page-numbers">
                  {currentPage > 3 && (
                    <>
                      <li className="page-item">
                        <button
                          className="page-link"
                          onClick={() => handlePageChange(1)}
                        >
                          1
                        </button>
                      </li>
                      <li className="page-item disabled">
                        <span className="page-link">...</span>
                      </li>
                    </>
                  )}

                  {getPageNumbers().map((page) => (
                    <li
                      key={page}
                      className={`page-item ${currentPage === page ? 'active' : ''}`}
                    >
                      <button
                        className="page-link"
                        onClick={() => handlePageChange(page)}
                      >
                        {page}
                      </button>
                    </li>
                  ))}

                  {currentPage < totalPages - 2 && (
                    <>
                      <li className="page-item disabled">
                        <span className="page-link">...</span>
                      </li>
                      <li className="page-item">
                        <button
                          className="page-link"
                          onClick={() => handlePageChange(totalPages)}
                        >
                          {totalPages}
                        </button>
                      </li>
                    </>
                  )}
                </div>

                {/* Következő oldal */}
                <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
                  <button
                    className="page-link prev-next"
                    onClick={() => handlePageChange(currentPage + 1)}
                  >
                    Következő
                  </button>
                </li>
              </ul>
              <div className="pagination-info text-center mt-2">
                Összesen {totalPages} oldal
              </div>
            </nav>
          )}
        </div>
      </div>
    </div>
  );
};

export default Search;
