export const getAllPerfumes = async () => {
  try {
    const response = await fetch('http://api.parfumvilag.com/perfumes'); // Cseréld a valódi URL-re!
    if (!response.ok) throw new Error('Hiba a parfümök lekérdezésekor');
    const data = await response.json();
    console.log('Betöltött parfümök:', data); // Ellenőrzés
    return data;
  } catch (error) {
    console.error('Hiba az adatbetöltésnél:', error);
    // Tesztadatok, ha az API nem működik
    const testData = [
      { id: 1, name: "Creed Aventus", brand: "Creed", scentType: "Fás", gender: "férfi", price: 30000 },
      { id: 2, name: "Chanel No 5", brand: "Chanel", scentType: "Virágos", gender: "női", price: 25000 },
      { id: 3, name: "Tom Ford Oud Wood", brand: "Tom Ford", scentType: "Fás", gender: "unisex", price: 40000 },
    ];
    console.log('Tesztadatok használata:', testData);
    return testData;
  }
};
