// frontend/src/components/SearchBar.js
import React, { useState } from "react";
import axios from "axios";

const SearchBar = ({ onSearch, searchTerm, setSearchTerm, suggestions, handleSuggestionClick }) => {
  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.trim() === '') {
      return;
    }

    // Gyors javaslatok lekérése
    axios.get('/api/perfumes', {
      params: { search: value }
    })
    .then(response => {
      const filteredSuggestions = response.data
        .filter((perfume) =>
          perfume.name.toLowerCase().includes(value.toLowerCase()) ||
          perfume.brand.toLowerCase().includes(value.toLowerCase())
        )
        .slice(0, 5); // Legfeljebb 5 javaslat
      handleSuggestionClick(filteredSuggestions);
    })
    .catch(error => {
      console.error('Nem sikerült betölteni a javaslatokat:', error);
    });
  };

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <div className="search-container">
      <h2>Keresés</h2>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={searchTerm}
          onChange={handleInputChange}
          placeholder="Keresés..."
        />
        <button
          className="btn btn-primary"
          type="button"
          onClick={handleSearch}
        >
          Keresés
        </button>
      </div>
      {/* Gyors Javaslatok */}
      {suggestions.length > 0 && (
        <div className="suggestions-list">
          {suggestions.map((perfume) => (
            <div
              key={perfume.id}
              className="suggestion-item"
              onClick={() => handleSuggestionClick(perfume)}
            >
              {perfume.name} ({perfume.brand})
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
