// --- START OF FILE EditReviewModal.js ---
import React, { useState, useEffect } from 'react';
import { updateReview } from '../services/reviewService'; // Assuming updateReview service exists

// Reusable StarRatingInput (can be moved to its own file)
const StarRatingInput = ({ rating, onRatingChange, disabled }) => {
    const [hoverRating, setHoverRating] = useState(0);
    return (
      <div className="star-rating-input">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={`star-input ${hoverRating >= star ? 'hover' : ''} ${rating >= star ? 'filled' : ''}`}
            onMouseEnter={() => !disabled && setHoverRating(star)}
            onMouseLeave={() => !disabled && setHoverRating(0)}
            onClick={() => !disabled && onRatingChange(star)}
          >
            ★
          </span>
        ))}
      </div>
    );
  };


function EditReviewModal({ reviewData, onUpdate, onClose }) {
  // Internal state for the form within the modal
  const [ratings, setRatings] = useState({ sillage: 0, longevity: 0, value: 0, overall: 0 });
  const [comment, setComment] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Populate form when reviewData prop is available (modal opens)
  useEffect(() => {
    if (reviewData) {
      setRatings({
        sillage: reviewData.scent_trail_rating || 0,
        longevity: reviewData.longevity_rating || 0,
        value: reviewData.value_rating || 0,
        overall: reviewData.overall_impression || 0,
      });
      setComment(reviewData.review_text || '');
      setError(''); // Clear previous errors
    }
    // Reset when modal closes (reviewData becomes null) - handled by key prop in parent better
  }, [reviewData]);

  const handleRatingChange = (category, value) => {
    setRatings(prev => ({ ...prev, [category]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Basic validation
    if (Object.values(ratings).some(r => r === 0)) {
      setError("Kérlek, adj értékelést minden szempontra (minimum 1 csillag)!");
      return;
    }
    if (!comment.trim()) {
      setError("Kérlek, írj véleményt!");
      return;
    }

    setError('');
    setLoading(true);

    const payload = {
      scent_trail_rating: ratings.sillage,
      longevity_rating: ratings.longevity,
      value_rating: ratings.value,
      overall_impression: ratings.overall,
      review_text: comment,
    };

    try {
      // Call the update service function
      const result = await updateReview(reviewData.id, payload);
      onUpdate(result.review); // Pass the updated review object back to parent
      // onClose(); // Parent will close the modal by setting reviewData to null via onUpdate
    } catch (err) {
      console.error("Error updating review:", err);
      setError(err.message || "Hiba történt az értékelés frissítésekor.");
    } finally {
      setLoading(false);
    }
  };

  // If no reviewData, modal shouldn't be rendered by parent, but defensive return
  if (!reviewData) return null;

  return (
    <>
      {/* Modal Overlay */}
      <div className="modal-overlay" onClick={onClose}></div>

      {/* Modal Content */}
      <div className="modal-content-edit-review">
        <button className="modal-close-btn" onClick={onClose}>×</button>
        <h4 className="mb-3">Értékelés szerkesztése</h4>

        <form onSubmit={handleSubmit}>
          {error && <div className="alert alert-danger py-2 mb-3">{error}</div>}

          {/* Star Ratings using the component */}
          <div className="review-form-section mb-3">
            <label className="form-label d-block">Illatfelhő (Sillage):</label>
            <StarRatingInput
              rating={ratings.sillage}
              onRatingChange={(value) => handleRatingChange('sillage', value)}
              disabled={loading}
            />
          </div>
          <div className="review-form-section mb-3">
            <label className="form-label d-block">Tartósság (Longevity):</label>
            <StarRatingInput
              rating={ratings.longevity}
              onRatingChange={(value) => handleRatingChange('longevity', value)}
              disabled={loading}
            />
          </div>
           <div className="review-form-section mb-3">
            <label className="form-label d-block">Ár/Érték arány (Value):</label>
            <StarRatingInput
              rating={ratings.value}
              onRatingChange={(value) => handleRatingChange('value', value)}
              disabled={loading}
            />
          </div>
           <div className="review-form-section mb-3">
            <label className="form-label d-block">Összbenyomás (Overall):</label>
            <StarRatingInput
              rating={ratings.overall}
              onRatingChange={(value) => handleRatingChange('overall', value)}
              disabled={loading}
            />
          </div>

          {/* Comment Textarea */}
          <div className="review-form-section mb-3">
            <label htmlFor="editReviewComment" className="form-label">Vélemény:</label>
            <textarea
              id="editReviewComment"
              className="form-control"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              rows="4"
              required
              disabled={loading}
            />
          </div>

          {/* Action Buttons */}
          <div className="d-flex justify-content-end gap-2 mt-4">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={onClose}
              disabled={loading}
            >
              Mégse
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? "Mentés..." : "Módosítás mentése"}
            </button>
          </div>
        </form>
      </div>
    </>
  );
}

export default EditReviewModal;
// --- END OF FILE EditReviewModal.js ---
