import React, { useState, useEffect } from 'react';

function Sidebar({ onFilterChange }) {
  const [brands, setBrands] = useState([]);
  const [selectedBrands, setSelectedBrands] = useState([]);

  // Márkák betöltése az API-ból
  useEffect(() => {
    fetch('http://localhost:5000/api/brands')
      .then(res => res.json())
      .then(data => {
        if (data.message) {
          setBrands([]); // Ha nincs adat, üres lista
        } else {
          setBrands(data); // Márkák betöltése
        }
      })
      .catch(err => console.error('Hiba a márkák betöltésekor:', err));
  }, []);

  // Márka kiválasztása
  const handleBrandChange = (brandId) => {
    const updatedSelection = selectedBrands.includes(brandId)
      ? selectedBrands.filter(id => id !== brandId)
      : [...selectedBrands, brandId];
    setSelectedBrands(updatedSelection);
    onFilterChange({ brands: updatedSelection }); // Szűrő átadása a szülő komponensnek
  };

  return (
    <div className="sidebar">
      <h3>Márkák szerinti szűrés</h3>
      {brands.length > 0 ? (
        <ul>
          {brands.map(brand => (
            <li key={brand.id}>
              <label>
                <input
                  type="checkbox"
                  checked={selectedBrands.includes(brand.id)}
                  onChange={() => handleBrandChange(brand.id)}
                />
                {brand.name}
              </label>
            </li>
          ))}
        </ul>
      ) : (
        <p>Nincsenek elérhető márkák</p>
      )}
    </div>
  );
}

export default Sidebar;
