// frontend/src/pages/PerfumeDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getPerfumeById } from '../services/perfumeService';
import ReviewForm from '../components/ReviewForm';
import ReviewList from '../components/ReviewList';

const PerfumeDetail = () => {
  const { id } = useParams();
  const [perfume, setPerfume] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPerfume = async () => {
      try {
        const perfumeData = await getPerfumeById(id);
        setPerfume(perfumeData);
      } catch (error) {
        setError('Nem sikerült betölteni a parfüm részleteit!');
      }
    };
    fetchPerfume();
  }, [id]);

  if (!perfume && !error) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <h2>Parfüm Részletek</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      {perfume && (
        <div className="row">
          <div className="col-md-6">
            <img src={perfume.image_url} className="img-fluid mb-3" alt={perfume.name} />
            <h3>{perfume.name}</h3>
            <p><strong>Márka:</strong> {perfume.brand}</p>
            <p><strong>Kategória:</strong> {perfume.gender}</p>
            <p><strong>Illatok:</strong> {perfume.scents.join(', ')}</p>
            <p><strong>Leírás:</strong> {perfume.description}</p>
            <p><strong>Ár:</strong> {new Intl.NumberFormat('hu-HU').format(perfume.price)} Ft</p>
          </div>
          <div className="col-md-6">
            <h2>Értékelések</h2>
            <ReviewForm perfumeId={perfume.id} />
            <ReviewList perfumeId={perfume.id} />
          </div>
        </div>
      )}
      <div className="mt-3">
        <button className="btn btn-secondary" onClick={() => window.history.back()}>Vissza a kereséshez</button>
      </div>
      
    </div>
  );
};

export default PerfumeDetail;
