import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../style.css';

const PerfumeCard = ({ perfume }) => {
  const { id, name, brand, price, image_url } = perfume;
  const [isFavorite, setIsFavorite] = useState(false);
  const [showLoginPrompt, setShowLoginPrompt] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  // Kedvencek ellenőrzése az API-n keresztül
  useEffect(() => {
    const checkIfFavorite = async () => {
      if (!isLoggedIn) return;

      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setMessage('Hiányzó autentikációs token. Kérlek, jelentkezz be újra.');
          return;
        }

        const response = await fetch('/api/favorites', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error(`Hiba a kedvencek lekérdezésekor: ${response.status} - ${response.statusText}`);
        }

        const favorites = await response.json();
        setIsFavorite(favorites.some((fav) => fav.perfume_id === id));
      } catch (error) {
        console.error('Hiba a kedvencek ellenőrzésekor:', error);
        setMessage(`Nem sikerült ellenőrizni a kedvenceket: ${error.message}`);
      }
    };

    checkIfFavorite();
  }, [id, isLoggedIn]);

  // Kedvencekhez adás/törlés
  const toggleFavorite = async () => {
    if (!isLoggedIn) {
      setShowLoginPrompt(true);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('user_id');

      if (!token || !userId) {
        setMessage('Hiányzó autentikációs adatok. Kérlek, jelentkezz be újra.');
        return;
      }

      if (isFavorite) {
        // Törlés a kedvencekből
        const response = await fetch(`/api/favorites/${id}`, {
          method: 'DELETE',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Hiba a kedvenc törlésekor: ${response.status} - ${errorText}`);
        }

        setMessage('Parfüm eltávolítva a kedvencekből.');
      } else {
        // Hozzáadás a kedvencekhez
        const response = await fetch('/api/favorites', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            user_id: userId,
            perfume_id: id,
          }),
        });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Hiba a kedvenc hozzáadásakor: ${response.status} - ${errorText}`);
        }

        setMessage('Parfüm hozzáadva a kedvencekhez.');
      }

      setIsFavorite(!isFavorite);
    } catch (error) {
      console.error('Hiba a kedvencek kezelésekor:', error);
      setMessage(`Hiba történt: ${error.message}`);
    }
  };

  return (
    <>
      <div className="perfume-card">
        <Link
          to={`/parfume/${id}`}
          className="perfume-card-link"
          style={{ textDecoration: 'none', color: 'inherit' }}
        >
          <img
            src={image_url || 'https://via.placeholder.com/220'}
            alt={name}
            className="perfume-card-img"
          />
          <div className="perfume-card-body">
            <h5 className="perfume-card-title">{name}</h5>
            <p className="perfume-card-subtitle">{brand}</p>
            <p className="perfume-card-text">{price ? `${price} Ft` : 'Ár nem elérhető'}</p>
          </div>
        </Link>
        <button
          className={`favorite-btn ${isFavorite ? 'active' : ''}`}
          onClick={(e) => {
            e.preventDefault();
            toggleFavorite();
          }}
        />
      </div>

      {/* Visszajelzés üzenet */}
      {message && (
        <div className="message-prompt">
          <p>{message}</p>
          <button onClick={() => setMessage('')}>Bezár</button>
        </div>
      )}

      {/* Bejelentkezés üzenet modális */}
      {showLoginPrompt && (
        <>
          <div className="login-prompt-overlay" onClick={() => setShowLoginPrompt(false)} />
          <div className="login-prompt">
            <button
              className="close-btn"
              onClick={() => setShowLoginPrompt(false)}
            >
              ×
            </button>
            <h3>Bejelentkezés szükséges</h3>
            <p>Be kell jelentkezni, hogy menteni tudjunk a kedvencek közé.</p>
            <button
              className="login-btn"
              onClick={() => navigate('/bejelentkezes')}
            >
              Bejelentkezés
            </button>
          </div>
        </>
      )}
    </>
  );
};

export default PerfumeCard;
