import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import { getAllPerfumes } from "../services/perfumeService";
import Sidebar from "../components/Sidebar";
import PerfumeCard from "../components/PerfumeCard";
import "../style.css"; // Ensure styles are imported

// Helper for Pagination Buttons
const PageButton = ({
  page,
  currentPage,
  onClick,
  isDisabled = false,
  children,
}) => (
  <li
    className={`page-item ${currentPage === page ? "active" : ""} ${
      isDisabled ? "disabled" : ""
    }`}
  >
    <button
      className="page-link"
      onClick={() => !isDisabled && onClick(page)}
      disabled={isDisabled}
    >
      {children || page}
    </button>
  </li>
);

const Search = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [perfumes, setPerfumes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [totalPages, setTotalPages] = useState(1);
  // Current page derived from URL search params
  const currentPage = parseInt(searchParams.get("page") || "1", 10);
  // Search term state for the input field
  const [searchTerm, setSearchTerm] = useState(searchParams.get("query") || "");

  // State to track window width for responsive layout adjustments
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  // Effect to update window width state on resize
  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    // Cleanup listener on component unmount
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Effect to fetch perfumes whenever searchParams change
  useEffect(() => {
    const fetchPerfumes = async () => {
      setLoading(true);
      setError("");
      try {
        // Extract parameters from the current URL search params
        const params = {
          query: searchParams.get("query") || "",
          brand: searchParams.get("brand") || "",
          note: searchParams.get("note") || "",
          gender: searchParams.get("gender") || "",
          sort: searchParams.get("sort") || "name-asc",
          min_price: searchParams.get("min_price"),
          max_price: searchParams.get("max_price"),
          page: searchParams.get("page") || "1",
          per_page: 24, // Keep consistent or make dynamic
        };

        console.log("Search.js: Fetching perfumes with params:", params);
        const response = await getAllPerfumes(params); // Call the API service

        // Update state with the response
        setPerfumes(response.perfumes || []);
        setTotalPages(response.totalPages || 1);
      } catch (err) {
        console.error("Search.js: Error fetching perfumes:", err);
        setError(err.message || "Hiba a parfümök betöltésekor.");
        setPerfumes([]);
        setTotalPages(1);
      } finally {
        setLoading(false);
      }
    };

    fetchPerfumes();
    // Dependency: re-run fetch whenever searchParams object changes
  }, [searchParams]);

  // Handler for the search input field change
  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handler for submitting the search term (via button or Enter)
  const handleSearchSubmit = () => {
    const newParams = new URLSearchParams(searchParams);
    if (searchTerm.trim()) {
      newParams.set("query", searchTerm.trim());
    } else {
      newParams.delete("query");
    }
    newParams.set("page", "1"); // Reset to page 1 for new search query
    setSearchParams(newParams); // Update URL -> triggers useEffect -> fetches data
  };

  // Handler for changing the page via pagination
  const handlePageChange = (page) => {
    const newPage = Math.max(1, Math.min(page, totalPages)); // Ensure page is valid
    const newParams = new URLSearchParams(searchParams);
    newParams.set("page", String(newPage));
    setSearchParams(newParams); // Update URL -> triggers useEffect
    window.scrollTo({ top: 0, behavior: "smooth" }); // Scroll to top for new page
  };

  // --- Pagination Logic ---
  const getPageNumbers = () => {
    const pages = [];
    const maxPagesToShow = windowWidth < 576 ? 3 : 5; // Show fewer buttons on extra small screens
    const halfPages = Math.floor(maxPagesToShow / 2);
    let startPage, endPage;

    if (totalPages <= maxPagesToShow) {
      startPage = 1;
      endPage = totalPages;
    } else if (currentPage <= halfPages + 1) {
      startPage = 1;
      endPage = maxPagesToShow;
    } else if (currentPage + halfPages >= totalPages) {
      startPage = totalPages - maxPagesToShow + 1;
      endPage = totalPages;
    } else {
      startPage = currentPage - halfPages;
      endPage = currentPage + halfPages;
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }
    return {
      pages,
      showStartEllipsis: startPage > 1,
      showEndEllipsis: endPage < totalPages,
    };
  };

  const {
    pages: pageNumbers,
    showStartEllipsis,
    showEndEllipsis,
  } = getPageNumbers();
  // --- End Pagination Logic ---

  // Dynamically calculate padding for main content based on sidebar visibility
  // Sidebar is assumed to be 250px wide and visible on screens > 991px
  const mainContentPaddingLeft = windowWidth > 991 ? "250px" : "0";

  return (
    // Use d-lg-flex: Flexbox layout only on large screens and up
    // On smaller screens, elements will stack (Sidebar might be hidden/overlay)
    <div className="d-lg-flex">
      {/* Sidebar Component */}
      <Sidebar />
      {/* Main Content Area */}
      <div
        className="flex-grow-1 main-content-area" // Use a specific class
        // Apply padding dynamically only on large screens
        // Use padding instead of margin to avoid collapsing issues
        style={{
          paddingLeft: mainContentPaddingLeft,
          transition: "padding-left 0.3s ease-in-out", // Smooth transition
          minHeight: "calc(100vh - 70px)", // Ensure it pushes footer down
        }}
      >
        {/* Use container-fluid for full width padding control */}
        <div className="container-fluid py-4 px-lg-4 px-md-3 px-sm-2 px-1">
          {/* Search Input Row */}
          <div className="row justify-content-center mb-4 mb-lg-5">
            <div className="col-md-10 col-lg-8 col-xl-6">
              <div className="input-group shadow-sm">
                <input
                  type="text"
                  className="form-control form-control-lg" // Larger input
                  placeholder="Keresés parfümre..."
                  value={searchTerm}
                  onChange={handleSearchTermChange}
                  onKeyPress={(e) => e.key === "Enter" && handleSearchSubmit()}
                  aria-label="Parfüm keresése"
                />
                <button
                  className="btn btn-primary px-3"
                  onClick={handleSearchSubmit}
                  aria-label="Keresés"
                >
                  <i className="fas fa-search"></i>
                </button>
              </div>
            </div>
          </div>

          {/* Loading Indicator */}
          {loading && (
            <div className="text-center py-5 my-5">
              <div
                className="spinner-border text-primary"
                style={{ width: "3rem", height: "3rem" }}
                role="status"
              >
                <span className="visually-hidden">Parfümök betöltése...</span>
              </div>
            </div>
          )}

          {/* Error Message */}
          {error && !loading && (
            <div className="alert alert-danger text-center col-md-8 mx-auto">
              {error}
            </div>
          )}

          {/* Perfume List Grid - Only render if not loading and no error */}
          {!loading && !error && (
            <div className="row g-3" id="perfumeList">
              {perfumes.length === 0 ? (
                // No Results Message
                <div className="col-12">
                  <div className="text-center card border-0 shadow-sm p-4 p-md-5 my-4">
                    <i className="fas fa-box-open fa-3x mb-3 text-secondary opacity-50"></i>
                    <h4 className="text-muted">Nincs Találat</h4>
                    <p className="mb-0 text-muted">
                      A megadott feltételekkel nem található parfüm. Próbálj más
                      szűrőket!
                    </p>
                  </div>
                </div>
              ) : (
                // Display Perfume Cards
                perfumes.map((p) => (
                  <div
                    key={p.id}
                    className="col-xl-3 col-lg-4 col-md-6 col-sm-6 col-12 d-flex align-items-stretch"
                  >
                    <PerfumeCard perfume={p} />
                  </div>
                ))
              )}
            </div>
          )}

          {/* Pagination - Only render if not loading, no error, and more than one page */}
          {!loading && !error && totalPages > 1 && (
            <nav
              className="mt-5 d-flex justify-content-center"
              aria-label="Perfume pagination"
            >
              <ul className="pagination pagination-sm shadow-sm">
                {" "}
                {/* Smaller pagination */}
                {/* Previous Button */}
                <PageButton
                  page={currentPage - 1}
                  currentPage={currentPage}
                  onClick={handlePageChange}
                  isDisabled={currentPage === 1}
                >
                  « {/* « */}
                </PageButton>
                {/* First Page & Ellipsis */}
                {showStartEllipsis && (
                  <>
                    <PageButton
                      page={1}
                      currentPage={currentPage}
                      onClick={handlePageChange}
                    />
                    <li className="page-item disabled">
                      <span className="page-link px-2">...</span>
                    </li>{" "}
                    {/* Smaller ellipsis */}
                  </>
                )}
                {/* Page Numbers */}
                {pageNumbers.map((page) => (
                  <PageButton
                    key={page}
                    page={page}
                    currentPage={currentPage}
                    onClick={handlePageChange}
                  />
                ))}
                {/* End Ellipsis & Last Page */}
                {showEndEllipsis && (
                  <>
                    <li className="page-item disabled">
                      <span className="page-link px-2">...</span>
                    </li>
                    <PageButton
                      page={totalPages}
                      currentPage={currentPage}
                      onClick={handlePageChange}
                    />
                  </>
                )}
                {/* Next Button */}
                <PageButton
                  page={currentPage + 1}
                  currentPage={currentPage}
                  onClick={handlePageChange}
                  isDisabled={currentPage === totalPages}
                >
                  » {/* » */}
                </PageButton>
              </ul>
            </nav>
          )}

          {/* Optional: Pagination Info Text */}
          {!loading && !error && totalPages > 0 && perfumes.length > 0 && (
            <div className="text-center text-muted small mt-2 mb-4">
              {currentPage}. oldal / {totalPages}
            </div>
          )}
        </div>{" "}
        {/* End Container-fluid */}
      </div>{" "}
      {/* End Main Content Area */}
    </div> // End d-lg-flex
  );
};

export default Search;
