// Parfumvilag_2.0\frontend\src\pages\Home.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import PerfumeCard from '../components/PerfumeCard';
import axios from 'axios';
import '../style.css';

const Home = () => {
  const [randomPerfumes, setRandomPerfumes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRandomPerfumes = async () => {
      try {
        // Lekérdezzük az összes parfümöt az API-ból
        const response = await axios.get('/api/perfumes');
        const allPerfumes = response.data;

        // Ha nincs adat, üres tömböt állítunk be
        if (!allPerfumes || allPerfumes.length === 0) {
          setRandomPerfumes([]);
          return;
        }

        // Véletlenszerűen kiválasztunk 3 parfümöt
        const shuffleArray = (array) => {
          const shuffled = [...array];
          for (let i = shuffled.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
          }
          return shuffled.slice(0, 3); // Mindig 3 elemet veszünk
        };

        const selectedPerfumes = shuffleArray(allPerfumes);
        setRandomPerfumes(selectedPerfumes);
      } catch (err) {
        console.error('Error fetching perfumes:', err);
        // Hiba esetén üres kártyák jelennek meg
        setRandomPerfumes([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRandomPerfumes();
  }, []);

  // Mindig 3 kártyát jelenítünk meg, akár üresen is
  const displayPerfumes = Array(3).fill(null).map((_, index) => {
    const perfume = randomPerfumes[index] || {};
    return {
      id: perfume.id || `placeholder-${index}`,
      name: perfume.name || 'Parfüm neve',
      brandId: perfume.brand_id || 0,
      gender: perfume.gender || 'unisex',
      type: perfume.type || 'Eau de Parfum',
      description: perfume.description || 'Nincs leírás',
      imageUrl: perfume.image_url || 'https://via.placeholder.com/200', // Alapértelmezett kép
    };
  });

  return (
    <div className="home-wrapper">
      <section className="hero-section">
        <div className="hero-content">
          <h1>Üdvözöljük a Parfümvilágban</h1>
          <p>
            Találd meg álmaid parfümjét ajánlásainkkal és árösszehasonlításainkkal – egyszerűen, gyorsan, stílusosan!
          </p>
          <div className="hero-actions">
            <Link to="/kereses" className="btn btn-peach hero-btn">Keresés indítása</Link>
            <Link to="/katalogus" className="btn btn-outline-peach ms-3 hero-btn">Hírek felfedezése</Link>
          </div>
        </div>
      </section>

      <section className="featured-section container my-5">
        <h2 className="section-title text-center mb-4">Ajánlott Parfümjeink</h2>
        {loading ? (
          <div className="text-center">Betöltés...</div>
        ) : (
          <div className="row g-3" id="perfumeList">
            {displayPerfumes.map((perfume) => (
              <div key={perfume.id} className="col-lg-4 col-md-4 col-12">
                <PerfumeCard perfume={perfume} />
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Home;
